INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('343d1d52-3002-4868-a9f6-c2adfb3b949e', '2019-06-14 00:00:00.000000', 854, 5, 0, '8k08DwAAQBAJ', 'Все точки над Ё', 'http://books.google.com/books/content?id=8k08DwAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'У буквы «ё» – непростая и очень интересная судьба. У неё имеются влиятельные и авторитетные друзья, но есть и немало противников. Вокруг неё всегда возникали и до сих пор кипят горячие споры. История буквы «ё», самой новой из всех букв современного русского алфавита, ярко, с юмором рассказана в этой книге.', 93, 84);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('9561f621-2fd8-46b5-ba5d-7822d9d8bdbc', '2021-06-03 00:00:00.000000', 815, 23, 1, '63MxEAAAQBAJ', 'Чиновник категории «Ё». Рассказы', 'http://books.google.com/books/content?id=63MxEAAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Рассказы. Обычное описание того, что увидено, а часто домыслено или придумано. Не стоит искать конкретных фактов или совпадений.Как сказал Алексей Максимович Горкий (сосед по парте):– Люблю я прозу Алексея Витальевича...', 200, 180);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('83d8983a-0037-4490-8fb2-a804bf29255a', '2018-02-23 00:00:00.000000', 358, 26, 0, 'nO5NDwAAQBAJ', 'Такая нескучная жизнь', 'http://books.google.com/books/content?id=nO5NDwAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'В этот сборник вошли рассказы из трёх книг, которые терпеливо ждут своего дописания: «Эдем по имени КРАУСС», «Внуково – центр вселенной» и «Записки охотника за головами». В них отсутствует основная сюжетная линия, поэтому книгу «Такая нескучная жизнь» можно читать как вам заблагорассудится, хоть сзаду наперёд.', 75, 67);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('12270bad-4ce9-4e35-9fb4-a10a1ee293df', '2018-02-23 00:00:00.000000', 333, 26, 0, '8u5NDwAAQBAJ', 'Прощай, бизнес-ланч. Часть I. Погружение в новую реальность', 'http://books.google.com/books/content?id=8u5NDwAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Весёлая книга о перипетиях жизни кулинара-ботаника Ё-Маззая. Главный герой – сам автор, в прошлом генеральный директор одного из столичных кадровых агентств. В преддверии кризиса 2008 года он начал готовиться к обещанному экономистами голоду и другим катаклизмам. Неожиданно для себя Маззай страстно увлёкся кулинарией и оставил бизнес. В новую жизнь он вошёл с долгами и полным комплектом вредных привычек. Но это не мешает ему радоваться жизни и спасать человечество от энзимной недостаточности.', 125, 112);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('ec4e3982-f732-4f52-ae91-19593ae45f0a', '2018-01-31 00:00:00.000000', 446, 1, 0, 'tpYxDwAAQBAJ', 'Сорняки – это еда!', 'http://books.google.com/books/content?id=tpYxDwAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Эта книга – не справочник по лекарственным травам. Она – стимул и руководство к действию. В ней изложены принципы определения и запоминания дикорастущих растений, правила употребления сорняков, самые распространённые съедобные травы, очень интересные и полезные рецепты.', 312, 281);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('23f40c00-ba06-4080-acd0-a8da6fe2084b', '2021-05-12 00:00:00.000000', 195, 9, 0, 'PgkuEAAAQBAJ', 'Корейский без ошибок', 'http://books.google.com/books/content?id=PgkuEAAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', '«Корейский без ошибок» – это первое издание мирового бестселлера «Correct your Korean» на русском языке.В пособии представлен подробный разбор самых частых ошибок, которые допускают изучающие корейский язык. Яркие примеры и многочисленные таблицы помогут быстро понять самые сложные нюансы корейской грамматики, а тесты по типу международного экзамена ТОРIK после каждой темы позволят быстро закрепить пройденное.Книга предназначена всех, кто изучает корейский язык самостоятельно или на специализированных курсах, а также для преподавателей.', 811, 730);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('0902ba1f-1130-4d37-ae22-d55a993668da', '2018-01-31 00:00:00.000000', 176, 1, 0, 'hYcwDwAAQBAJ', 'Иван-чай. По китайским технологиям и не только...', 'http://books.google.com/books/content?id=hYcwDwAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Эта книга вобрала в себя тысячелетний опыт китайских чайных мастеров, многовековой опыт российских мастеров-киприотов и результаты собственной многотысячедневной Ё-практики. Она содержит базовые знания по приготовлению иван-чая, конкретные рецепты и инструменты для собственного творчества.', 312, 281);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('f6de1647-9643-4a30-8b45-351a44cd056d', '2020-01-19 00:00:00.000000', 63, 11, 0, '8B_LDwAAQBAJ', 'Ё-моё', 'http://books.google.com/books/content?id=8B_LDwAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Живые песенки моив основу сборника легли —они правдивы и легки...Мне чем смогли, тем помогли,в час испытаний по Судьбе,осознавая Бытиё,по Правде жить, не по злобе́,и с вдохновеньем – Ё-МОЁ...Второе издание – переработанное...', 125, 112);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('08306b48-d8f0-4b91-a739-abf568248bf0', null, 154, 4, 1, 'sAwKAwAAQBAJ', 'An Egyptian Hieroglyphic Dictionary', 'http://books.google.com/books/content?id=sAwKAwAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'With an index of english words, king list and geographical list with indexes, list of hieroglyphic characters, coptic and semitic alphabets, etc. (In two volumes) VOL. I.', 468, 468);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('515e0412-a0a3-4ec8-808a-e7746571a75b', null, 485, 4, 1, 'whb_AgAAQBAJ', 'Верховные боги индоевропейцев', 'http://books.google.com/books/content?id=whb_AgAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', null, 456, 456);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('d0e34ffc-932a-41d5-9915-ec264993daf7', '2019-02-22 00:00:00.000000', 281, 51, 1, '8mKJDwAAQBAJ', 'Мобильный тренинг: от А до Ё', 'http://books.google.com/books/content?id=8mKJDwAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Данная книга уникальна тем, что авторы подошли к мобильному обучению очень осознано: успешно опробовали и доработали свои подходы на практике. Им удалось найти свой формат – Мобильный тренинг, который отлично сочетает в себе преимущества очного тренинга и максимальный комфорт прохождения обучения. Ведь сегодня кратчайший и удобнейший способ завоевать внимание, сердце и ум обучающегося – доставить знания через его мобильный телефон! И это не может не работать!', 500, 450);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('c9de04dc-957c-45df-beee-e35c1f8579ce', '2018-01-31 00:00:00.000000', 410, 23, 1, 'GvBADwAAQBAJ', 'Прощай, бизнес-ланч. Часть III. Снова в раю', 'http://books.google.com/books/content?id=GvBADwAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Для бывшего офисного планктона провести всё лето на природе, в палатке, рядом с русской печкой, передвигаться автостопом... Это настоящее погружение в иную реальность, кульминация дауншифтинга и неописуемый кайф от ощущения свободы. Присоединяйтесь!', 125, 112);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('44e7e752-b56c-43b8-a965-983e4a2d8655', '2019-08-24 00:00:00.000000', 485, 46, 0, 'bxKrDwAAQBAJ', 'Ё-ёжики. Рассказки-подсказки для малышей', 'http://books.google.com/books/content?id=bxKrDwAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Когда девочка Варя угощала в своем саду разными вкусняшками лесных гостей-зверушек, два ёжика рассказали ей удивительную историю о том, как и почему у всех ёжиков появились иголки.', 124, 112);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('efb3a58d-f763-4d16-9922-9d7503a724a4', '2009-10-26 00:00:00.000000', 468, 23, 0, '0Y6lAAAAQBAJ', 'Точки над «Ё»', 'http://books.google.com/books/content?id=0Y6lAAAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', '«Ася Топоркова, девица двадцати пяти лет, окончившая институтский курс, имела монументальную фигуру и сентиментальную душу. Попытка выйти замуж за ушлого пройдоху была во время пресечена мудрыми родителями, но надолго травмировала её сердце. Необходимость зарабатывать на жизнь счастливым образом была реализована, благодаря другу», – так начал бы свой рассказ Чехов, если бы он знал Асю.Но Ася живёт в ХХI веке. Земляки её узнают... нет, не в лицо, но по голосу! Асин уникальный голос – фирменный знак областной радиостанции, а историями о русском языке заслушивается и стар и млад. У неё прекрасная работа, необычная семья и друг, который с каждым днём становится всё ближе и дороже. Правда, он упорно считает Асю тургеневской девушкой и обращается с ней как с кисейной барышней. Обрадует ли его столкновение с реальностью?', 186, 167);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('1295eeb7-3442-4d36-b47b-93a86df2a057', '2013-01-10 00:00:00.000000', 43, 19, 1, '5FxOvfH8fSQC', 'Творим свое прекрасное тело', 'http://books.google.com/books/content?id=5FxOvfH8fSQC&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Перед вами краткое руководство по чудесному преображению своего тела, достижению стройности, красоты и здоровья, основанное на материалах книги: «Худеем вместе с феей. Мое прекрасное тело». Вы узнаете о том, как настроить свое сознание на молодость, красоту и здоровье. Как нормализовать свой вес с помощью медитаций, укрепить внутреннюю силу, выполняя дыхательные упражнения, научиться получать энергию из природных и духовных источников. Автор расскажет вам, как можно стать жизнерадостнее и энергичнее при любом весе и телосложении. Эта книга поддержит, вдохновит вас в работе над собой и поможет сознательно творить свое тело, свою жизнь, руководствуясь высшими этическими и духовными принципами!', 63, 57);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('a476aaa8-60ff-4b23-9f01-6808c8e867ca', '2003-07-31 00:00:00.000000', 375, 25, 0, 'E9dtCQAAQBAJ', 'Graph Drawing', 'http://books.google.com/books/content?id=E9dtCQAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'This year’s meeting marked the Eighth International Symposium on Graph D- wing. The organizing and program committees worked hard to make this year’s symposium possible, and we were delighted that so many people came to - lonial Williamsburg, Virginia, for three days of the latest results in the eld of graph drawing. As in previous years, the review process was quite competitive. We accepted 30 out of 53 regular-length submissions, and 5 out of 15 short submissions, for a total acceptance ratio of 35 out of 68, or 51%. This year’s program featured several new developments in the eld. Four di erent approaches for handling very large graphs were presented in a session on force-directed layout. Two sessions were devoted to the latest advances in orthogonal graph drawing. And alongside the usual mix of theory and practice papers we had several contributions based on empirical studies of users and of systems. Our invited talks were given by two speakers who were new to most members of the GD community, but who work in areas that are closely related to graph drawing. Professor Colin Ware of the University of New Hampshire told us how knowledge of human visual perception is useful for the design of e ective data visualizations. And Professor David Jensen of the University of Massachusetts at Amherst talked about the process of knowledge discovery from graphs, a process that involves more than just graph drawing and visualization.', 5771, 5771);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('ab6a7274-adf5-4619-b6b7-7617ed470c4e', null, 576, 44, 0, 'drb7AgAAQBAJ', 'Самоучитель пчеловодства', 'http://books.google.com/books/content?id=drb7AgAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Общедоступное руководство для пчеловодов-практиков.', 510, 510);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('82a7191c-909b-4109-98f6-50f1b308576c', '2012-11-18 00:00:00.000000', 43, 19, 0, 'Xkr13k47bbQC', 'Фэншуй загородного дома и сада', 'http://books.google.com/books/content?id=Xkr13k47bbQC&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Перед вами краткое руководство по фэншуй загородного дома и земельного участка, основанное на материалах книг «Создайте хороший фэншуй», «Фэншуй для квартиры и дачи», дополненное новыми рекомендациями автора.. Вы узнаете, как можно изменить всю свою жизнь, обустроив в соответствии с практическими правилами фэншуй собственный дом. Наталия дает подробные рекомендации о том, как выбрать приусадебный участок и обустроить его. Цель правильного фэншуй – сделать так, чтобы в доме циркулировала благотворная энергия Ци, которая способна принести удачу, благополучие и счастье обитателям. Советы Наталии Правдиной помогут читателям создать в своем доме хороший фэншуй, что непременно изменит жизнь в лучшую сторону. Применяйте фэншуй, он поможет вам повысить качество вашей жизни!', 63, 57);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('4d3337a0-2f25-4948-8b88-707275537111', null, 873, 30, 0, 'Rd39AgAAQBAJ', 'Термодинамика необратимых процессов', 'http://books.google.com/books/content?id=Rd39AgAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'This book, "Termodinamika neobratimyh protsessov," by S.R de Groot, is a replication of a book originally published before 1956. It has been restored by human beings, page by page, so that you may enjoy it in a form as close to the original as possible.', 387, 387);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('6c90c68a-e7d8-4a67-976d-b49b292d7f55', null, 115, 30, 0, 'JPf9AgAAQBAJ', 'Теоретическая электротехника', 'http://books.google.com/books/content?id=JPf9AgAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'В книге рассматриваются и вопросы теории поля и вопросы теории цепей, как линейных, так и нелинейных, причем широко привлекаются современные методы их анализа (матрицы и тензоры, понятие магнитного тока, обобщенная теория мультиполей, плоскость ван-дер-Поля и т. п. ). Все основные положения подвергаются углубленному анализу при постановке задачи и при ее исходной формулировке. Во многих разделах книги, как и в заключительной ее части, автор стремится обрисовать дальнейшие перспективы развития теории и границы применимости излагаемых методов. В частности, очень интересна попытка автора рассмотреть границы между классической и квантовой электродинамикой с точки зрения современного инженера-электрика.', 456, 456);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('680856c6-d4e5-41d8-ae71-1d374229a92f', '2018-10-01 00:00:00.000000', 29, 23, 0, '77xwDwAAQBAJ', '낙화유수. 2', 'http://books.google.com/books/content?id=77xwDwAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', '하루하루 한의사로서 치열한 나날을 보내던 김소진. 그러나 예상치 못한 화재사고로 목숨을 잃게 된 그녀는 다시 눈을 떴을 때 자신이 고구려 시대, 절노부 연씨 가문의 ‘우희’가 되었다는 것을 알게 된다. 귀한 가문의 딸로 태어나 이 생에는 편할 줄 알았는데 하필이면 그녀가 떨어진 고구려는 전쟁이 판치는 시대! 게다가 이제 갓 열두 살이 된 그녀에게 정략혼이라는 예기치 못한 시련이 찾아오고- “많이 놀란 모양이구나. 담덕이 네게 아무 말도 하지 않았던 것이냐?” “담덕이라면.......” 하필이면, 결혼 상대자로 지목된 사람의 이름이 광개토 대왕의 이름이다. 역사를 잘 공부하지 않은 그녀도 잘 알고 있는 유명한 이름! 아니, 그것보다 혼인이라니! 열두 살의 나이에 혼인이라니! “전...... 전 태자님이랑 혼인 안 할 겁니다!” 역사상 가장 위대했던 태왕, 그리고 ‘미래’를 기억하는 여자, 우희가 꽃 피우는 연애담. 김다함 작가의 장편 시대물 로맨스 낙화유수 [*본 작품은 역사적 사실을 바탕으로 한 픽션으로 실제 역사와는 차이가 있을 수 있습니다. 또한 작품 내 등장하는 한의한 지식은 소설에 맞추어 재구성되었습니다.]', 201, 181);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('662db1df-f68f-48a2-bb8c-6bbae1152857', '2018-09-22 00:00:00.000000', 167, 37, 0, 'RbFvDwAAQBAJ', 'Valmiki Ramayana - Yuddha Kanda', 'http://books.google.com/books/content?id=RbFvDwAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'యుద్ధ కాండ యందు సేతు నిర్మాణము, శాంతి కొరకు రాముని సంధి ప్రయత్నమూ, విభీషణ శరణాగతి, విభీషణ పట్టాభిషేకం, లక్ష్మణ పరాక్రమము, లక్ష్మణ మూర్చ, హనుమంతుడు సంజీవిని పర్వతమును తెచ్చి లక్ష్మణుని బ్రతికించుట, ఇంద్రజిత్తు మొదలగు వారి సంహారం, శ్రీ రాముని యుద్ధ కుశలత, కుంబకర్ణ, రావణ సంహారం మరియు మండోదరి విలాపము తో పాటు, సీత అగ్ని పునీత అగుట, సీతా రాములు వానరుల తో కూడి పుష్పక విమానమున అయోధ్యా పయనమగుట, సీతా రామ పట్టాభిషేకము వివరింపబడ్డాయి', 0, 0);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('1d29902a-bacf-4bb4-b2a5-2a97509cf4de', null, 862, 31, 0, 'CkX6AgAAQBAJ', 'Высшая математика для начинающих физиков и техников', 'http://books.google.com/books/content?id=CkX6AgAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Введение в матемематический анализ.Наряду с изложением начал аналитической геометрии и математического анализа (дифф. и интегр. исчисления),книга содержит понятия о степенных и тригонометрических и о простейших дифференциальных уравнениях,а также затрагивает ряд разделов и тем из физики.', 577, 577);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('96d67edd-8eb2-4e9e-80e7-6e0079983c54', '2013-01-15 00:00:00.000000', 43, 19, 0, 'RSHVTg3huLMC', 'Мысли позитивно - живи отлично!', 'http://books.google.com/books/content?id=RSHVTg3huLMC&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Автор многочисленных бестселлеров Наталия Правдина утверждает, что можно изменить свою судьбу в лучшую сторону, изменив образ мыслей. Думая про успех, процветание, любовь и счастье, мы притягиваем все это в свою жизнь. Наталия, опираясь на многочисленные реальные жизненные истории, показывает, как можно избавиться от разнообразных страхов, затаившихся в сознании практически каждого человека. Советы Наталии Правдиной, вдохновляющий, позитивный настрой книги помогут читателю преодолеть негативизм мышления. Позитивное сознание ускорит исполнение желаний и откроет путь к новой счастливой жизни!', 63, 57);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('e0fd180b-ccc4-46c6-a863-9574a7275853', '2022-03-10 00:00:00.000000', 359, 37, 0, '76FjEAAAQBAJ', 'Эволютсия: Факт ё Имон?', 'http://books.google.com/books/content?id=76FjEAAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Назарияи эволютсия ба як маъно ба дини илмӣ табдил ёфтааст. Қариб ҳамаи олимон онро қабул карданд ва бисёриҳо омодаанд, ки мушоҳидаҳои худро барои мувофиқат кардан ба он тоб оранд. Олимони эволюционист бо суханони худ, ки дар ин китоб чоп шудаанд, иштибоҳҳои «эътиқод»-и худро шарҳ медиҳанд. Эволютсия бо далелҳо мувофиқат намекунад! Эҷоди махсус мекунад!', 0, 0);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('c26cf7df-1324-4b5b-adbd-399cb2e0806f', '2013-02-20 00:00:00.000000', 43, 19, 0, 'fcBWRHY7yIwC', 'Источники жизненной силы', 'http://books.google.com/books/content?id=fcBWRHY7yIwC&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Перед вами краткое руководство по волшебному преображению жизни с помощью позитивного мышления и фэншуй, основанное на материалах книг: "Я обретаю силу! Чудесные практики исцеления" и "Худеем с удовольствием", дополненное новыми матералами. Наталия Правдина утверждает, что можно обрести радостное состояние души, изменив образ жизни и способ мышления. В этой книге речь пойдет о том, как укрепить здоровье, наполнить себя силой, стать моложе, жизнерадостнее и энергичнее. Вы узнаете о практиках цигун, которые гармонизируют тело и дух, насыщают человека энергией. Автор приводит эффективные упражнения, медитации, специальные методы энергетической защиты и очищения, которые помогут вам преобразить себя. Советы по фэншуй помогут создать в доме полезную для здоровья гармоничную, радостную атмосферу.', 63, 57);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('dbd53ea7-bb9c-4a6f-9ebb-5f77c243e793', '2012-01-20 00:00:00.000000', 433, 30, 0, 'EtvBDwAAQBAJ', 'Treaty Series 2313', 'http://books.google.com/books/content?id=EtvBDwAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'In accordance with Article 102 of the Charter and the relevant General Assembly Resolutions, every treaty and international agreement registered or filed and recorded with the Secretariat since 1946 is published in the United Nations Treaty Series. At present, the collection includes about 30,000 treaties reproduced in their authentic languages, together with translations into English and French, as necessary. The Treaty Series, where treaties are published in the chronological order of registration, also provides details about their subsequent history (i.e., participation in a treaty, reservations, amendments, termination, etc.). Comprehensive Indices covering 50-volume-lots are published separately. A Standing Order service is available for the Series and out-of-print volumes are available on microfiche.', 1275, 1275);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('68e354f3-063c-47d7-982c-4ff37e40b9ee', null, 873, 31, 0, 'Fc39AgAAQBAJ', 'Курс аналитической геометрии и линейной алгебры', 'http://books.google.com/books/content?id=Fc39AgAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', null, 501, 501);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('cf1361bc-4a2f-43c3-b133-3120d84e08f1', '2016-05-09 00:00:00.000000', 160, 2, 0, '2QQiDAAAQBAJ', 'Seamless Abstract Patterns Coloring Book for Grown-Ups 1', 'http://books.google.com/books/content?id=2QQiDAAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'The perfect coloring book for every abstract designs lover. A collection of 40 beautiful seamless abstract patterns. Vivid colors, great illustrations and imagination are all you need to keep calm and relaxed! Each picture is printed on its own 8.5 x 8.5 inch page so no need to worry about smudging.', 505, 505);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('30bd847c-4f9e-457a-97ea-46c015e0e07c', '2010-01-07 00:00:00.000000', 214, 49, 1, 'KuGJAwAAQBAJ', 'McGraw-Hill''s Chinese Dictionary and Guide to 20,000 Essential Words', 'http://books.google.com/books/content?id=KuGJAwAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'No other dictionary is available on the market with this unique look-up system Contains indices that allows look-up by all other approaches: pinyin, radicals and strokes, and English meanings Contains 2,000 commonly used characters, which then allows the user to create 20,000 words with those characters and numerous example sentences', 3584, 3584);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('5d1a0657-90b9-4785-a573-519ec8f8b440', null, 2, 40, 0, '8Tw1DwAAQBAJ', 'Kuishinbo!', 'http://books.google.com/books/content?id=8Tw1DwAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'The worldâs greatest food battle, the Kui-lympics! 10 competitors made it to the finals and now they must compete in the 5,000 meter endurance Food battle! Itâs a total of 15 KGs of food! Can they eat it all? Mantaro!!', 364, 328);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('1139c875-d6e2-4d29-bdc8-eb3a4a4b0544', '2019-03-02 00:00:00.000000', 873, 23, 0, 'Z192DwAAQBAJ', '9-й день недели', 'http://books.google.com/books/content?id=Z192DwAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Журналист и полицейский снова в центре противостояния двух могущественных сил. На этот раз поле битвы – политическая арена. Политический триллер «9-й день недели» – продолжение фэнтези «8-й день недели». Книга содержит нецензурную брань.', 125, 112);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('aff4d2cd-862f-48d5-8a7c-7519bd9ed5aa', null, 172, 31, 0, 'QZ79AgAAQBAJ', 'Приложение анализа к геометрии', 'http://books.google.com/books/content?id=QZ79AgAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Родословное дерево дифференциальной геометрии уходит своими корнями по меньшей мере столь же глубоко, как родословное дерево анализа бесконечно малых. Более того, в известной степени дифференциальная геометрия даже старше анализа. В самом деле, простейшие образы и понятия дифференциальной геометрии стали объектом точного математического знания раньше, чем понятия анализа выкристаллизовались даже в первичной своей форме. В книге собраны работы Гаспара Можна по дифференциальной геометрии.', 456, 456);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('ad747c90-d57a-49cb-9162-61feee09d4e8', '2013-11-15 00:00:00.000000', 431, 51, 0, 'MplEAgAAQBAJ', 'Traditional Chinese Thinking on HRM Practices', 'http://books.google.com/books/content?id=MplEAgAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Suggests important ramifications for both Western and Eastern Human Resources Practices and is the first research of its kind to empirically investigate the effect of Chinese core values, which originated from Chinese traditional thinking, on HRM practices in China.', 5771, 5771);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('5b749e83-fb09-46ed-994e-94635d04543a', '2014-12-01 00:00:00.000000', 369, 50, 0, 'g6zVBgAAQBAJ', 'Taylor Swift - 1989 Songbook', 'http://books.google.com/books/content?id=g6zVBgAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', '(Easy Guitar). When Taylor Swift''s first official "pop" album, 1989 , was released in October 2014, it quickly achieved record-breaking sales. According to Billboard , it sold almost 1.3 million copies in its debut week, the largest sales week for an album since 2002 and made Taylor the first artist ever to have three albums top $1 million in sales in a week. Our easy guitar songbook features all 13 songs from the album, including the mega-hit "Shake It Off," plus: All You Had to Do Was Stay * Bad Blood * Blank Space * How You Get the Girl * I Know Places * I Wish You Would * Out of the Woods * Welcome to New York * and more.', 1447, 1302);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('655f84e0-e06f-49a0-80c7-ee9e40c228fc', '2013-01-20 00:00:00.000000', 43, 19, 1, 'VJwrORk7Oy0C', 'Формулы любви', 'http://books.google.com/books/content?id=VJwrORk7Oy0C&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Перед вами краткое руководство по волшебному преображению жизни с помощью позитивного мышления и фэншуй, основанное на материалах книги "Я излучаю любовь и силу! Волшебные уроки счастья для новой женщины". Наталия Правдина, самый известный в России дипломированный мастер фэншуй, собрала в книге эффективные советы, полезные для каждого, кто плавает в бурном море любовных отношений. В книге, основной темой которой является счастье в любви, рассказывается, как преобразить свою жизнь, как привлечь в нее больше радости, любви и счастья. Любовь к себе, самоуважение, развитие своих талантов, искренний интерес к окружающим позволят женщинам исполнять все свои желания, чувствовать себя достойными восхищения и любви. Здесь вы узнаете ответы на важнейшие вопросы: Как найти любовь? Какова формула взаимной любви? Как создать в своем доме атмосферу любви и гармонии? Эта книга поможет вам привлечь в свою жизнь любовь и сохранить ее на долгие годы.', 63, 57);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('0daa6899-376c-4108-98b0-cbc050ab69ab', '2011-10-10 00:00:00.000000', 238, 18, 0, 'x5bFUVmORfMC', 'Чудотворные иконы', 'http://books.google.com/books/content?id=x5bFUVmORfMC&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Много веков святые молитвы укрепляют православных христиан, служат путеводной нитью каждому сердцу, ищущему Бога. Мы молимся перед чудотворными иконами в нуждах, скорбях и напастях, уповая на их силу, воздаем им благодарность и просим прощения за свои грехи. В книге собраны чудотворные иконы, даны краткие жития святых угодников Божиих, описания икон, а также сами молитвы к Божией Матери, Архангелам, Ангелам, пророкам, апостолам, святителям, мученикам, преподобным, бессребреникам и другим святым, скорым ходатаям пред Господом Богом нашим Иисусом Христом. Эта книга станет для вас помощником в житейских бурях и духовных стремлениях', 181, 163);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('f2380f66-c6ba-4eb6-b716-59dcb53391f5', null, 18, 30, 0, 'TU_6AgAAQBAJ', 'Гидродинамика', 'http://books.google.com/books/content?id=TU_6AgAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', null, 665, 665);
