INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('ba7f0fcb-9b85-4617-a666-3c75a1874725', null, 198, 44, 0, '0sX-AgAAQBAJ', 'Электромагнитные переходные процессы в электрических системах', 'http://books.google.com/books/content?id=0sX-AgAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'В книге рассмотрены электромагнитные переходные процессы в электрических системах и даны методы их расчета. Применение методов иллюстрировано числовыми практическими примерами. Книга предназначена в качестве учебника для студентов специальностей: «Электрические станции», «Электрические системы и сети», «Кибернетика электрических систем»; для студентов других электроэнергетических и электротехнических специальностей она может служить учебным пособием. Книга может быть также использована аспирантами ,научными и инженерно-техническими работниками энергетической промышленности', 720, 720);
