INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('029eb198-abd6-4cab-8693-232cf1023438', '2021-04-20 00:00:00.000000', 264, 51, 0, 'HId2DQAAQBAJ', 'Экономика организации (предприятия). Практикум', 'http://books.google.com/books/content?id=HId2DQAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Практикум содержит основные понятия, методику расчета основных показателей, примеры решения задач, задачи для самостоятельного решения и тесты для самоконтроля. Позволит студентам приобрести практические навыки по вопросам управления производственными ресурсами организации и оценки эффективности их использования, механизма функционирования и развития организации, формирования финансовых результатов организации, оценки эффективности ее финансово-хозяйственной деятельности. Для студентов и учащихся экономических специальностей учреждений высшего и среднего специального образования, слушателей системы повышения квалификации и переподготовки кадров.', 228, 205);
