INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('2f344454-e8a9-4fd2-b475-06da64dc3048', null, 896, 44, 0, 'chT_AgAAQBAJ', 'Датчики Холла и магниторезисторы', 'http://books.google.com/books/content?id=chT_AgAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'В книге охвачена совокупность вопросов, связанных со свойствами, технологией изготовления, конструкцией и применением датчиков Холла и магниторезисторов. Книга предназначена для инженеров и техников, работающих в области измерительной техники, радиоэлектронных, вычислительных и автоматических устройств. Книгой могут пользоваться также студенты технических вузов.', 236, 236);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('bb863893-069b-407a-8176-876214037b67', '2014-02-07 00:00:00.000000', 485, 23, 0, 'ql_QAgAAQBAJ', '2024-й', 'http://books.google.com/books/content?id=ql_QAgAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Юрий Никитин – популярный автор, работающий в различных литературных жанрах, таких как фэнтези, научно-популярная литература, исторический роман и др. Для писателя славянское фэнтези является одним из любимых. Созданный в этом стиле цикл "Трое из Леса" завоевал широкую популярность среди читателей. За последние несколько десятков лет из-под пера Никитина вышло около ста романов, разошедшихся огромными тиражами.', 59, 59);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('071957fc-4335-4c4a-8fbf-8ac0ee895378', '2020-12-31 00:00:00.000000', 718, 23, 1, 'lYgREAAAQBAJ', 'Коридор в 1937-й год. Книга 3.', 'http://books.google.com/books/content?id=lYgREAAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Эта книга - продолжение истории о студентах, которых угораздило очутиться в 1937-м году. Историю лучше читать по порядку, с первой книги.', 152, 152);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('524398ff-c972-429b-94a2-8860dac6512b', '2017-09-05 00:00:00.000000', 485, 4, 0, 'CdN7CwAAQBAJ', '93-й. Год великого поражения', 'http://books.google.com/books/content?id=CdN7CwAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'В данной книге представлены общественно-политические статьи и выступления Ю.П. Власова за период от конца 80-х до середины 90-х годов.В них отражается обстановка, которая была в стране в то время, ясно видны предпосылки событий 93-го года. Анализируя их, автор приходит к обоснованному выводу о том, что, по сути, они явились буржуазной контрреволюцией, «революцией богатых против бедных», и окончательно похоронили надежды на лучшее будущее для России и народа при существующем политическом строе.', 161, 145);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('7a2ed15d-98b0-4fa5-a280-930e54964ac4', '2014-01-27 00:00:00.000000', 485, 23, 0, '8pi2AgAAQBAJ', 'Москва, 2000-й...', 'http://books.google.com/books/content?id=8pi2AgAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Юрий Никитин – популярный автор, работающий в различных литературных жанрах, таких как фэнтези, научно-популярная литература, исторический роман и др. Для писателя славянское фэнтези является одним из любимых. Созданный в этом стиле цикл "Трое из Леса" завоевал широкую популярность среди читателей. За последние несколько десятков лет из-под пера Никитина вышло около ста романов, разошедшихся огромными тиражами.', 19, 19);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('62a54f6d-a3e0-45e6-b063-b7abb49a1321', '2019-07-04 00:00:00.000000', 445, 23, 0, '_7-gDwAAQBAJ', 'Тренажёр по чистописанию. Учимся писать за 30 занятий. 1-й класс', 'http://books.google.com/books/content?id=_7-gDwAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', '«Тренажёр по чистописанию. Учимся писать за 30 занятий. 1-й класс» представляет собой рабочую тетрадь, в которой первоклассники потренируются аккуратно и красиво писать буквы, их элементы, варианты соединений, а также слоги, слова и предложения. Занимаясь по этому пособию, ребёнок сможет выработать красивый почерк и увеличить скорость письма всего лишь за 30 занятий. Пособие можно использовать в качестве дополнительного материала на уроках в классе, а также для работы дома.Для начального образования.', 123, 111);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('e37b138c-d529-47b8-8dc9-c76e76337578', '2021-02-18 00:00:00.000000', 547, 23, 1, 'XRMfEAAAQBAJ', '1003-й свободный человек', 'http://books.google.com/books/content?id=XRMfEAAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'В рассказах Марты Антоничевой все связано: мрачное будущее – следствие цифровой реальности и не изжитых еще 90-х. Заслуживает ли человек спасения, или он обречен? Каждый рассказ – лаконичный, но неожиданный ответ. Тот, кто пройдет этот лабиринт за автором до конца, станет другим человеком.«Дорогой читатель! Не нужно долго думать. Просто открой эту книгу на рассказах „Мишура из фантиков и оберток“, „Руководство к действию“, „I lave you“. Не обязательно читать все из них, можно хотя бы один. Если рассказ понравился, понравится и вся книга. Нет? Значит, не судьба. Прекрасный сборник предельно честных историй», – Алексей Сальников, писатель.«Люди – странные существа. Мы настолько свыклись с этим, что перестали замечать. Книга Марты Антоничевой – не только предупреждение о том, насколько болезненно и абсурдно обыденное существование, но и напоминание, что за всеми нашими причудами скрывается ищущая выход наружу, к свету, человечность», – Сергей Морозов, литературный критик.«Книга Марты Антоничевой – это интереснейший гибрид шершавого, бытового реализма, посвященного в мелкие затруднения и привычки обычного современного человека, и фантастики, подкручивающей этот невзрачный быт до экзистенциальной метафоры», – Валерия Пустовая, литературный критик.«В рассказах Марты Антоничевой от необычного до жуткого – один шаг. Это шаг из зоны комфорта в область странного и заставляющего тревожно задуматься – и, может, понять очень важное», – Шамиль Идиатуллин, писатель.', 473, 426);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('565d78f0-b312-4c10-ad89-6f70f0caadd6', '2019-11-29 00:00:00.000000', 90, 4, 0, 'ovTADwAAQBAJ', 'История чемпионатов СССР по футболу. 1-й том (1936—1969 годы)', 'http://books.google.com/books/content?id=ovTADwAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Эта книга для всех любителей футбола. Она освещает историю футбола, футбольных клубов и футбольных регионов в период СССР. Основа книги – история развития футбола России, начиная с самого начала возникновения футбола в Российской империи, его развитие в советский период вплоть до 1991 года в рамках чемпионата СССР по футболу.', 500, 450);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('e993bd8b-2d76-424c-826b-670d8d577915', '2020-06-23 00:00:00.000000', 158, 23, 0, 'k9HsDwAAQBAJ', '224-й', 'http://books.google.com/books/content?id=k9HsDwAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Представьте себе ситуацию, при которой может возникнуть «сознание» у робота... Как Вы думаете, какое оно всё-таки будет? И какие желания оно может генерировать?', 186, 167);
