INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('d399b848-9d2a-4121-bf37-711ce0a7f5f4', '2017-09-05 00:00:00.000000', 451, 23, 1, 'QP8rDQAAQBAJ', 'Дзержинский 119-й (Недокументальная быль)', 'http://books.google.com/books/content?id=QP8rDQAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Роман петербургского писателя и публициста Игоря Бойкова «Дзержинский 119-й» погружает читателя в атмосферу бурных политических битв начала 2000-х, которые известны автору не понаслышке.Главный возмутитель спокойствия тех лет – созданная Эдуардом Лимоновым радикальная партия – пытается вытащить своего вождя из застенков Лефортовского СИЗО, обеспечив его депутатским иммунитетом в результате победы на выборах в городе Дзержинске.На этом историческом фоне автор пишет коллективный портрет молодой гвардии грядущей России: юные и бескомпромиссные, наивные и упорные, «партийцы»-лимоновцы вступили в неравную борьбу с системой. Они проиграли, но столкновение с реальностью изменило жизнь каждого из них.Уникальная, но неизвестная до сих пор широкой российской общественности политическая схватка в Дзержинске – это уже страничка наиновейшей истории России, знать и помнить которую надо каждому патриоту нашей страны.', 250, 225);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('fb3c8678-32d4-48f5-b289-c6275b0bd93d', '2021-07-08 00:00:00.000000', 90, 23, 0, '4lc3EAAAQBAJ', 'Русская история. 6-й класс. Homeschooling', 'http://books.google.com/books/content?id=4lc3EAAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Этот учебник не заменяет школьного учебника истории. Он является дополнительным пособием для усвоения и понимания основной, стержневой истории отечества. Истории русского народа, объединяющего в систему весь венок национальностей, входящих в современную Российскую Федерацию.', 25, 22);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('b245dda6-3b8e-46aa-be5d-23e91540457b', '2014-05-28 00:00:00.000000', 469, 23, 1, 'Yf2lAwAAQBAJ', '1900-й. Легенда о пианисте', 'http://books.google.com/books/content?id=Yf2lAwAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Новеченто — 1900-й — это не только цифра, обозначающая год. Так зовут гениального пианиста-самоучку, родившегося на борту океанского лайнера. У него нет ни документов, ни гражданства, ни родственников, только имя, данное кочегаром, нашедшим ребенка. 1900-й никогда не покидал корабля, никогда не ступал на твердую землю. В бурю и в штиль он не отрывается от клавиш. Книга представляет собой драматический монолог, пронизанный удивительной музыкой и океанским ветром. Кажется, что Господь Бог управляет Вселенной, перебирая людские судьбы, как клавиши громадного рояля.', 0, 0);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('244540d8-dae5-443a-a4d6-624f873a31e3', '2019-12-17 00:00:00.000000', 213, 30, 0, 'N9vEDwAAQBAJ', 'Переписка. 119-й год нобелевской эры', 'http://books.google.com/books/content?id=N9vEDwAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Сформирована «полуавтоматически» из моего «лытдыбр» за пару (или чуть больше) месяцев. Книга содержит нецензурную брань.', 45, 40);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('adf9d1fa-73ac-46c0-8895-31bbc2c0966c', '2021-07-15 00:00:00.000000', 418, 23, 0, 'D244EAAAQBAJ', '13-й канал. Повесть', 'http://books.google.com/books/content?id=D244EAAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Игорь и представить не мог, что в его захолустный район будет проведено кабельное телевидение, и когда это происходит, он с радостью дает заявку на подключение. «Только никогда не включай „13-й канал“», – такое странное предупреждение Игорь услышал от своей знакомой. Книга содержит нецензурную брань.', 400, 360);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('07d73e1c-4da6-4203-b6e6-352ef34b07b8', '2019-10-22 00:00:00.000000', 164, 23, 0, 'XIG4DwAAQBAJ', 'Вандербикеры с 141‐й улицы', 'http://books.google.com/books/content?id=XIG4DwAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Вандербикеры всегда жили в красном доме на 141-й улице. А дом всегда принадлежал их соседу – противному мистеру Байдерману. За пять дней до Рождества Вандербикеры собрались на семейный совет. Мистер Байдерман попросил их покинуть дом! Мама и папа в отчаянии, зато младшие Вандербикеры решили не сдаваться.Пятеро непоседливых детишек, пёс и кролик докажут: лучших жильцов Байдерману не найти. В ход пойдёт всё: подкуп, комплименты, угрозы, печенье и даже рождественские песни. Смогут ли детишки найти путь к сердцу угрюмого соседа и остаться жить в лучшем доме на свете?', 373, 336);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('9646dc42-cea1-44ea-8121-f9583a983d1f', '2021-01-18 00:00:00.000000', 140, 30, 0, 'rjcVEAAAQBAJ', 'Тесты по биологии. 5-й класс. Часть 1', 'http://books.google.com/books/content?id=rjcVEAAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Тематические тесты, вопросы которых позволяют проверить уровень знаний учащихся по биологии за 5-й класс. Каждая тема полностью соответствует содержанию учебника «Биология. Бактерии, грибы, растения. 5 класс. Издательство Вертикаль. ФГОС», по линии В. В. Пасечника.', 250, 225);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('fd649340-882b-4b96-a1a4-134d4f42828f', '2018-12-01 00:00:00.000000', 562, 44, 0, 'vpVMDwAAQBAJ', 'Самолет Як-18Т 36-й серии. Анализ конструкции и летной эксплуатации', 'http://books.google.com/books/content?id=vpVMDwAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Книга может оказаться полезной для авиаспециалистов, изучающих конструкцию и летную эксплуатацию планера и функциональных систем учебного самолета Як-18Т 36-й серии.', 350, 315);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('21a92b3c-c208-4353-bd88-a5b99e8cbf63', null, 66, 26, 0, 'tjDgDQAAQBAJ', 'История Французской революции. 3-й том', 'http://books.google.com/books/content?id=tjDgDQAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', null, 350, 350);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('9c6f7ada-508a-4e89-b569-5815a057633d', '2020-05-28 00:00:00.000000', 738, 23, 0, '673nDwAAQBAJ', 'Хикикомори. 33-й день самоизоляции', 'http://books.google.com/books/content?id=673nDwAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Книга написана в юмористическом жанре. В ней я делюсь с читателями своими наблюдениями, сделанными на тридцать третий день самоизоляции в Москве в мае 2020 года.', 45, 40);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('25aa7410-7d55-4b96-9692-f9bd0ea72866', '2021-01-14 00:00:00.000000', 140, 30, 1, 'ieoTEAAAQBAJ', 'Тесты по биологии. 5-й класс. К учебнику В. В. Пасечника. Растения (ботаника)', 'http://books.google.com/books/content?id=ieoTEAAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Тесты полностью соответствуют содержанию учебника В. В. Пасечника «Биология. Бактерии, грибы, растения. 5-й класс, Издательство Дрофа».', 500, 450);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('fd9314c6-516f-4719-a51d-29dee33670bd', '2018-12-20 00:00:00.000000', 378, 50, 0, 'q1cvDwAAQBAJ', '1-й квартет для 2 скрипок, альта и виолончели', 'http://books.google.com/books/content?id=q1cvDwAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', null, 25, 22);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('29472336-e21b-4266-868d-c030317701a8', '2021-07-10 00:00:00.000000', 418, 30, 0, '6JsZEAAAQBAJ', 'А если так?.. 2020-й', 'http://books.google.com/books/content?id=6JsZEAAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Уникальная книга перед вами – сборник статей с канала на Яндекс Дзен за один календарный год. Это своего рода хронология важнейших и знаменательных событий. Автор смотрит, думает, переживает и пишет. По зову души. В реальном времени. Книга увлечёт вас по волнам памяти об этом удивительном, переломном годе. Вы переживёте заново водоворот мыслей и эмоций. Увидите разницу между тем, что мы ждём, на что надеемся, чего боимся – и как это происходит на самом деле. Предупреждение: автор статей «на светлой стороне», на созидательных позициях, с объёмным взглядом на происходящее.', 112, 101);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('c2634dfd-77ba-44f9-8a8a-99edcae39db0', '2022-05-15 00:00:00.000000', 73, 16, 0, '1-vGDwAAQBAJ', 'Клетчатка – 5-й элемент здорового питания', 'http://books.google.com/books/content?id=1-vGDwAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Обилие противоречивой информации о правильном питании сводит с ума. Трудно разобраться, что на самом деле полезно, а что вредно? Перед Вами книга о здоровом питании и клетчатке, основанная на 17-тилетнем практическом опыте и огромном массиве научной информации. Вместе с тем, автор легко и увлекательно раскрывает тайны «её величества Клетчатки». Развенчивает окружающие её мифы. Показывает, связанные с ней, маркетинговые хитрости индустрии здорового питания. Раскрывает простые, но не очевидные секреты, мешающие стройности, хорошей физической форме и активному долголетию.', 186, 167);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('3f9b0c9e-20c7-4382-a845-c674f9ede1e2', '2020-05-08 00:00:00.000000', 873, 23, 0, 'KV_jDwAAQBAJ', 'Ставропольский писатель. 3-й коллективный сборник', 'http://books.google.com/books/content?id=KV_jDwAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Третий том издания «Ставропольский писатель» не только знакомит с писателями Ставропольского края, но и предполагает возможность участия в следующих томах сборника новых авторов.Произведения представлены в «авторской редакции».', 25, 22);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('a24229c1-c888-4220-9880-dec89e879cd3', '2021-01-21 00:00:00.000000', 898, 23, 0, 'WmsWEAAAQBAJ', 'Как 9-й «Б» школьную комиссию встречал, или О вреде чтения', 'http://books.google.com/books/content?id=WmsWEAAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', '«...Итак, мы наконец приблизились к тому дню, когда все завертелось с неимоверной силой. Суббота 2019 года. Ярко светило солнце, громко пели птички, асфальт размывало водой после очередного дождя (плохая была весна в том году), люди утопали в грязи по вышеуказанной причине, а я с одноклассниками бесцельно шаталась по школьным коридорам в поисках кабинета, в каком можно было отсидеть последний урок...»', 350, 315);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('7c7f2dd9-a10d-426c-bd3c-6af7e6764ad6', '2017-09-05 00:00:00.000000', 873, 23, 0, '8oAtDwAAQBAJ', 'Гибель 4-й роты', 'http://books.google.com/books/content?id=8oAtDwAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', '«На днях я прочитал в газете извещение о смерти Якова Берсенева. Я давно уже потерял его из виду, и, просмотрев газету, я был удивлен не столько тем, что он умер, сколько тем, как еще он смог прожить до сих пор, имея не менее шести ран – сломанные ребра и совершенно отбитые прикладами легкие...»', 25, 22);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('eb25e62d-0b56-4043-a43f-264e79fc4ce8', '2021-04-30 00:00:00.000000', 873, 4, 0, 'JhEsEAAAQBAJ', '1983-й. Мир на грани', 'http://books.google.com/books/content?id=JhEsEAAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'В настоящей книге речь идет о международном политическом кризисе 1983 года, когда мир в очередной раз оказался на грани ядерной войны. Опираясь на документы американских архивов, Тейлор Даунинг проводит кропотливый анализ драматических событий. В центре внимания – сложности и хитросплетения американо-советских отношений, а также политические приоритеты Рейгана и Андропова – лидеров мировой политики той эпохи.Труд Тейлора Даунинга, сочетающий научную основательность с занимательностью изложения, может быть интересен не только специалистам-историкам, но и самому широкому кругу читателей.В формате PDF A4 сохранен издательский макет.', 562, 506);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('c9b14031-c0d1-4a22-af4b-2498514647e3', '2019-11-29 00:00:00.000000', 418, 11, 0, 'uvPADwAAQBAJ', 'Жизнь без начала и конца. Стихи. Поэмы. 21-й век', 'http://books.google.com/books/content?id=uvPADwAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Прикосновение к Виртуальности: не просто прикосновение – это осмысленное слияние с виртуальной материей.Виртуальный мир наполнен энергетикой. Виртуальность плавно переносит сознание в будущее и из него начинается моделирование прошлого, изменяя прошлое для настоящего, в котором и происходит трансформация разума в прошлое – настоящее и будущее.Точка в Виртуальности Вселенной – это точка начала и конца, в которой началось движение в череде смертей и рождений.', 250, 225);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('04f739f2-3064-4959-a4f9-65c29102c98a', '2019-09-19 00:00:00.000000', 757, 3, 0, 'vGWwDwAAQBAJ', '21-й век. Мир опасен не потому, что некоторые люди творят зло, но потому, что некоторые видят это и ничего не делают', 'http://books.google.com/books/content?id=vGWwDwAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Мы заложники 21-го века, заложники информационного прессинга и экономических махинаций, заложники самих себя. У нас потеряно понятие чести и достоинства. Мы будто увязли в болоте лицемерия и зла. Нам ничего не стоит залить все черной краской. Перестать быть добрыми по отношению друг к другу. Но почему нельзя просто протянуть руку помощи одному, а второму – за неё ухватиться и наконец жить не в болоте, а цветнике.', 150, 135);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('49daec9d-85ab-4bce-9221-cc82173c238f', '2019-11-15 00:00:00.000000', 413, 26, 0, 't5y-DwAAQBAJ', 'Уникальные женщины мира. 19-й век', 'http://books.google.com/books/content?id=t5y-DwAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Уникальность женщин, представленных десятью рассказами в книге, заключается в том, что каждая из них совершила то, чего не существовало и не могло существовать в 19 веке. Откинув предрассудки и условности, они добивались своей цели вопреки укоренившемуся представлению в обществе о месте женщины, обязанной быть дочерью, женой, матерью, но не личностью. Ломая стереотипы, они пришли к потрясающим результатам, проложив зелёную дорогу в науку и культуру женщинам последующих поколений. Доступно всем!', 150, 135);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('8038cd04-0340-47e2-85fd-c34a86f68431', null, 167, 31, 0, 'ge78AgAAQBAJ', 'Линейная алгебра и элементарная геометрия', 'http://books.google.com/books/content?id=ge78AgAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Список лит.: с. 307 (13 назв.)', 236, 236);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('13d009b0-3e87-4cf4-8304-81e1ba1e4c7c', '2018-01-31 00:00:00.000000', 371, 23, 1, '6BEWDgAAQBAJ', 'Трибунал для комдивов. 41-й', 'http://books.google.com/books/content?id=6BEWDgAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Имена целого ряда командиров дивизий оказались вычеркнуты из истории и забыты по причине их осуждения трибуналами. В книге на основе архивных следственно-судебных документов рассказаны трагические истории двадцати комдивов, арестованных и осужденных в первые месяцы войны. Практически все они посмертно реабилитированы. А значит – невиновны и имеют право на память – в числе тех фронтовиков, кто принял на себя первые удары Вермахта.', 250, 225);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('e352c603-9dac-4cb5-9df3-c6f2937e280d', '2015-06-08 00:00:00.000000', 66, 26, 0, 'tOYVCgAAQBAJ', 'Империя. 4-й том. Книга I', 'http://books.google.com/books/content?id=tOYVCgAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Луи-Адольф Тьер (1797—1877) — политик, премьер-министр во время Июльской монархии, первый президент Третьей республики, историк, писатель. Полвека связывают историю Франции с этим именем. Автор фундаментальных исследований «История Французской революции» и «История Консульства и Империи». Оба труда представляют собой очень подробную историю Французской революции и эпохи Наполеона I и по сей день цитируются и русскими, и европейскими историками. Тем более удивительно, что в полном виде «История Консульства и Империи» в России никогда не издавалась. В 1846—1849 годах вышли только первые четыре тома — «Консульство», которое «Захаров» переиздало в новой литературной редакции в 2012 году. Вторая часть — «Империя» — так и не была издана! «Захаров» предлагает вам впервые на русском языке — через полтора века после издания во Франции! — это захватывающее чтение в замечательном переводе Ольги Вайнер.', 500, 500);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('3d283dca-3098-4b76-867e-8845b2219934', '2021-10-21 00:00:00.000000', 88, 51, 0, '_oZJEAAAQBAJ', 'Метод Морозова. Маркетинг и продажи для предпринимателей. 1-й том', 'http://books.google.com/books/content?id=_oZJEAAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Это книга-пособие. Читайте и внедряйте. Ниши разные, точка А разная, но начальный подход всегда одинаковый. Разберитесь со своим фундаментом перед тем, как строить стены бизнеса. На этих страницах вы найдете свою уникальность, определитесь с фокусом, проработайте линейку продаж, начнёте разбираться в трафике и увеличите средний чек. Больше не будет отговорок «Я не понимаю», теперь у вас есть чёткий алгоритм действий. Готовы работать? Тогда до встречи на страницах этой книги.', 350, 315);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('b0c8250d-0682-4e77-9c73-d61656344454', '2021-01-21 00:00:00.000000', 775, 49, 1, 'mpxTDwAAQBAJ', 'Финский с любовью. Й. Линнанкоски. Песнь об огненно-красном цветке / Johannes Linnankoski. Laulu tulipunaisesta kukasta', 'http://books.google.com/books/content?id=mpxTDwAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Роман «Песнь об огненно-красном цветке» – своеобразная интерпретация бессмертного сюжета о Дон Жуане – является одним из самых читаемых произведений финской литературы. Глубокое и яркое, страстное и поэтичное, оно по выражению одного из критиков стало «чудо-цветком, расцветшим в суровых северных лесах и манящим каким-то необычайным, неповторимым ароматом».Текст романа адаптирован по методу Ильи Франка: снабжен дословным переводом на русский язык и необходимым лексико-грамматическим комментарием.Уникальность метода заключается в том, что запоминание слов и выражений происходит за счет их повторяемости, без заучивания и необходимости использовать словарь.Издание способствует эффективному освоению языка, может служить дополнением к учебной программе. Предназначено для широкого круга лиц, изучающих финский язык и интересующихся финской культурой.Дополнительные аудиоматериалы к изданию доступны на сайте издательства www. muravei.ru', 311, 280);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('242d1755-06b5-453f-a05c-b3c4710f6efb', '2019-12-21 00:00:00.000000', 502, 26, 0, 'nxXGDwAAQBAJ', 'О моём брате. Том 3-й: Натюрморты.', 'http://books.google.com/books/content?id=nxXGDwAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Рассказ о трагической судьбе художника, мыслителя и автора, Виталия Гунина, чьи работы и визионерский дар предсказали будущее, предостерегая от необратимых последствий разрушения мира... Виталий Гунин (1964-1990) - художник и резчик, дизайнер и предприниматель, поэт, визионер, ведущий музыкальных программ и профессиональный знаток музыки, антрепренёр, председатель первого в Бобруйске (и, возможно, в Беларуси) многопрофильного кооператива ЛиК - ушёл из жизни, не прожив и полных 26 лет, но сделав за свою короткую жизнь больше, чем другие, ушедшие на вечный покой в преклонном возрасте. Климатическая катастрофа и загрязнение жизненной среды, несущее генетические мутации; опасности, связанные с изуверскими технологиями и с ИИ; и другие угрозы самому существованию рода человеческого: действие тех же сил и человеческих монстров, что убили моего брата. Посмертная трагедия Виталия заключается в том, что его художественные работы - живопись (см. 2-й том: картины маслом), графика (3-й том), резьба по дереву (4-й том), проектно-дизайнерские работы (5-й том), афиши, и пр., - так и остались не пристроенными, и пылятся в чемоданах и мешках, разрушаясь от времени. Его рассказы, стихи; его страшный и полный трагизма Дневник: не опубликованы. Память о нём, человеке, столько сделавшем для родного города Бобруйска: вытравлена из истории города. Об организованном им всесоюзном рок-фестивале, о премьерах знаменитых фильмов, о концертах известных звёзд нигде не упоминается. И даже его могила на бобруйском кладбище заросла кустами и травой, брошенная и одинокая...', 15, 15);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('73cadf39-ef40-4ee6-ba86-18a5dcab5bf4', '2016-02-09 00:00:00.000000', 123, 16, 0, 'XSq-wqnrQxkC', 'Береза, пихта, гриб чага', 'http://books.google.com/books/content?id=XSq-wqnrQxkC&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'В книге читатель найдет множество рецептов применения березы, пихты для профилактики и лечения многих заболеваний', 29, 29);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('c7dfba9a-fe4f-4a7a-b63c-7800061d08be', null, 117, 50, 0, 'wgr7AgAAQBAJ', 'Очерки и исследования по истории русской музыки', 'http://books.google.com/books/content?id=wgr7AgAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Летопись русского музыкального искусства - так хочется назвать этот сборник статей, исследований и очерков, охватывающий лишь небольшую часть творческих работ выдающегося советского учёного, музыковеда-историка, критика и публициста Юрия Всеволодовича Келдыша. Каждый кто обратится к этой книге, невольно заметит в ней то главное, что выделяет её среди многих изданий аналогичного жанра: единство и цельность общей научной концепции. Последовательно и стройно развёртывается перед читателем путь русской музыки. Строгое величавое певческое искусство средневековья; решающий стилевой перелом, наступивший в 17 веке, на пороге нового, петровского времени; первые творческие опыты русских композиторов 18 века, мощный рассвет русской музыки в классическую "глинкинскую" эпоху, а также многое другое вы найдёте в этой книге.', 456, 456);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('1069b1c5-923c-4dd8-adb8-9253eba8d194', '2015-11-19 00:00:00.000000', 25, 4, 0, 'KBn8CgAAQBAJ', 'Объекты культурного наследия. Том 1 и 2. Учебник', 'http://books.google.com/books/content?id=KBn8CgAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'В учебнике рассматриваются проблемы, организационные формы и методы градостроительства, связанные с охраной объектов культурного наследия. В томе I освещаются особенности отечественного и зарубежного законодательства в этой области, теоретические основы охраны объектов культурного наследия и регенерации исторических мест. Раскрываются сложившиеся подходы в исследованиях и разработках исторической среды. Даются обоснования направлений исследования, разработки мероприятий по сохранению объектов культурного наследия. Рассматриваются особенности различных объектов: исторических поселений, архитектурных ансамблей, историко-культурных и природных комплексов. В томе II освещаются основные задачи охраны объектов культурного наследия на уровнях территориального планирования, градостроительного зонирования, установления градостроительных регламентов, разработки проектов планировки. Раскрываются принципы установления зон охраны объектов культурного наследия, их границ, градостроительных регламентов. Даются обоснования эффективных средств регенерации. Рассматриваются направления разработки мероприятий по сохранению и регенерации исторических поселений, достопримечательных мест. Выявляются специфические особенности функционально-планировочной организации, использования принципов композиции в регенерации исторической среды в условиях применения действующего законодательства. Издание адресовано обучающимся на бакалавров по направлению «Градостроительство».', 350, 350);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('7a135aa5-6259-4beb-a47d-d6f096d84503', null, 7, 44, 0, 'NPD9AgAAQBAJ', 'Первые шаги по устройству пасеки', 'http://books.google.com/books/content?id=NPD9AgAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Перед Вами пятое издание известной книги Вс. Шимановского «Пасека при народной школе». Многие указывали автору на то, что «такое заглавие уменьшает круг читателей, и нуждающиеся в указаниях при первых шагах на пасеке, не прибегают к ней, как к книге, имеющей специальное назначение». Автор переработал книгу и изменил название. Основная цель книги по словам Шимановского – «это руководительство молодым пчеловодом шаг за шагом, что представляет собою как бы дневник работ. В последних же главах я все свожу воедино, в цельный план с указаниями, где читатель может найти подробности». Он считал, что «Раз первые шаги будут пчеловодом пройдены, тогда к его услугам и толстые книги, и … периодические пчеловодные издания».', 236, 236);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('1eb0e85a-7ea6-4839-becb-ea95952be0cf', '2020-01-08 00:00:00.000000', 151, 51, 0, 'RbvODwAAQBAJ', 'Эконометрика', 'http://books.google.com/books/content?id=RbvODwAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Учебно-методическое пособие предназначено для бакалавров и студентов ссузов экономических и других направлений (специальностей) очной и заочной форм обучения. В учебно-методическом пособии представлен краткий курс лекций по основным темам учебной дисциплины «Эконометрика». Студентам предлагаются также контрольные вопросы, задачи и тесты которые позволяют им самостоятельно выяснить, как они усвоили основные задачи, принципы и методы эконометрики, особенности и границы их применения. Текст приводится в авторской редакции.', 122, 109);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('7f6988af-e7a8-4f1f-a9f9-a72395f6ab05', '2020-01-30 00:00:00.000000', 485, 0, 1, 'r88tDwAAQBAJ', 'Гражданское право в схемах. 4-е издание. Учебное пособие', 'http://books.google.com/books/content?id=r88tDwAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'В данном учебном пособии, которое является четвертым, переработанным и дополненным изданием, в наглядной, схематичной форме излагается курс Общей и Особенной частей гражданского права. В первой части рассматривается также подотрасль «Наследственное право». Во второй части анализируются отдельные виды гражданско-правовых договоров и обязательств. При разработке схем использовались Гражданский кодекс РФ, федеральные законы, подзаконные акты, судебно-арбитражная практика и т. д. Издание подготовлено по состоянию законодательства на январь 2017 г. Пособие предназначено для студентов юридических вузов и факультетов, преподавателей, научных работников, предпринимателей, руководителей и специалистов организаций всех форм собственности, работников государственных структур, судей, депутатов и всех, кто интересуется данной тематикой.', 230, 230);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('fb6a4964-1e46-4ecb-9a6b-223fa7ace187', '2015-07-10 00:00:00.000000', 485, 51, 0, 'EVglCgAAQBAJ', 'Стратегическое управление персоналом. Краткий курс', 'http://books.google.com/books/content?id=EVglCgAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'В книге рассматривается управление персоналом с точки зрения стратегического управления организацией. Приводятся характеристика бизнес-стратегий и анализ различных подходов к стратегическому управлению персоналом, определяется роль эффективной организационной структуры и службы управления персоналом в стратегическом управлении. Отдельно представлены организационные стратегии, связанные с управлением знаниями, организационной культурой, управлением изменениями и формированием бренда работодателя, а также отдельные функциональные стратегии управления персоналом. Книга предназначена для студентов бакалавриата и магистрантов, обучающихся по направлению «Управление персоналом», специалистов по работе с персоналом.', 150, 150);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('785f6099-3d69-4f21-91fe-800a68c29ae5', '2013-11-21 00:00:00.000000', 485, 51, 0, '9XokAgAAQBAJ', 'Микроэкономика. 2-е издание. Учебник', 'http://books.google.com/books/content?id=9XokAgAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Представлен стандартный курс микроэкономического анализа, который по своему содержанию отвечает действующим Государственным образовательным стандартам по направлению «Менеджмент». Учебник отличают простота и краткость изложения, а также ориентация на развитие компетенций обучающихся. Главы снабжены краткими выводами, решениями типовых задач, списками учебной и дополнительной литературы. Для студентов неэкономических специальностей, изучающих экономическую теорию, а также предпринимателей, стремящихся к углублению представлений о функционировании рынков и овладению принципами принятия хозяйственных решений.', 199, 199);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('678cb2f8-f500-4f90-89aa-909b77fc51ce', '2013-06-28 00:00:00.000000', 485, 0, 0, 'W-AYaSYmkxAC', 'Римское право. Учебное пособие', 'http://books.google.com/books/content?id=W-AYaSYmkxAC&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'В пособии в соответствии с программой изложены основные разделы курса римского права в юридических вузах, дается представление как об историческом развитии институтов римского права, так и об их цивилистическом понимании. В отдельных разделах логически последовательно проанализированы конкретные институты права и теоретические принципы, положенные в их основу, показаны их юридическое значение, соотнесенность с другими институтами права, место в правовой системе в целом. Приведен хрестоматийный материал, подобранный из фрагментов Институций Гая и Дигест Юстиниана, схематично размещенных соответственно отдельным разделам римского частного права. Для студентов высших учебных заведений, обучающихся по специальности "Юриспруденция". Также может быть полезно аспирантам, преподавателям и специалистам в сфере истории права и частного права.', 119, 119);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('55f20a78-0527-414e-8ddc-922c4ac97bdd', null, 485, 49, 0, 'C9n-AgAAQBAJ', 'Письменный перевод с русского языка на английский', 'http://books.google.com/books/content?id=C9n-AgAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Данная книга представляет собой первую в нашей стране попытку создать курс письменного перевода с русского языка на английский. Она может быть использована как при подготовке переводчиков, так и в целях повышения квалификации преподавателей английского языка, а также всех изучающих английский язык.', 708, 708);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('eef461ee-f145-4471-b1da-8b6a70860ccc', '2017-03-15 00:00:00.000000', 10, 30, 0, 'g7JbDgAAQBAJ', 'Экология. 2-е издание. Учебник', 'http://books.google.com/books/content?id=g7JbDgAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Рассмотрены современные представления о возникновении жизни на Земле, связанные с формированием биосферы. Описаны структура биосферы, закономерности существования и развития экосистем, взаимоотношения организма и среды, экологии и здоровья человека, глобальные проблемы окружающей среды. Особое значение придается вопросам экологии человека, анализу антропогенных воздействий на экосистемы и биосферу, включая экологические кризисы. Уделено внимание основам экологического права, профессиональной ответственности, международному сотрудничеству в области охраны окружающей среды, экологическим принципам рационального использования природных ресурсов и охраны природы, а также элементам системы управления качеством окружающей среды и концепции устойчивого развития, определяющей роль человеческого общества в социальной сфере. Содержатся сведения об антропогенном и естественном загрязнении окружающей среды и инженерных методах и средствах защиты окружающей среды от техногенных воздействий. Соответствует Федеральному государственному образовательному стандарту высшего профессионального образования третьего поколения. Для студентов бакалавриата технических вузов всех специальностей. Полезен также магистрантам, аспирантам и специалистам.', 299, 299);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('29aff69b-7ea1-4f30-a68d-c9130d4d53d4', null, 873, 44, 0, 'AiD_AgAAQBAJ', 'Металлургия чугуна, железа и стали в общедоступном изложении', 'http://books.google.com/books/content?id=AiD_AgAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'This book, "Metallurgiya chuguna, zheleza i stali v obschedostupnom izlozhenii," by D. M. Tatarchenko, is a replication of a book originally published before 1932. It has been restored by human beings, page by page, so that you may enjoy it in a form as close to the original as possible.', 720, 720);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('cd7f79a4-dd19-4951-9018-2ebf684ad7a2', '2021-07-09 00:00:00.000000', 342, 0, 0, 'tnw3EAAAQBAJ', 'Международное уголовное право. Учебное пособие', 'http://books.google.com/books/content?id=tnw3EAAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Учебное пособие подготовлено для преподавания курса «Международное уголовное право». В работе рассматриваются основные направления международного сотрудничества государств в области борьбы с преступностью, вопросы квалификации деяний в качестве международных преступлений и преступлений международного характера; исследуются проблемы ответственности индивидов на основе норм международного права; дается обзор организационно-правовых механизмов по борьбе с преступностью, в том числе институциональных. Законодательство приведено по состоянию на 1 июля 2020 г. Пособие ориентировано на бакалавров, магистрантов, аспирантов. Может быть полезным для специалистов, профессионально занимающихся проблемами борьбы с преступностью.', 670, 670);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('2a904c3f-fe11-4397-be3d-3583d3156363', '2017-01-16 00:00:00.000000', 485, 51, 0, 'kvjpDQAAQBAJ', 'Управленческие решения. Учебник', 'http://books.google.com/books/content?id=kvjpDQAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'В учебнике рассматриваются управленческие решения как элемент системы управления организацией, анализируется процесс принятия решений на разных уровнях управленческой иерархии, в том числе придается большое значение решениям в системе государственного управления. В отличие от подобных учебно-методических пособий и учебников акцентируется внимание на проведении организационной диагностики как базиса для разработки сложных, масштабных по своему влиянию управленческих решений, раскрываются методы диагностики организационных проблем, анализа рисков в организации, проведения конфликтологической экспертизы. Большое значение в учебнике придается рассмотрению технологии, методов и инструментов принятия управленческих решений в контексте организационной культуры, специфика которой может существенно повлиять на специфику и качество решений в компаниях. Учебник предназначен для студентов магистерских программ гуманитарных и экономических вузов, слушателей программ МВА, а также специалистов-практиков в области организационного развития, управления человеческими ресурсами и менеджмента.', 229, 229);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('4afae14f-9286-4f25-b4e2-4bd483b341dd', '2020-03-20 00:00:00.000000', 485, 30, 0, '0uLiDwAAQBAJ', 'Электрические подстанции', 'http://books.google.com/books/content?id=0uLiDwAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Приведены общие сведения о режимах работы электрических систем и подстанций; приведены методы расчета токов КЗ и выбора элекгрооборудования подстанций и электросетей, дана классификация подстанций, рассмотрены конструкции трансформаторов РУ, аппаратов ВН и НН подстанций, вопросы их релейной зашиты, требования ПУЭ и ПТЭ к устройству и эксплуатации подстанций. Пособие предназначено для студентов специальностей 140205 и 140211, изучающих СДОЗ, а также может быть полезным студентам среднего профобразования, мастерам и инженерам-электрикам, повышающим свой технический уровень без отрыва от производства.', 415, 373);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('2666c280-c488-426f-9196-d2a8d703e4bc', '2021-04-30 00:00:00.000000', 64, 51, 0, 'D2grEAAAQBAJ', 'Ф*к Ю мани. Как перестать зависеть от денег', 'http://books.google.com/books/content?id=D2grEAAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'В 35 лет мои доходы от инвестиций превысили расходы, и я вышел на пенсию.В своей первой книге я рассказал о том, как именно мне это удалось. Перед вами вторая часть моей истории.Я хочу познакомить вас с удивительным миром самостоятельных пенсионеров. Их много. Их опыт поражает. Их мысли заставляют задуматься.Идея книги – рассказать о движении F.I.R.E (financial independence retire early, финансовая независимость и ранний выход на пенсию). Я хочу показать, что такой образ жизни удобен, интересен и очень даже полезен.Фак Ю Мани – так в кругу ранних пенсионеров называется сумма, которая позволит безболезненно наслаждаться жизнью, послав к черту надоевшую работу. Фак Ю Мани – целая философия жизни.Я написал для вас путеводитель. Вооружившись основными инструментами и знаниями, каждый способен проложить к обеспеченной жизни свой собственный маршрут. Желаю вам удачи!Любые совпадения с реальными лицами и компаниями являются случайными.', 220, 198);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('4f1e6a94-2337-4e5d-b75d-0cedc73b0675', '2017-09-05 00:00:00.000000', 558, 23, 0, 'xHPYnqAqMVYC', 'Ю-ю', 'http://books.google.com/books/content?id=xHPYnqAqMVYC&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', '«Звали ее Ю-ю. Не в честь какого-нибудь китайского мандарина Ю-ю и не в память папирос Ю-ю, а просто так. Увидев ее впервые маленьким котенком, молодой человек трех лет вытаращил глаза от удивления, вытянул губы трубочкой и произнес: «Ю-ю». Точно свистнул. И пошло – Ю-ю...»', 25, 22);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('f03b6416-1d53-4915-9fdb-9f739f6f6567', '2017-09-05 00:00:00.000000', 873, 26, 0, 'UDCYDgAAQBAJ', 'Опыт моей жизни. Книга 1. Эмиграция', 'http://books.google.com/books/content?id=UDCYDgAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', '«...Интересно положить под лупу любую человеческую жизнь и рассмотреть ее по атомам и молекулам. Затем взглянуть на полотно той же жизни с большого расстояния. Ведь жизнь человека, как живое растение: смотришь со стороны – как будто зеленый листок, а под микроскопом... совсем по-другому каждая клетка этого листка открывается...»', 625, 562);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('ca9771cd-aefa-4e84-86ea-5f11035277dc', '2018-02-02 00:00:00.000000', 558, 23, 0, 'NSBKDwAAQBAJ', 'Ю-ю', 'http://books.google.com/books/content?id=NSBKDwAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', '«Ю-ю» Александра Куприна – один из серии рассказов автора про животных, в котором он симпатизирует их чуткой душе, порою более искренней, чем душа человека***. Главная героиня произведения – кошка по имени Ю-ю, которая была необычайно добрая и отзывчивая с хозяевами. Перу автора принадлежат такие известные произведения как «Белый пудель», «В недрах земли», «Винная бочка», «Волшебный ковер», «Друзья», «Жидкое солнце», «Звезда Соломона», «Жрец», «Изумруд», «На реке» и др. Александр Куприн – русский писатель-реалист, автор психологических повестей и рассказов.', 3, 3);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('ae898490-e683-4441-8be8-4f650a41ffeb', null, 485, 30, 0, '8d_7AgAAQBAJ', 'Оптические резонаторы и лазерные пучки', 'http://books.google.com/books/content?id=8d_7AgAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'На основе кратко изложенных общих законов прохождения когерентных световых пучков через оптические системы широкого класса рассмотрены процессы формирования когерентного излучения в оптических резонаторах; проанализированы факторы, определяющие пространственную структуру лазерного излучения; даны рекомендации по выбору типа и параметров резонаторов; приведены сведения о различных методах воздействия на характеристики излучения путем видоизменения резонаторов и внесения в них дополнительных элементов. Основное внимание уделено способам повышения пространственной когерентности излучения и уменьшения его расходимости. Для специалистов, занимающихся разработкой и применением лазеров всех типов, а также теорией оптических систем и вопросами дифракции. Может быть рекомендована студентам оптических специальностей.', 936, 936);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('44b5ac0f-1de1-4cfb-80ed-e6596a321fdd', null, 241, 44, 0, 'Ktf-AgAAQBAJ', 'Электрические машины', 'http://books.google.com/books/content?id=Ktf-AgAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Том II труда Рихтера состоит из трех частей. В первой части подробно изложены основные понятия, служащие для описания и характеристики явлений переменного тока в машинах. Во второй части изучается синхронная машина в ее современных разновидностях и изложены наиболее важные и специфические явления в эксплоатации синхронных машин. В третьей части автор рассматривает одноякорные преобразователи, подробно анализируя важнейшие явления и свойства эпмх машин. И в первой, и во второй части уделено большое внимание методам экспериментального испытания машин и указана методика проектирования их. Книга рассчитана на студентов энергетических втузов и на инженеров.', 456, 456);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('1e4ac709-a711-481d-8a9b-5b225ae94b14', '2013-10-09 00:00:00.000000', 485, 51, 0, 'LbBFAQAAQBAJ', 'Инвестиционный менеджмент. Учебник', 'http://books.google.com/books/content?id=LbBFAQAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Раскрываются сущность, значение, функции инвестиционного менеджмента, проблемы становления и тенденции развития инвестиционных процессов в экономике России, рассматриваются закономерности функционирования инвестиционного рынка. Большое внимание уделяется характеристике источников, методов, форм финансирования инвестиционной деятельности, способам их оптимизации, организации и условиям привлечения иностранных инвестиций. Освещаются вопросы разработки, бизнес-планирования и управления реализацией инвестиционного проекта, подходы к оценке эффективности инвестиций. Определяются сущность, цели формирования и подходы к управлению инвестиционным портфелем. Устанавливаются принципы и последовательность разработки инвестиционной стратегии организации, освещаются вопросы принятия и реализации инвестиционных решений. Рассматриваются подходы к оценке инвестиционного климата и инвестиционной привлекательности отдельных объектов инвестирования, а также вопросы государственного регулирования инвестиционной деятельности в РФ. В конце каждой темы содержатся вопросы для самоконтроля. Для студентов экономических специальностей, аспирантов, преподавателей.', 119, 119);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('24942388-3e84-4823-952f-57fb118d7f2c', null, 485, 34, 0, 'FuD7AgAAQBAJ', 'Фехтование на штыках', 'http://books.google.com/books/content?id=FuD7AgAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Фехтование на карабинах с эластичным штыком,или, как принято его называть, фехтование на штыках, является наиболее доступным и простым из всех видов фехтования.С другой стороны, основанный на общих принципах фехтования, бой на штыках содержит все положительные стороны этого полезного вида спорта. В вольном бою, штыкист должен действовать осмысленно, умело и искусно использовать технику, уметь, в зависимости от обстановки, принимать самые разнообразные решения и проводить и проводить их в жизнь, проявлять быстроту и стремительность, а главное - упорство и непреклонную волю к достижению победы.', 397, 397);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('302cbbfd-c822-484a-9448-d354aa059b8b', '2019-08-28 00:00:00.000000', 272, 4, 0, 'GYMYVuXVxRcC', 'История России. 4-е издание. Учебник', 'http://books.google.com/books/content?id=GYMYVuXVxRcC&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Четвертое издание, переработанное и дополненное. В учебнике изложена история России с древнейших времен до наших дней с учетом новейших данных, накопленных исторической наукой. Для студентов высших учебных заведений, абитуриентов и всех интересующихся историей Отечества.', 370, 370);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('8a9b2680-dadb-4f98-90d5-d035f87ce24a', '2021-03-26 00:00:00.000000', 485, 0, 0, 'FwUmEAAAQBAJ', 'Налоговое право. 3-е издание. Учебник', 'http://books.google.com/books/content?id=FwUmEAAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Учебник по налоговому праву подготовлен коллективами кафедр финансового права и налогового права Московского государственного юридического университета имени О. Е. Кутафина (МГЮА). В нем с позиций современного права освещены все основные темы по данной дисциплине. Учебник раскрывает все основные категории и институты налогового права России, включая вопросы установления и введения налогов и сборов, их видов, структуры налоговых правоотношений, прав и обязанностей налогоплательщиков, проведения налогового контроля, налоговой ответственности, а также взимания федеральных, региональных и местных налогов и сборов в РФ. Учебник позволит лучше ориентироваться в процессах, происходящих в налоговом законодательстве России. Нормативные акты используются по состоянию на 1 февраля 2020 г. Для студентов, аспирантов, научных сотрудников, преподавателей юридических и экономических вузов, всех, кто интересуется проблемами налогового права.', 560, 560);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('13189b33-208c-4b4b-b340-3facd57792f8', null, 485, 30, 1, 'I0f8AgAAQBAJ', 'Справочник по аналитической химии', 'http://books.google.com/books/content?id=I0f8AgAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'This book, "Spravochnik po analiticheskoj himii," by YU. YU. Lur''e, is a replication of a book originally published before 1971. It has been restored by human beings, page by page, so that you may enjoy it in a form as close to the original as possible.', 488, 488);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('2b1d17cf-e490-462b-8bb9-a95eabf4582a', '2020-02-26 00:00:00.000000', 485, 0, 0, '8OqwCgAAQBAJ', 'Право Евразийского экономического союза. Учебник', 'http://books.google.com/books/content?id=8OqwCgAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'В учебнике рассматривается весь комплекс вопросов, связанных с правовыми основами создания и функционирования Евразийского экономического союза (ЕАЭС) между Россией и другими республиками бывшего СССР, правовым регулированием интеграционных процессов в его рамках. В разделе I «Евразийский экономический союз и его правовая система» рассмотрены история создания и современный правовой статус ЕАЭС как организации региональной экономической интеграции, цели, компетенция, система и правовой статус органов ЕАЭС (Высший Евразийский экономический совет, Евразийский межправительственный совет, Евразийская экономическая комиссия, Суд Евразийского экономического союза и др.). В разделе II «Правовое регулирование интеграционных процессов в Евразийском экономическом союзе» рассмотрены интеграционно-правовые механизмы ЕАЭС, обеспечивающие формирование в его рамках общего (единого) рынка со свободным перемещением товаров, услуг, капитала и рабочей силы, координацию внутренней политики и сближение законодательства государств — членов ЕАЭС в разных областях (техническое регулирование, макроэкономическая политика, конкурентная политика, интеллектуальная собственность и др.). Особое внимание уделяется механизмам, предусмотренным правом ЕАЭС для регулирования интеграции в отдельных секторах экономики (энергетика, транспорт, промышленность, сельское хозяйство и др.), а также правовому регулированию внешнеэкономических связей ЕАЭС, включая международную деятельность, таможенное регулирование, единую внешнеторговую политику. Законодательство приводится по состоянию на февраль 2015 г. Для студентов, аспирантов и преподавателей юридических вузов и факультетов, факультетов международных отношений, работников органов государственной власти, внешнеэкономических и внешнеполитических организаций, предпринимателей, а также для всех заинтересованных читателей.', 180, 180);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('f28f8593-797a-43a8-bc48-4c1b49cfdc85', null, 470, 34, 0, 'PNf8AgAAQBAJ', 'Атлетизм', 'http://books.google.com/books/content?id=PNf8AgAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Эта книга поведет читателя в волшебную страну - атлетизм, где слабый может стать сильным, больной - здоровым, где юноши, стесняющиеся своей хилости и неразвитости, превращаются в красивых и могучих парней. Читатель найдет в книге комплексы атлетической гимнастики, узнает об выдающихся атлетах прошлого и настоящего.', 708, 708);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('9d05496a-399b-47e4-926d-66f5512f749b', '2017-03-15 00:00:00.000000', 485, 0, 0, 'eXFbDgAAQBAJ', 'Интеграционное право. Учебник', 'http://books.google.com/books/content?id=eXFbDgAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Первый в России учебник, в котором комплексно рассмотрен и обобщен мировой опыт правового регулирования интеграционных процессов, проанализированы закономерности становления и развития интеграционного права на региональном, межрегиональном и глобальном уровнях, охарактеризованы важнейшие достижения правового регулирования международной интеграции в экономической, политической и других сферах общественной жизни, исследован правовой статус ведущих интеграционных организаций, функционирующих в разных частях земного шара, правовые основы участия в них России. В Особенной части проанализированы ведущие интеграционные правопорядки, сложившиеся на глобальной арене и в отдельных регионах мира, в том числе на постсоветском пространстве: право Всемирной торговой организации, право Европейского союза, право Североамериканской ассоциации свободной торговли и др. Особое внимание уделяется практическим аспектам участия России в интеграционных процессах, включая правовые основы интеграции на территории бывшего СССР, правовое регулирование создания и функционирования Евразийского экономического союза. Законодательство приведено по состоянию на сентябрь 2016 г. Для бакалавров, магистрантов и аспирантов юридических вузов и факультетов, студентов факультетов международных отношений, работников органов государственной власти, внешнеэкономических и внешнеполитических организаций, преподавателей, а также для всех заинтересованных читателей.', 420, 420);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('362e9a65-8b22-4224-a66b-c0caf2679bca', '2020-05-08 00:00:00.000000', 358, 24, 0, 'Ns6hDwAAQBAJ', 'Идеализм и идеалисты', 'http://books.google.com/books/content?id=Ns6hDwAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', '5.836.Идеалист – это тот, кто не может смириться, что истина – это хлеб с солью.Мы все идеалисты и все от этого серьезно страдаем. Более 500 ценных мыслей о том, что есть идеализм, какие проблемы он вызывает и как с ним бороться или использовать для пользы дела.Разномыслие – путеводитель по лабиринтам мысли. В этой книге каждый найдёт нечто ценное и полезное для себя.Смысл книг Разномыслия – это поиск истины. Знание – сила.', 385, 346);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('51bc7046-2830-495e-89ab-d9d52b21da8f', null, 305, 44, 0, 'pfjvAgAAQBAJ', 'Аэродинамика и летные характеристики вертолетов', 'http://books.google.com/books/content?id=pfjvAgAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', null, 456, 456);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('6fa7eab9-e484-446d-bf9b-eea82abd2543', null, 62, 45, 0, 'L3b-AgAAQBAJ', 'Речевой этикет и культура общения', 'http://books.google.com/books/content?id=L3b-AgAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Книга посвящена правилам, регулирующим речевое поведение носителей языка. Читатель познакомится с многочисленными способами приветствовать и прощаться, благодарить и извиняться, поздравлять, обращаться к знакомым и незнакомым людям и т. п. Автор предлагает задуматься над тем, как вежливо отказаться от приглашения, кому и что можно советовать, чем просьба отличается от приказа, кто имеет право запрещать и требовать и многим другим. Для широкого круга читателей.', 456, 456);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('9365fcaa-c974-4ae3-b2dd-eb6c63f0a39b', null, 350, 36, 1, '1Uv6AgAAQBAJ', 'Дикорастущие полезные растения СССР', 'http://books.google.com/books/content?id=1Uv6AgAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Справочник- определитель содержит характеристики более 500 видов пищевых, лекарственных, красильных, смолоносных, камеденосных и др. растений.Более 220 из них изображены на цветных таблицах и около 100 - на черно-белых рисунках.Для каждого вида приводятся сведения о морфологии, сроках цветения и плодоношения, распространении в СССР, характере местообитания, химическом составе, способах применения.Книга иллюстрирована картами и цветными таблицами и расчитана на широкий круг читателей.', 476, 476);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('b127cf00-2076-4ef8-b42d-a7f50a490f22', null, 778, 30, 0, 'q-D_AgAAQBAJ', 'Распространение волн в турбулентной атмосфере', 'http://books.google.com/books/content?id=q-D_AgAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Книга посвящена изложению теории распространения волн в среде со случайными неоднородностями показателя преломления, а также анализу экспериментальных данных по распространению радиоволн, света и звука в атмосфере. Она предназначена для радиофизиков, занимающихся проблемой распространения радиоволн и света, и для геофизиков, занимающихся физикой атмосферы. Книга может оказаться полезной и для физиков-теоретиков, интересующихся приложениями методов квантовой теории поля к задачам классической физики.', 456, 456);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('8d750bad-b5dc-4b50-b7ca-1fe9bd160f80', null, 225, 44, 1, '7fD9AgAAQBAJ', 'Производство коньяка и кальвадоса в Молдавии', 'http://books.google.com/books/content?id=7fD9AgAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', null, 720, 720);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('ecc1b7fc-e657-4403-ba24-c353110dbcb8', '2022-06-02 00:00:00.000000', 620, 25, 1, 'kttwBgAAQBAJ', 'Программирование. Принципы и практика использования C++', 'http://books.google.com/books/content?id=kttwBgAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Вводный курс программирования, написанный автором языка C++.Подготовка к созданию реальных программ.Автор книги полагает, что читатели в конце концов начнут писать нетривиальные программы либо в качестве профессиональных разработчиков программного обеспечения, либо в качестве программистов, работающих в разных областях науки и техники.Упор на основные концепции и методы.Основные концепции и методы программирования в книге излагаются глубже, чем это принято в традиционных вводных курсах. Это позволит читателям разрабатывать полезные, правильные, понятные и эффективные программы.Программирование на современном языке C++.Книга представляет собой введение в программирование вообще, включая объектно-ориентированное и обобщенное программирование. Она также представляет собой прекрасное введение в язык C++, один из наиболее популярных языков программирования в современном мире. В книге описаны современные методы программирования на C++, включая стандартную библиотеку, позволяющую упростить программирование.Для начинающих программистов и всех, кто хочет научиться программировать. Книга предназначена в основном для людей, никогда ранее не программировавших. Она опробована более чем тысячей студентов университета. Однако опытные программисты и студенты, уже изучившие основы программирования, также найдут в книге много полезной информации, которая позволит им перейти на более высокий уровень мастерства.Широкий охват тем.Первая половина книги охватывает широкий спектр основных понятий, методов проектирования и программирования, свойств языка C++ и его библиотек. Это позволит читателям писать программы, выполняющие ввод и вывод данных, вычисления и построение простых графических изображений. Во второй половине рассматриваются более специализированные темы, такие как обработка текста и тестирование. В ней содержится много справочного материала. Исходные коды и другие приложения читатели могут найти на веб-сайте автора.', 1500, 1350);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('f0255875-011d-4c8d-8eb7-e551d21043a7', '2014-12-09 00:00:00.000000', 873, 0, 1, 'SurIBQAAQBAJ', 'Европейское публичное право. Монография', 'http://books.google.com/books/content?id=SurIBQAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'В книге раскрываются основные вопросы публичного права Европейского cоюза в свете изучения истории европейских cообществ, структуры Европейского cоюза, основных положений институционного права. При изложении материала учтены современные тенденции развития права Европейского cоюза, анализируются изменения, которые произошли после вступления с 1 декабря 2009 г. в силу Лиссабонского договора о внесении изменений в Договор о Европейском союзе и Договор об учреждении Европейского сообщества от 13 декабря 2007 г. Адресуется студентам, изучающим учебную дисциплину «Право Европейского союза», аспирантам, преподавателям юридических учебных заведений, специалистам в области европейского права, всем заинтересованным читателям.', 210, 210);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('705f0c70-6505-44fc-a9d2-e968ae65d9ef', '2021-05-18 00:00:00.000000', 64, 3, 1, 'K_0-EAAAQBAJ', 'Работа и личность. Трудоголизм, перфекционизм, лень (PDF)', 'http://books.google.com/books/content?id=K_0-EAAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Новая книга мастера психологии профессора Е. П. Ильина охватывает широкий круг актуальных вопросов, среди которых отношение современных людей к труду, психологические последствия потери работы, особенности личности и выбор вида деятельности, профессиональная деформация личности, психология профессиональных аддикций, трудоголизм, лень и склонность к лени, а также многие другие. В конце пособия приведены полезные методики и подробный библиографический список. Издание предназначено для психологов, педагогов, социологов, представителей смежных специальностей, а также студентов вузовских факультетов соответствующих профилей.', 899, 899);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('79448bb0-6cdc-4969-953f-b744cf09cc57', '2011-03-16 00:00:00.000000', 64, 3, 0, 'BKXLUrMv3JoC', 'Работа и личность. Трудоголизм, перфекционизм, лень', 'http://books.google.com/books/content?id=BKXLUrMv3JoC&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Издание содержит: работа и труд в жизни человека; особенности личности и выбор вида деятельности и профессии; личность, отношение к работе и ее эффективность и др.', 86, 86);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('d9213ff6-77f7-4167-a683-1511e3fcdd2d', '2017-09-05 00:00:00.000000', 27, 4, 0, 'epkQDAAAQBAJ', 'Татары Перми: история и культура', 'http://books.google.com/books/content?id=epkQDAAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'В книге в научно-популярной форме рассказывается об истории татар в Перми и их культуре. Помимо общих сведений об этногенезе, материальной и духовной культуре читателю представлен очерк по истории татар в Перми, о современном общественном национально-культурном движении, участии татар в общественной и культурной жизни города Перми.Издание адресовано специалистам в области истории и этнографии, педагогам, представителям органов власти, широкому кругу читателей.', 112, 101);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('620f1c9b-f258-486b-8bff-29df959e8ed0', '2011-12-02 00:00:00.000000', 775, 23, 0, 'lTrDCzIeQz8C', 'Государь (сборник)', 'http://books.google.com/books/content?id=lTrDCzIeQz8C&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', '«Цель оправдывает средства» – таков закон политики. «Цель оправдывает средства» – таков, по Макиавелли, девиз всякой сильной власти. Потому что «сильный государь» – залог процветания государства. С этим можно не соглашаться, однако сначала необходимо ознакомиться с «Государем» – не подвластным времени, умным и циничным «учебником правителей».', 348, 313);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('e4b3b3c2-676d-4ea0-9059-897b90b45062', null, 257, 27, 0, 'msX8AgAAQBAJ', 'Сопоставительная грамматика русского и казахского языков', 'http://books.google.com/books/content?id=msX8AgAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', '"Сопоставительная грамматика русского и казахского языков (Морфология)" освещает особенности грамматического строя русского языка в сопоставлении с грамматическим строем казахского языка. В ней рассматриваются основные особенности в словоизменении, словообразовании и употреблении частей речи, исследуются общие обоим языкам факты.', 912, 912);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('b5146218-c5d3-45e0-8a7e-9ed103df4967', null, 238, 25, 0, 'ViLArLCwL-kC', 'Blender: 3D-моделирование и анимация. Руководство для начинающих (+ дистрибутив)', 'http://books.google.com/books/content?id=ViLArLCwL-kC&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Руководство для начинающих по трехмерному моделированию и анимации в свободно-распространяемой программе Blender. На простых примерах показаны типичные приемы работы в описываемой области. Рассмотрены варианты создания трехмерных моделей различными способами, в том числе новейшими инструментами скульптурного моделирования, работа с NURBS, поверхностями, текстурами и материалами, использование шейдерных эффектов, возможности имитации физических явлений, а также все виды анимации, включая скелетную. Прилагаемый CD содержит файлы рассмотренных в книге примеров, а также свободно-распространяемое ПО: установочные файлы Blender для Windows и Linux, короткометражный фильм Big Buck Bunny и набор текстур. Файлы для книги можно скачать по ссылке ftp://ftp.bhv.ru/9785977503938.zip', 159, 143);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('dc053ece-4332-40f8-9a2a-4c05ee78c923', '2019-12-12 00:00:00.000000', 114, 49, 0, '4HkEAwAAQBAJ', 'Русский язык и культура речи. Учебник', 'http://books.google.com/books/content?id=4HkEAwAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'В учебнике рассмотрены теоретические основы культуры речи, специфика и виды речевой деятельности, дана характеристика основных механизмов речи. Имеющиеся в учебнике задания для самостоятельной работы активизируют познавательную деятельность, контролируют ее результаты, формируют коммуникативно-речевые умения. Учебник написан в соответствии с государственным образовательным стандартом высшего образования Российской Федерации. Для студентов, аспирантов, преподавателей вузов.', 300, 300);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('01d35c1d-934d-4ff5-b532-2ac49933f1c3', null, 715, 25, 0, 'lPZXqWPNOsQC', 'PHP 5/6 и MySQL 6. Разработка Web-приложений, 2 издание', 'http://books.google.com/books/content?id=lPZXqWPNOsQC&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'На практических примерах описана разработка Web-приложений на языке PHP версий 5 и 6. Большая часть кода примеров совместима с обеими версиями PHP, но особое внимание уделено новым функциям PHP 6. Даны начала программирования на PHP: установка и настройка PHP и MySQL, выбор редактора PHP-кода, основы синтаксиса и самые полезные функции PHP. Рассмотрено создание собственного движка сайта и ряда дополнительных модулей - фотогалереи, RSS-граббера, модуля для работы с MP3, модуля продажи недвижимости, гостевой книги, а также применение мощного шаблонизатора Smarty и создание простейшего собственного шаблонизатора. В качестве хранилища данных использованы два сервера - самая современная версия MySQL 6 и "суперлегкий" сервер баз данных SQLite. Показано, как с помощью технологии Ajax добиться обновления данных на странице без ее перезагрузки. Во втором издании описаны новые инструменты для создания сложных проектов PEAR, CVS и PHPUnit. Прилагаемый компакт-диск содержит дополнительные главы, все листинги из книги, а также необходимое программное обеспечение. Файлы для книги можно скачать по ссылке ftp://ftp.bhv.ru/9785977505819.zip', 239, 215);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('e26be5d6-85f7-4229-b4cd-61b8ca29ef04', '2016-06-21 00:00:00.000000', 684, 23, 1, 'RjNGDAAAQBAJ', 'Война и Мир. 4 тома в одной книге.', 'http://books.google.com/books/content?id=RjNGDAAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Предлагаем вниманию читателей одно из наиболее грандиозных литературных явлений XIX века роман Льва Толстого "Война и мир". Пришлось приложить немало усилий, чтобы это художественное полотно, занимающее 4 объёмистых бумажных тома, уместилось на виртуальных страницах одной электронной книги в формате ePub, ничего не потеряв ни в художественной, ни в познавательной части. Более того, теперь этот роман действительно способен войти в каждый дом, в каждый школьный ранец, в каждый студенческий рюкзак. Чем был и остается Лев Толстой для каждого русского человека? "Л. Н. Толстой был самым ярким представителем русской интеллигенции, и не только в том смысле, что он был гениальный художник, ведущий деятель русской литературы, но и в том, что он чутко сопереживал все вопросы, которыми болела интеллигенция его времени. И интеллигенция, порой расходившаяся с ним в практических и даже моральных выводах, никогда не относилась равнодушно к его личности, к его мысли и деятельности"(В.Г.Короленко).', 99, 99);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('b85ba93f-cc51-48b1-860a-7794d0274c0e', '2022-04-29 00:00:00.000000', 873, 25, 0, 'zcZwDwAAQBAJ', 'HTML и CSS. Разработка и дизайн веб-сайтов', 'http://books.google.com/books/content?id=zcZwDwAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Эта книга – самый простой и интересный способ изучить HTML и CSS. Независимо от стоящей перед вами задачи: спроектировать и разработать веб-сайт с нуля или получить больше контроля над уже существующим сайтом, эта книга поможет вам создать привлекательный, дружелюбный к пользователю веб-контент. Простой визуальный способ подачи информации с понятными примерами и небольшим фрагментом кода знакомит с новой темой на каждой странице. Вы найдете практические советы о том, как организовать и спроектировать страницы вашего сайта, и после прочтения книги сможете разработать свой веб-сайт профессионального вида и удобный в использовании.', 848, 763);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('90343393-9626-4507-ab84-ce520b011b2e', '2020-03-13 00:00:00.000000', 136, 23, 0, 'tChgDwAAQBAJ', 'Академия Меча и Чар 2 (полная версия)', 'http://books.google.com/books/content?id=tChgDwAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Продолжение "Академии Меча и Чар 1". Это полная версия второй части. Книга построена так, что ее можно читать, даже если вы не читали первую часть. Хотите продолжения приключений в Волшебной Школе с уроками магии, интригами, заклятьями, предательствами, настоящей дружбой и даже любовью? Добро пожаловать в Академию Меча и Чар! Автор выкладывает главы на Wattpad. Псевдоним - @KTigris СЮЖЕТ: Сентябрьское дождливое утро… Невыспавшаяся толпа учеников мнется у школьных ворот, проклиная дождь и греясь рассказами о вчерашнем лете… Среди них есть несколько странных новичков, которых никто раньше не видел не только в этом тихом городке, но и в мире смертных. Древнейшие магические силы легко манипулируют подростками. Вот один заключает сделку с демоном, второй ворует удачу у самой судьбы… А где-то на краю Земли неведомый человечеству Мир Светлых Магиров терпит поражение в битве с темными силами и демонами. Светлый Король решает пойти на отчаянный шаг – посылает своего гонца-магира в мир смертных, чтобы выбрать из их числа самых одаренных подростков и вручить им эльфийские приглашения в город Светлых Магиров, а именно – в Академию Меча и Чар. Но приглашения в волшебный мир попадают совершенно не к тем подросткам, которым они предназначались... Повествование ведётся от лица ПЯТИ ГЛАВНЫХ ГЕРОЕВ (и одного очень хитрого рыжего кота), которые сами того не ведая, получили эльфийские приглашения. В результате чего нелегкие судьбы двух девчонок и троих парней тесно переплелись. Теперь им предстоит пройти испытания, найти Наставников и поступить на первый курс Академии Меча и Чар. ГЛАВНЫЕ ГЕРОИ: 1. НИКИТА КОЛТИН (или НИК). Загорелый темноволосый оборванец "без рода и племени". Он целыми днями торчит в хижине у моря, ждёт, пока вернётся из последнего плавания его отец-рыбак и ищет приключения на свою кудрявую голову. Из последней ночной переделки его неожиданно спасает таинственный незнакомец в черном кожаном плаще, повелевший звать себя Мастер. 2. АЛЕКСЕЙ ЗЛАТОНОВСКИЙ (или АЛЕКС, ЛЕКС), "золотой мальчик" и единственный наследник самого богатого и влиятельного бизнесмена в городе. Но так ли роскошна и беззаботна жизнь Лекса? Почему же он тогда так худ, бледен и жесток? Так же, как и его расчётливый отец, парень ведёт "свой бизнес" и "заключает сделки". Последняя из них оказалась не совсем выгодной. Белокурый школьный новичок продал Лексу дополнительную жизнь и даже ради него повернул время вспять, попросив за услугу не больше, не меньше, а саму душу Алексея. 3. АНДРЕА ЛЕПРЕНТО, гениальный итальянский семнадцатилетний скрипач. Парень был так счастлив, отмечая в роскошном ресторане помолвку со своей возлюбленной, пока там не появился Ник, Алекс и Марк, а хуже всего – бутылка дорогого красного вина, подкинутая неизвестной старухой. К счастью, вино не выпили, но бутылку опрокинули, и смертельный яд попал на прекрасные белые розы, чьими острыми шипами укололась подруга Андеа. И потому влюбленный скрипач отправился искать противоядие в мир Магиров. 4. КИРА. Новенькая девчонка в обычной земной школе, где учатся Лекс и Ник. На волшебном корабле девушка попадает в магазин гоблинши, где покупает магическое предметы, делающие ее неуязвимой. Она переодевается в парня, чтобы попасть на факультет легионеров, куда, естественно, девчонок не берут. А ей срочно нужно научиться сражаться, чтобы отомстить за свою семью и забрать то, что по праву принадлежит ей. 5. МАРГАРИТА (или МАРГО). Неприглядная одинокая девочка в страшных очках с толстыми линзами. В обычной школе ее все дразнят и издеваются над ней. Но попадая на волшебный корабль, Маргарита случайно разбивает свои злополучные очки, и ее внешность тут же меняется. Очки оказались заколдованы, именно они превращали ее в страшилу, защищая тем самым от ненужного внимания и пряча ее истинную красоту. Марго – совсем не та, кем она представляла себя долгие шестнадцать лет. Какие ещё тайны скрывает ее прошлое? 6. МАРК. Худощавый подросток с белыми волосами и черными, как уголь, глазами. Это Старший Демон, разбуженный из недр Земли испытаниями мощного ядерного оружия. Сквозь портал он отправился в мир людей "на каникулы" и теперь развлекает себя, как может, прикалываясь над человеческими судьбами, словно над игрушками. В обычной школе Марк за бесценок "покупает" душу у Алексея Златоновского, всячески помыкая им, делая из парня своего беспрекословного слугу. Но долго ли Лекс будет терпеть все "шуточки" и поручения Марка? В Академии Магиров смертным предлагается 5 ФАКУЛЬТЕТОВ на выбор: 1. ЛЕГИОНЕРЫ (Самые сильные и выносливые из магиров, в совершенстве владеют рукопашным боем и мечами, выкованными гномами из заколдованного металла, который действует на темных. Передвигаются на боевых драконах и несут вечную службу у Огненных врат – портала в потусторонний мир демонов) 2. МАГИ (Древнейший чистокровный род магиров, владеющий секретами магии и умеющий повелевать духами – джиннами, феями, призраками и прочими волшебным существами, которые находятся у них на службе. Ненавидят Алхимиков.) 3. АЛХИМИКИ (Семья ученых, которая откололась от рода магов, желая постичь секреты волшебства и найти его формулу. Ненавидят Магов.) 4. МАСТЕРА (Самая многочисленная и дружелюбная семья магиров. Это трудолюбивые работяги, их талантливыми руками создано все в Люксарде – от роскошного дворца до ароматного яблочного пирога) 5. ПРАВИТЕЛИ и СУДЬИ (Самая малочисленная, но могущественная семья магиров. Именно ее представители занимают все управляющие должности в Люксарде. Во главе находится Светлый Король и его наследник Принц) В книге созданы оригинальные СВЕТЛЫЙ и ТЕМНЫЙ волшебные миры: демоны, джинны, эльфы, кентавры, русалки, драконы, вампиры, варлоки, тролли, гоблины, гномы и прочие мифические существа в достаточном количестве. ВНИМАНИЕ! Детальные сцены сражений, напряжённый закрученный сюжет с неожиданной развязкой и интригующим любовным четырехугольником. ОГРАНИЧЕНИЕ: 16+. Это не продолжение нашумевшей «Академии Магиров», это ее новая улучшенная версия со старыми героями, но совершенно иным, более острым и захватывающим сюжетом. *** *** *** ВНИМАНИЕ! Читатели теперь могут влиять на продолжение книги! Просто проголосуйте за понравившегося героя внизу и он обязательно будет в продолжении. Например: Ник+ Лекс+ Кира+ Демон+', 135, 135);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('0b68c020-eabc-4d0f-8168-77cc532523dc', '2014-11-27 00:00:00.000000', 169, 14, 0, 'mUmaBQAAQBAJ', 'Классика, после и рядом: Социологические очерки о литературе и культуре', 'http://books.google.com/books/content?id=mUmaBQAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Смысловой центр книги известного социолога культуры Бориса Дубина — идея классики, роль ее в становлении литературы как одного из важных институтов современного общества. Рассматриваются как механизмы поддержания авторитета классики в литературоведении, критике, обучении, книгоиздании, присуждении премий и др., так и борьба с ней, в том числе через выдвижение авангарда и формирование массовой словесности. Вошедшие в книгу статьи показывают трансформации идеи классики в прошлом и в наши дни, обсуждают подходы к их профессиональному анализу методами социологии культуры.', 176, 158);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('12494dd3-74a6-43cc-8a63-bc204a3d1c12', '2016-04-16 00:00:00.000000', 238, 37, 0, 'zWP9CwAAQBAJ', 'Возвращение домой. Когда рождается вера', 'http://books.google.com/books/content?id=zWP9CwAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', '«Возвращение домой» – настоящее откровение для приходящих в Церковь. Отец Андрей просто и понятно объясняет, для чего вообще нужна Церковь и что там происходит с верующими.Главные особенности книги – живость и простота. Ее достоинство – подлинное, современное, живое, что немаловажно, совершенно нескучное объяснение основ православной веры.Возвращение домой – это путь от себя заблудившегося к себе, обретшему духовную Родину. Это путь от корыта со свиной пищей, помянутого в притче о блудном сыне, к пению и пиру в доме отца, который сказал, что сын его «мертв был и ожил; пропадал и нашелся».', 261, 235);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('d28696c7-3e15-42b1-a968-c64708adc2f0', '2022-04-29 00:00:00.000000', 833, 38, 0, 'liI7EAAAQBAJ', 'ЕГЭ-2022. Английский язык. Разделы «Письмо» и «Говорение»', 'http://books.google.com/books/content?id=liI7EAAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Издание предназначено для подготовки учащихся старших классов к ЕГЭ по английскому языку. Материалы пособия помогут приобрести практические навыки выполнения заданий из разделов «Письмо» и «Говорение» и значительно увеличить лексический запас.Книга содержит:комплекс упражнений, сгруппированных в 14 тематических уроков;32 тренировочных варианта для подготовки к разделу «Письмо»;56 тренировочных вариантов для подготовки к устной части;опорные фразы для выполнения заданий разделов «Письмо» и «Говорение»;шаблоны устного ответа на английском и русском языках.К пособию прилагается обширный банк цветных иллюстраций в электронном виде для выполнения заданий устной части.Книга будет полезна учителям английского языка, так как даёт возможность эффективно организовать подготовку учащихся к ЕГЭ непосредственно на уроках, в процессе изучения учебного материала.', 311, 280);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('56c39c94-d1d9-44ec-a90d-fc733fb8e64f', '2015-10-27 00:00:00.000000', 329, 0, 0, 'KpzRCgAAQBAJ', 'Криминалистические аспекты розыскной и поисковой деятельности следователя. Монография', 'http://books.google.com/books/content?id=KpzRCgAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Монография посвящена криминалистическим проблемам розыскной и поисковой деятельности следователя при расследовании преступлений. Дано понятие розыскной и поисковой деятельности следователя. В системе собирания доказательств значительное внимание уделено оперативно-розыскной деятельности, показано взаимодействие следственных и оперативно-розыскных органов по поиску криминалистически значимой информации. Законодательство приведено по состоянию на февраль 2015 г. Монография рассчитана на студентов юридических факультетов высших учебных заведений, слушателей и курсантов образовательных учреждений МВД России и других правоохранительных органов, а также сотрудников органов внутренних дел и суда.', 99, 99);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('c9fde6b5-02ad-4d98-b523-eca89a2b31b4', null, 247, 15, 0, 'UrGwVXXk7coC', 'Книга предсказаний. Спроси и получи ответ. Открывай левой рукой', 'http://books.google.com/books/content?id=UrGwVXXk7coC&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Какие события ждут нас в будущем? На что в настоящем нужно обратить особое внимание? Открывайте книгу наугад: вы интуитивно выберете мудрое изречение, и оно укажет, в каком направлении вам необходимо действовать, чтобы достичь душевной гармонии и добиться успеха в жизни.', 45, 45);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('5864597b-3d0c-44a2-8ab7-c39442ac1c04', '2022-04-20 00:00:00.000000', 639, 49, 0, '3NlICAAAQBAJ', 'Чешская грамматика в таблицах и схемах', 'http://books.google.com/books/content?id=3NlICAAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Данное справочное пособие предназначено для всех изучающих чешский язык, как в университетах, так и на различных курсах и самостоятельно. Оно призвано помочь учащимся систематизировать и структурировать знания по грамматике. Основные грамматические правила представлены наглядно – в виде таблиц и кратких пояснений на русском языке, что делает возможным использование пособия на разных этапах обучения. Кроме грамматики освещаются и некоторые лексические трудности, с которыми часто приходится сталкиваться учащимся.', 360, 324);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('3e806a76-e028-46ce-8fed-0c23070c9746', '2020-09-27 00:00:00.000000', 970, 9, 0, 'l3OKCgAAQBAJ', 'Афоризмы житейской мудрости (сборник)', 'http://books.google.com/books/content?id=l3OKCgAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Артур Шопенгауэр – один из самых известных мыслителей иррационализма, мизантроп. Произведенный Шопенгауэром метафизический анализ воли, его взгляды на человеческую мотивацию и желания, афористичный стиль письма оказали влияние на многих известных мыслителей, включая Ницше, Вагнера, Эйнштейна, Фрейда, Юнга. Основной философский труд Шопенгауэра – «Мир как воля и представление» (1818), комментированием и популяризацией которого Шопенгауэр занимался до самой смерти.В «Афоризмах» философ впервые попытался противопоставить собственной «пессимистически-идеалистической» концепции концепцию иную – эпикурейскую.В формате a4.pdf сохранен издательский макет.', 25, 22);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('3b4f4b93-7b8a-4c2a-99c7-b9f1983dfca8', null, 416, 37, 0, 'M9f9AgAAQBAJ', 'Дао и даосизм в Китае', 'http://books.google.com/books/content?id=M9f9AgAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', null, 1368, 1368);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('73b8e61e-29bb-416f-93fe-23dc49199458', '2017-09-05 00:00:00.000000', 278, 27, 0, '6DOdCgAAQBAJ', 'Язык и мысль. Современная когнитивная лингвистика', 'http://books.google.com/books/content?id=6DOdCgAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Международный коллектив авторов сборника, впервые собравшийся в таком составе, представляет панораму современной когнитивной лингвистики. Когнитивная лингвистика понимается максимально широко – как исследование любого аспекта языка в связи с познавательными процессами человека. Сборник состоит из трех разделов. В статьях первого раздела обсуждается общая архитектура языка в когнитивной перспективе. Два последующих раздела посвящены двум основным режимам существования языка – язык как хранилище и язык как коммуникативный процесс. Книга будет полезна не только специалистам – лингвистам, психологам, исследователям в области искусственного интеллекта, – но и широкому кругу читателей, интересующихся строением языка, его эволюцией, процессами познания, мышления и речевой коммуникации.', 437, 393);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('3633f868-c8f9-447d-bdcc-6f79b6473ff3', '2017-09-05 00:00:00.000000', 152, 27, 0, 'WExHAgAAQBAJ', 'Путь в язык. Одноязычие и двуязычие', 'http://books.google.com/books/content?id=WExHAgAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Сборник статей посвящен освоению русского языка как первого и как второго. Затрагиваются и другие разнообразные проблемы – одновременного и последовательного двуязычия, утраты одного из языков, языковой интерференции и пр. Сборник включает три раздела. В первом рассматриваются вопросы, связанные с теоретическими проблемами освоения языка; во втором – освоение русского языка как родного (первого), в третьем – разные аспекты двуязычия. Авторы сборника – российские ученые, а также исследователи из Германии, Финляндии, Швеции, Австрии. Книга адресована в основном лингвистам, но представляет интерес и для психологов, педагогов, логопедов – для всех, кто интересуется тем, каким образом человек приобретает язык.', 250, 225);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('c525e852-713b-438c-9f7e-4e292b380a08', '2022-03-31 00:00:00.000000', 418, 49, 0, 'N9TXCQAAQBAJ', 'Нидерландская грамматика в таблицах и схемах', 'http://books.google.com/books/content?id=N9TXCQAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Данное справочное пособие предназначено для широкого круга читателей. Оно призвано помочь изучающим нидерландский язык систематизировать и структурировать знания по грамматике. Основные грамматические правила представлены наглядно – в виде таблиц, схем и кратких пояснений. Все объяснения даются на русском языке, что делает возможным использование пособия на разных этапах изучения языка.Кроме грамматики, в книге освещаются и некоторые лексические трудности, с которыми часто приходится сталкиваться учащимся.', 388, 349);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('226b9cf1-249c-4366-bbbc-11c46d5a6474', '2017-03-15 00:00:00.000000', 688, 30, 1, 'qLJbDgAAQBAJ', 'Физическая культура: методики практического обучения. Учебник', 'http://books.google.com/books/content?id=qLJbDgAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Рассматриваются методики практического обучения студентов физической культуре. Особое внимание уделено обоснованию требований к организации занятий по физической культуре, спорту и туризму, последовательности обучения физическим упражнениям, тренировкам в секциях и командах, проведению соревнований, путешествий и экскурсий по родному краю. Соответствует Федеральному государственному образовательному стандарту высшего профессионального образования третьего поколения. Для студентов бакалавриата учебных учреждений, будущих специалистов в области физической культуры и спорта, педагогов, тренеров, специалистов, организаторов учебно-тренировочных занятий по физической культуре, спорту и туризму.', 299, 299);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('014d13d4-7c33-4b4c-8b24-379503350837', '2020-07-13 00:00:00.000000', 716, 19, 0, 'V6v_DwAAQBAJ', 'Теория и методика социальной работы', 'http://books.google.com/books/content?id=V6v_DwAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Учебное пособие посвящено актуальным вопросам теории и практики социальной работы и адресовано студентам среднего профессионального образования, обучающимся по специальностям: 39.01.01 «Социальный работник» (квалифицированный служащий); 39.02.02 «Социальный работник» (специалист среднего звена), а также всех, кто интересуется проблемами социальной работы. В пособии отражены общие вопросы теории социальной работы, основные подходы, применяющиеся в социальной работе, технологии и методики социальной работы с различными категориями населения. Учебное пособие включает теоретический материал, практикум, включающий в себя контрольные вопросы, практические задания, контрольные тесты, тематику для самостоятельного изучения и рефератов, список использованных источников и литературы, словарь терминов.', 97, 87);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('f89eff03-908b-4a57-8837-53a6175bbc88', '2020-01-29 00:00:00.000000', 775, 0, 1, 'Kh0iCwAAQBAJ', 'Право интеллектуальной собственности. 2-е издание. Учебник', 'http://books.google.com/books/content?id=Kh0iCwAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Российская государственная академия интеллектуальной собственности представляет второе издание учебника «Право интеллектуальной собственности», подготовленное коллективом академии. Во втором издании учтены пожелания специалистов и наших читателей. В учебнике доступным языком представлен большой объем сведений, относящихся к области интеллектуальной собственности, что предоставляет возможность углубленного изучения норм законодательства, освоения знаний об интеллектуальной собственности. Законодательство приводится по состоянию на сентябрь 2015 г. Учебник будет полезен как студентам, получающим специальность «интеллектуальная собственность», «инженер-патентовед», так и студентам и аспирантам юридических, экономических, инженерно-технических специальностей, он также будет способствовать профессиональному исполнению служебных обязанностей специалистов, государственных служащих в сфере интеллектуальной собственности.', 730, 730);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('60c82da9-d328-4f8a-b215-53b96a26b8dd', '2022-05-13 00:00:00.000000', 642, 3, 0, 'Qi4kEAAAQBAJ', 'Исследования социально-экономических и политических процессов. Практикум 2-е изд., испр. и доп. Учебное пособие для академического бакалавриата', 'http://books.google.com/books/content?id=Qi4kEAAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'В учебном пособии рассмотрены различные вопросы, связанные с исследованием социально-экономических и политических процессов. Дается представление о том, в каких ситуациях целесообразно прибегать к тому или иному способу опроса или наблюдения. Учебный материал четко систематизирован, включает задания четырех различных типов, направленные на закрепление практических навыков и формирование целостного знания по дисциплине.', 986, 887);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('92e94b9d-f250-4a95-8b15-d1d43ad5f490', '2022-05-13 00:00:00.000000', 88, 44, 1, '5BdfDwAAQBAJ', 'Информационные технологии в государственном и муниципальном управлении 2-е изд., испр. и доп. Учебное пособие для бакалавриата и магистратуры', 'http://books.google.com/books/content?id=5BdfDwAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Проникновение информационных технологий (ИТ) во все сферы общественной жизни меняет общество, которое переходит на качественно новую ступень развития. Государственное управление является той уникальной сферой деятельности, в которой задачи применения и развития ИТ закреплены законодательно на уровне государственных программ и федеральных законов. Цель учебного пособия – сформировать у студентов представление о концептуальной архитектуре электронного правительства и функциональном назначении отдельных его компонентов, познакомить с современными информационными технологиями, применяемыми для организации информационных сервисов и обеспечения эффективного межведомственного взаимодействия. Книга состоит из шести разделов, каждый из которых завершают вопросы для самоконтроля и практические задания для закрепления материала. Пособие снабжено глоссарием, содержащим используемые в работе термины и определения.', 511, 460);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('c35d7550-8b72-405c-8b0c-31e87078204d', '2021-08-30 00:00:00.000000', 873, 0, 0, 'pwVAEAAAQBAJ', 'Научно-практический комментарий к Федеральному закону «О биомедицинских клеточных продуктах» (постатейный)', 'http://books.google.com/books/content?id=pwVAEAAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'В книге дан постатейный комментарий к Федеральному закону от 23 июня 2016 г. No 180-ФЗ (ред. от 3 августа 2018 г.) «О биомедицинских клеточных продуктах». В его подготовке участвовали специалисты ведущих научных школ России: НШ-2495.2020.7 «Разработка биотехнологических принципов прогностики, диагностики и коррекции социально-значимых заболеваний на основе молекулярно-генетических механизмов реакций клеточных систем на раздражители в норме и при патологии», Балтийского федерального университета имени И. Канта, Института права, экономики и управления Псковского государственного университета. При подготовке издания использованы нормативные правовые акты по состоянию на март 2020 г. Комментарий предназначен для специалистов, работающих в сфере обращения биомедицинских клеточных продуктов. Представляет интерес для преподавателей, аспирантов и студентов вузов, работников правоохранительных органов, широкого круга юристов-практиков.', 560, 560);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('4fbef4ff-16fc-4945-82bc-53bca2d3c538', '2022-05-13 00:00:00.000000', 372, 3, 0, 'uCwkEAAAQBAJ', 'Неуспевающие дети: нейропсихологическая диагностика младших школьников 3-е изд., испр. и доп. Учебное пособие для бакалавриата и магистратуры', 'http://books.google.com/books/content?id=uCwkEAAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Учебное пособие обобщает результаты проведенных авторами экспериментальных психологических обследований. В нем представлены основные принципы нейропсихологического подхода, условия психологического развития, диагностика познавательных функций и психического развития младших школьников. Книга сопровождается списком книг, обращение к которым углубит знания о нейропсихологическом подходе, а также навыки и умения эффективного применения нейропсихологических методик при диагностической и психолого-коррекционной работе с младшими школьниками.', 498, 448);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('77e07cc2-d281-4019-afe2-cbec23ca9a83', '2018-01-26 00:00:00.000000', 391, 23, 0, 'uRNJDwAAQBAJ', 'Кочубей', 'http://books.google.com/books/content?id=uRNJDwAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', '«Кочубей» - историческая повесть российского писателя, поэта и переводчика Егора Васильевича Аладьина (1796-1860). *** Василий Леонтьевич Кочубей дает роскошный пир. Всем довольны гости, и, кажется, нет ни единой души на свете, более счастливой, чем хозяин… Другими выдающимися произведениями писателя являются «Мои досуги», «Кум Иван», «Сочинения и переводы, в прозе», «Повести» и «Быль, в баснословном рассказе — союз любви и славы». Егор Васильевич Аладьин – представитель дворянского рода, который стал издателем знаменитого российского журнала под названием «Невский альманах».', 3, 3);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('49d498cc-9f9c-405e-af4e-fc90476044e0', '2022-05-13 00:00:00.000000', 716, 51, 0, '0k_aDwAAQBAJ', 'Право интеллектуальной собственности 3-е изд., испр. и доп. Учебник и практикум для академического бакалавриата', 'http://books.google.com/books/content?id=0k_aDwAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'В учебнике рассматриваются общее понятие интеллектуальной собственности, авторское право и его источники, объекты авторских и смежных прав, права авторов и иных лиц, смежные права, передача имущественного права, наследование исключительных прав, договоры в праве интеллектуальной собственности. Самостоятельное значение имеют также институты защиты авторских и смежных прав и ответственности за нарушение законодательства об интеллектуальной собственности. Кроме того, отдельные главы посвящены вопросам патентного права и средствам индивидуализации участников гражданского оборота, а также товаров, работ и услуг. Указанные темы рассматриваются на основе норм международных договоров, Гражданского кодекса Российской Федерации и научной разработанности. Материал предложенных тем сопровождается примерами из судебной практики, тестами и задачами для более успешного усвоения материала. Соответствует актуальным требованиям Федерального государственного образовательного стандарта высшего образования. Для студентов, обучающихся по программам академического бакалавриата, аспирантов и преподавателей высших юридических учебных заведений, школ дизайна, для тех, кто занимается вопросами правового регулирования результатов интеллектуальной деятельности, а также для широкого круга читателей, интересующихся вопросами интеллектуальной собственности.', 1636, 1472);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('c32f2823-ec76-4659-98fc-5a2d5fbe8908', '2022-05-13 00:00:00.000000', 716, 51, 1, 'eEQkEAAAQBAJ', 'Авторское право 3-е изд. Учебник и практикум для вузов', 'http://books.google.com/books/content?id=eEQkEAAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'На основе норм международных договоров и Гражданского кодекса Российской Федерации в учебнике рассмотрены общее понятие интеллектуальной собственности, авторское право и его источники, объекты авторских и смежных прав, права авторов и иных лиц, смежные права, передача имущественного права, наследование исключительных прав, договорное регулирование имущественных прав. Особое внимание уделено институтам защиты авторских и смежных прав и ответственности за нарушение законодательства об интеллектуальной собственности. Материал сопровождается примерами из судебной практики, а также вопросами и заданиями для самоконтроля, задачами и тестами, позволяющими лучше усвоить курс. Соответствует актуальным требованиям Федерального государственного образовательного стандарта высшего образования. Для студентов, изучающих курс академического бакалавриата, аспирантов и преподавателей высших юридических учебных заведений, школ дизайна, для тех, кто занимается вопросами правового регулирования и оформления авторских и смежных с ними прав, а также для широкого круга читателей, интересующихся вопросами авторских и смежных прав.', 0, 0);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('1ac3c4ac-f734-4417-b214-0a52b7d4981e', '2007-12-29 00:00:00.000000', 757, 3, 0, '1-Tl0DOA4BYC', 'Синдром выгорания', 'http://books.google.com/books/content?id=1-Tl0DOA4BYC&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Avtory vtorogo, pererabotannogo, izdaniya knigi (pervoe vyshlo v 2005 g.) analiziruyut izuchenie sindroma vygoraniya zarubezhnymi uchenymi i izlagayut rezul''taty sobstvennyh issledovanij. Chitateli uznayut ne tol''ko o prichinah i posledstviyah vygoraniya, metodah ego psihodiagnostiki, no i o vozmozhnyh sposobah pomoschi "vygorayuschim" rabotnikam. Dlya studentov i Vuzovskih prepodavatelej psihologicheskogo profilya, dlya menedzherov po personalu, dlya rabotodatelej, zabotyaschihsya o svoem zdorov''e i stabil''nosti kadrovogo sostava, "psihosotsial''nom" zdorov''e svoej organizatsii, a takzhe dlya vseh predstavitelej sotsial''nyh professij.', 99, 99);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('a3d020e8-3ae7-48a3-87ca-178020583e98', '2022-05-12 00:00:00.000000', 775, 31, 1, 'b94mEAAAQBAJ', 'Методика обучения математике. Обучение учащихся доказательству теорем 2-е изд., испр. и доп. Учебное пособие для академического бакалавриата', 'http://books.google.com/books/content?id=b94mEAAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'В пособии рассмотрены теоретические и практические основы обучения учащихся доказательству математических предложений. Раскрыт категориально-понятийный аппарат, относящийся к понятию «теорема», показаны ее виды, общие и частные методы доказательства. Описана пропедевтическая работа по подготовке учащихся к доказательству теорем, показана работа учителя по подготовке к уроку, на котором будет доказываться теорема. Рассмотрен вопрос об организации деятельности учащихся по «переоткрытию» формулировки теоремы и поиску способов и методов ее доказательства, описаны различные приемы закрепления теоремы.', 1498, 1348);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('6aafbe37-cf06-4421-b7e8-1fbe4177013d', '2022-05-13 00:00:00.000000', 62, 44, 0, 'MhInEAAAQBAJ', 'Электрические и электронные аппараты. Прикладные задачи виброударозащиты 2-е изд., испр. и доп. Учебное пособие для академического бакалавриата', 'http://books.google.com/books/content?id=MhInEAAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'В учебном пособии излагаются теоретические и практические методы расчета и проектирования виброзащитных устройств, особенности расчета и применения упругих амортизаторов, рекомендации по снижению вибраций и способам отстройки от резонанса. Также издание снабжено примерами, иллюстрациями и библиографическим списком.', 623, 561);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('9e56d968-f6a1-4482-817b-991ebab0b188', '2022-05-12 00:00:00.000000', 131, 31, 1, 'I2B2DwAAQBAJ', 'Высшая математика для экономистов в 3 ч. Часть 2 5-е изд., пер. и доп. Учебник и практикум для СПО', 'http://books.google.com/books/content?id=I2B2DwAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Цель данного издания научить будущих специалистов на основе фундаментального математического аппарата решать прикладные задачи современной экономики. Особенность учебника заключается в том, что он содержит не только основы классической теории по различным разделам высшей математики, но и практикумы к ним с решениями типовых и более сложных примеров и задач, в том числе экономической направленности. Издание представлено в трех частях. Для лучшего закрепления материала в учебнике приводятся задачи для самостоятельного решения, итоговые контрольные и тестовые задания как по каждому разделу, так и по курсу в целом.', 898, 808);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('ab1119ff-34a7-42e5-9fee-64ac5588248e', '2016-07-18 00:00:00.000000', 154, 51, 0, 'xCmwDAAAQBAJ', 'Живое обучение: Что такое e-learning и как заставить его работать', 'http://books.google.com/books/content?id=xCmwDAAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Для успешного развития бизнеса нужны сотрудники, которые действуют быстро, принимают верные решения, а их знания актуальны. Но не может же компания усадить несколько тысяч сотрудников за парты. На помощь приходит e-learning. Это способ повышения эффективности корпоративного обучения через применение современных технологий. Это обучение более быстрое, более гибкое, более точно отвечающее на быстро изменяющие потребности и задачи компании. Но важно помнить, что e-learning — это не просто использование новых технологий для передачи знаний. Технологии — лишь инструмент. Такая организация процесса обучения сотрудников — сама по себе совершенно новый метод, который таит в себе множество подвохов и сложностей. Елена Тихомирова, один из ведущих пропагандистов e-learning в стране, рассказывает о том, как избежать проблем при разработке и внедрении курсов.', 349, 349);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('568b0d29-dc53-40b0-b470-aa5a4421ebff', '2018-02-22 00:00:00.000000', 293, 23, 0, 'lGBNDwAAQBAJ', 'Письма Ивана Анненкова', 'http://books.google.com/books/content?id=lGBNDwAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Письма Ивана Анненкова» - эпистолярный сборник, составленный Прасковьей Егоровной Анненковой, урожденной Полиной Гёбль (Pauline Gueble, 1800 – 1876). Француженка по происхождению, эта смелая, мужественная женщина была супругой Ивана Александровича Анненкова, декабриста. Когда он был осужден на ссылку, Прасковья Егоровна испросила разрешения у императора отправиться вслед за мужем в Сибирь.*** Любовь Ивана и Полины Анненковых стала образцом горячего, преданного чувства, способного пройти через любые испытания. Она вдохновляла многих писателей и сценаристов. Отношениям русского декабриста и французской модистки посвящен роман А.Дюма «Учитель фехтования» (1840). В 1975г. советский режиссер В.Мотыль снял фильм «Звезда пленительного счастья», посвященный женам декабристов Письма Ивана Анненкова, изданные в сборнике, оживляют перед читателем события семейной биографии. Декабрист пишет матери, друзьям, чиновникам и любимой жене Прасковье. Он искренне беспокоится о семейных делах, пытается улучшить положение родных, заботится о вверенных ему имениях. Даже в ссылке Анненков не изменил своему горячему стремлению преобразовать мир к лучшему. Огромный интерес для читателей представляют и другие архивы семьи – «Воспоминания» и «Письма Полины Анненковой».', 3, 3);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('5ac0b3d3-7c03-40f7-9e39-6d3ccdd331e0', '2011-08-12 00:00:00.000000', 821, 51, 1, 'z0cqx6p9rLYC', 'Рынок ценных бумаг: Учебное пособие. 3-е изд. Стандарт третьего поколения', 'http://books.google.com/books/content?id=z0cqx6p9rLYC&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'В настоящем издании дается понятийно-терминологический аппарат, характеризующий основные аспекты функционирования рынка ценных бумаг. Раскрыты сущность, содержание ценных бумаг, их классификация; изложены основные формы регулирования рынка ценных бумаг, рассмотрены опе-рации на рынке ценных бумаг, в том числе различные биржевые операции. В книге предложен ряд современных форм и методик проверки знаний по курсу «Рынок ценных бумаг». Тесты, приведенные в издании, помогут проверить усвоение теоретического материала. Издание подготовлено в соответствии с новыми образовательными стандартами третьего поколения. Книга предназначена для студентов экономических вузов всех форм обучения, получающих степень бакалавра и специалиста по направлениям «Экономика», «Менеджмент», «Финансы». Данное издание позволит студенту получить знания и навыки для успешной профессиональной деятельности и (или) для продолжения профессионального образования в магистратуре.', 163, 163);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('ceff027d-c3c5-4b6a-8efe-1c383a8e83d8', '2022-01-27 00:00:00.000000', 44, 31, 0, 'JWIpDwAAQBAJ', 'Стохастический алгоритм поиска глобального минимума функции', 'http://books.google.com/books/content?id=JWIpDwAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'В статье дан обзор методов случайного поиска глобального минимума функции. Представлен алгоритм нахождения минимума одномерной функции на основе двух стратегий поведения случайных элементов: поиска и разведки. Приводятся результаты вычислительных экспериментов.', 210, 189);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('7c242470-d93b-4e96-9797-da00d1f4b406', '2021-09-22 00:00:00.000000', 909, 30, 0, 't6tHEAAAQBAJ', 'Подходы к представлению научных знаний в интернет-науке', 'http://books.google.com/books/content?id=t6tHEAAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', null, 35, 31);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('451c5986-8219-4351-8eb4-c7d145cdd10f', '2018-01-26 00:00:00.000000', 189, 14, 1, 'URNJDwAAQBAJ', 'Русская литература в школе. Фольклор, И. А. Крылов, М. Е. Салтыков-Щедрин, Ф. И. Тютчев, А. П. Чехов. Поурочные разработки. Учебно-методическое пособие с приложением', 'http://books.google.com/books/content?id=URNJDwAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Книга состоит из пяти разделов, размещенных в книге и приложении, в которые вошли поурочные разработки по изучению фольклора и сочинений русских писателей. В каждом разделе литературоведческие статьи сочетаются с методическими разработками и содержат материал, необходимый при изучении произведений на классных и внеклассных занятиях, рекомендации по использованию изобразительного и игрового материалов, воспоминания писателей и современников по той или иной теме.Учебное пособие адресовано учителям общеобразовательных организаций, реализующих ФГОС основного общего (5-9 кл.) и среднего общего образования (10-11 кл.)(Материалы с компакт-диска добавлены в конце книги.)', 311, 280);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('e39b1328-cc21-46be-9169-536e9eb25413', '2022-03-12 00:00:00.000000', 496, 44, 0, 'f99jEAAAQBAJ', 'Технологическая подготовка производства', 'http://books.google.com/books/content?id=f99jEAAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Рассматриваются особенности и методы разработки технологического процесса при подготовке производства. Показана необходимость использования установленных отечественных стандартов ЕСТД. Приведены примеры оформления документации, классификация видов технологических процессов, состав комплекта документов, словарь терминов и определений основных понятий. По каждой теме составлены контрольные вопросы.Предназначено для бакалавров по направлению 15.03.01 «Машиностроение», 22.03.01 «Материаловедение и технологии материалов», магистрантов технологических направлений и инженеров-технологов машиностроительных предприятий.', 623, 561);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('a4e5dafd-de86-435d-ae77-35b7447040ab', '2022-05-13 00:00:00.000000', 464, 49, 0, 'rDgkEAAAQBAJ', 'Современный английский язык для филологов 2-е изд., испр. и доп. Учебник для академического бакалавриата', 'http://books.google.com/books/content?id=rDgkEAAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Настоящий учебник включает в себя тексты известных британских и американских филологов и писателей по трем направлениям – общий английский, английский для филологов и английская лингвистическая терминология. Представленные тексты дополнены вопросами и заданиями, которые позволят студентам закрепить свои знания и применять их на практике.', 1311, 1180);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('95a23582-e271-45af-ba35-e7641ea6d3d1', '2017-09-05 00:00:00.000000', 13, 3, 0, '-UXQAAAAQBAJ', 'Коррекционная психология. Шпаргалка', 'http://books.google.com/books/content?id=-UXQAAAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Данное пособие является вспомогательным материалом для подготовки к экзаменам, зачетам по дисциплине «Коррекционная психология». Материал книги составлен в соответствии с Государственным образовательным стандартом высшего профессионального образования.Объем и качество изложенного материала поможет студентам сдать экзамены на оценку «отлично».', 49, 44);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('bfd3d4d7-b990-4fc2-92fe-a81dc3b23dad', '2020-01-11 00:00:00.000000', 296, 19, 0, 'E-bJDwAAQBAJ', 'Социология 4-е изд., пер. и доп. Учебник и практикум для академического бакалавриата', 'http://books.google.com/books/content?id=E-bJDwAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Данный курс социологии подготовлен в строгом соответствии с требованиями Федерального государственного образовательного стандарта высшего профессионального образования третьего поколения и представляет собой обобщение последних достижений мировой и отечественной социологической науки. Благодаря этому учебнику студенты научатся выявлять проблемы социального характера при анализе конкретных жизненных ситуаций в российском обществе, предлагать варианты их решения с учетом методологических требований научного метода в социологии, а также свободно будут владеть навыками применения современных инструментов эмпирического и прикладного социологического исследования для решения практических задач.', 1861, 1675);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('51c2fc5f-ab13-45a3-84a3-eb8dbf9ea0ab', '2022-05-12 00:00:00.000000', 845, 51, 0, 'ylEkEAAAQBAJ', 'Право интеллектуальной собственности 2-е изд., пер. и доп. Учебник для вузов', 'http://books.google.com/books/content?id=ylEkEAAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Московский государственный юридический университет имени О. Е. Кутафина (МГЮА) предлагает вниманию читателей первый кафедральный учебник, посвященный праву интеллектуальной собственности и подготовленный коллективом ведущих специалистов в данной сфере, а также молодыми учеными кафедры интеллектуальных прав. Соответствует актуальным требованиям Федерального государственного образовательного стандарта высшего образования. Данный учебник будет полезен студентам-бакалаврам, поскольку написан с учетом новых правовых тенденций, на современном методологическом уровне, является понятным для студента-юриста логически и стилистически, а также аспирантам, преподавателям, специалистам, чья работа связана с возникновением, приобретением, передачей и прекращением прав на объекты интеллектуальной собственности.', 1348, 1213);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('738a4153-77a2-45ff-be8e-8563329319d4', '2021-12-02 00:00:00.000000', 874, 23, 0, 'BLxSEAAAQBAJ', 'Мистер Д.', 'http://books.google.com/books/content?id=BLxSEAAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'После травмы в детском возрасте Диана перестала разговаривать. Немота не мешает девушке жить... до тех пор, пока в особняке, где она работает прислугой, не появляется новый эксцентричный владелец, таинственный мистер Д.Его вызывающее поведение с каждым днем вызывает всё больше вопросов. Кто он такой? Зачем появился в этом месте? И что ему нужно от странной немой девушки?', 99, 89);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('83c92860-e467-470e-91ad-e14f45050600', '2022-05-12 00:00:00.000000', 139, 51, 0, '6-0jEAAAQBAJ', 'Теория организации и организационное поведение. Методология организации 2-е изд., испр. и доп. Учебное пособие для академического бакалавриата', 'http://books.google.com/books/content?id=6-0jEAAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'В учебном пособии рассмотрены основные понятия и законы организации, показаны социальные организации с позиции системного подхода, представлена структура и жизненный цикл социальных организаций, а также тактические процессы управления знаниями и информационные системы для их обеспечения. Книга дополнена вопросами для обсуждения и повторения, которые помогут студентам освоить материалы учебного пособия.', 436, 392);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('247fe23e-7110-4ec9-9672-040ca01c433b', '2022-05-13 00:00:00.000000', 558, 44, 0, 'mkQkEAAAQBAJ', 'Информационные технологии в менеджменте 2-е изд., пер. и доп. Учебник для СПО', 'http://books.google.com/books/content?id=mkQkEAAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'В учебнике рассматриваются вопросы использования корпоративных информационных систем для автоматизации управления бизнес-процессами компаний. Описаны стандарты MRP, MRP II, ERP, CSRP, ERP II, функциональность некоторых из представленных на российском рынке информационных систем: «1С:Управление производственным предприятием», Oracle E-Business Suite, Microsoft Dynamics AX, Infor ERP LN (BAAN). Студенты познакомятся с такими известными стандартами и интерфейсами интеграции информационных систем, как ODBC, OLE DB, ADO, XML, CORBA и COM. В учебник включен практический пример реализации одного из бизнес-процессов в информационной системе Microsoft Dynamics AX. Соответствует актуальным требованиям Федерального государственного образовательного стандарта среднего профессионального образования и профессиональным требованиям. Для студентов образовательных учреждений среднего профессионального образования, обучающихся по экономическим специальностям.', 1398, 1258);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('f7b07a55-ac70-48fe-a18b-44260af55119', '2017-09-19 00:00:00.000000', 775, 44, 0, 'VN42DwAAQBAJ', 'Водобитумные эмульсии', 'http://books.google.com/books/content?id=VN42DwAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Излагается материал по водобитумным эмульсиям. Дан анализ физико-химического состава и эксплуатационных свойств нефтяных битумов и водобитумных эмульсий. Представлены: область применения эмульсий; классификация битумных эмульсий и поверхностно-активных веществ; основы процесса эмульгирования; методы получения эмульсий; методы определения свойств эмульсий и битумных вяжущих.', 400, 360);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('9c1c4381-70b9-4405-9921-6673ee7cd766', '2022-05-13 00:00:00.000000', 485, 51, 0, 'q_sjEAAAQBAJ', 'Право интеллектуальной собственности 3-е изд., пер. и доп. Учебник и практикум для академического бакалавриата', 'http://books.google.com/books/content?id=q_sjEAAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Современное развитие информационных технологий и права качественно влияет на все стороны общественной жизни. Одним из главных качеств современного юриста является его способность к аналитической работе с объектами интеллектуальной собственности. В книге изложены основные понятия интеллектуальной собственности, содержание понятия «охрана интеллектуальной собственности», рассмотрены законодательство и договоры, которые регулируют интеллектуальную собственность. Учебник отличает большая методическая часть (тесты, контрольные вопросы и задания, списки документов и литературы), которая должна помочь студентам в лучшем усвоении курса, а будущим юристам – в их профессиональной деятельности.', 1398, 1258);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('896fa30f-ee98-409f-ac18-c22dd8503369', '2018-12-01 00:00:00.000000', 305, 23, 0, 'axV9DwAAQBAJ', 'Евангелие от Александра. (Моя весть)', 'http://books.google.com/books/content?id=axV9DwAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'О главном для всех, об объединяющей все вере и неверии, философии, о лжи и противоречиях, отсутствующих притчах, таинствах церкви, действительном содомском грехе (власти), грядущем правителе (через совпадения с пророчествами), новых опасностях для людей и России. Если написавшие «вечную» книгу могут считаться писателями вечности, то тот, кто этим трудом вносит в неё существенные коррективы, становится единственным как рука Великого Целого. Пророческие стихи, анекдоты автора. Текст дополнен!!', 25, 22);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('f1ded8d0-de9a-4986-a5d3-bbd54a595615', null, 512, 0, 0, 'yAsHAwAAQBAJ', 'Общая теория обязательств', 'http://books.google.com/books/content?id=yAsHAwAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'This book, "Obschaya teoriya obyazatelstv," by E. Godeme, is a replication of a book originally published before 1948. It has been restored by human beings, page by page, so that you may enjoy it in a form as close to the original as possible.', 644, 644);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('3f7addae-578e-4a53-89c1-e2b2a4df3063', '2017-12-07 00:00:00.000000', 62, 23, 0, '0TJCDwAAQBAJ', 'Игра', 'http://books.google.com/books/content?id=0TJCDwAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Фантастическая история, в которой главный герой через пространственный портал, расположенный в карельских лесах, попадает на обособленную планету, которую местные жители называют Остров. Жители планеты много тысячелетий назад потеряли связь с центром галактики, но о планете Земля знают всё. У них даже есть для неё своё название – «Дикие земли». Здесь читатель вновь встретит героев книги «Страна за туманом».', 175, 157);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('2456c4f4-7bae-4a96-a15a-0b1f59ecb3b5', '2017-05-26 00:00:00.000000', 272, 25, 0, 'E-4tDwAAQBAJ', 'Теория и практика языков программирования. Учебник для вузов. 2-е изд. Стандарт 3-го поколения', 'http://books.google.com/books/content?id=E-4tDwAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Учебник посвящен систематическому изложению теории и практики языков программирования. Он отражает классическое содержание учебной дисциплины по языкам программирования. Все сложные вопросы поясняются законченными примерами. Кроме того, здесь предлагается полный комплекс задач и упражнений по узловым вопросам. Учебник охватывает базисные разделы следующих дисциплин: теория формальных языков, теория автоматов и формальных языков, языки программирования, программирование, объектно-ориентированное программирование, логическое и функциональное программирование, теория вычислительных процессов. В новом издании обсуждаются характеристики, а также последние тенденции развития универсальных языков программирования высокого уровня, таких как Scala, Go и Swift; поясняются главные особенности последних стандартов классических языков C++, Java и C#: лямбда-выражения во всех этих языках, cсылочный тип rvalue и семантика перемещения в языке C++ 11, ковариантность и контрвариантность родовых шаблонов в C#; существенно расширено представление скриптового языка Ruby, рассматриваются его блоки, механизмы единичного наследования и подмешивания, а также утиной типизации; добавлено описание аппарата событий и программирования на основе событий; показано применение стиля функционального программирования в скриптовых и объектно-ориентированных языках Python, Ruby, C#, Java, C++, Scala, Go и Swift. Учебник предназначен для студентов инженерного, бакалаврского и магистерского уровней компьютерных специальностей, может быть полезен преподавателям и исследователям/разработчикам трансляторов и другого программного обеспечения. Рекомендовано Санкт-Петербургским институтом информатики и автоматизации Российской академии наук (СПИИРАН) в качестве учебника по направлению «Информатика и вычислительная техника».', 1480, 1480);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('2c349c28-84bd-40d9-bb10-448ef133746d', '2020-01-11 00:00:00.000000', 13, 23, 0, 'E-jJDwAAQBAJ', 'Про Ивана Молодца, царя, бояр да горох. Глава 1', 'http://books.google.com/books/content?id=E-jJDwAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Вы побываете в мире древней романтики, прочитаете думы, былины и завещания на диалекте в стихах, в которых разнообразные обитатели мира помогают главным действующим героям, в которых интегрируются и царь, и народ, и житейская мудрость, и благоразумие соседей. Книга предназначена для архивариусов и для иностранного туриста, владеющего русским языком. Сноски. Обычаи и традиции. Повышение методов управления.', 612, 551);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('badbc1fb-618b-464c-876c-2051a7405886', '2022-05-13 00:00:00.000000', 5, 38, 0, 'oN2gDwAAQBAJ', 'Теория и методика музыкального воспитания детей с проблемами в развитии 2-е изд., испр. и доп. Учебное пособие для академического бакалавриата', 'http://books.google.com/books/content?id=oN2gDwAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'В учебном пособии раскрываются как общие подходы к музыкальному воспитанию детей с различными отклонениями в развитии, так и специфические особенности содержания и организации коррекционной работы средствами музыки в дошкольном образовательном учреждении компенсирующего вида. Впервые показаны возможности использования музыкотерапии в системе психологической помощи детям данной категории. В систематизированном виде представлены содержание и технология организации занятий по коррекционной ритмике с дошкольниками, имеющими проблемы в развитии. Соответствует актуальным требованиям Федерального государственного образовательного стандарта высшего образования. Книга предназначена студентам педагогических вузов, а также будет интересна педагогам, музыкальным руководителям, дефектологам, психологам, работающим в системе помощи детям с различными проблемами в развитии.', 1036, 932);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('1f38e341-08dc-4b89-9f87-e80459b19585', '2017-09-05 00:00:00.000000', 242, 49, 1, 'YVbQAAAAQBAJ', 'Общие основы педагогики. Шпаргалка', 'http://books.google.com/books/content?id=YVbQAAAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Данное пособие является вспомогательным материалом для подготовки к экзаменам, зачетам по дисциплине «Общие основы педагогики». Материал изложен последовательно, четко, в объеме, соответствующем задачам книги.Пособие поможет студентам сдать экзамены на оценку «отлично».', 87, 78);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('8a5c3a89-18f8-446b-a384-05616684ae42', '2018-02-22 00:00:00.000000', 293, 23, 0, 'pGBNDwAAQBAJ', 'Письма Полины Анненковой', 'http://books.google.com/books/content?id=pGBNDwAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', '«Письма Полины Анненковой» – эпистолярный сборник авторства Прасковьи Егоровны Анненковой, урожденной Полины Гёбль (Pauline Gueble, 1800 – 1876). Француженка по происхождению, эта смелая, мужественная женщина была супругой Ивана Александровича Анненкова, декабриста. Когда он был осужден на ссылку, Прасковья Егоровна испросила разрешения у императора отправиться вслед за мужем в Сибирь.*** В эпистолярную подборку входят письма разных лет, преимущественно – прошения к высокопоставленным лицам. Прасковья Егоровна не владела письменным русским языком, поэтому ее послания представлены в переводе или написаны рукой мужа. Среди писем читатель найдет знаменитое прошение Полины Гёбль к Николаю I о возможности последовать в ссылку за своим гражданским супругом. Любовь Ивана и Полины Анненковых стала образцом горячего, преданного чувства, способного пройти через любые испытания. Она вдохновляла многих писателей и сценаристов. Отношениям русского декабриста и французской модистки посвящен роман А.Дюма «Учитель фехтования» (1840). В 1975г. советский режиссер В.Мотыль снял фильм «Звезда пленительного счастья», посвященный женам декабристов. Не меньший интерес для читателя представляют другие архивы семьи – «Воспоминания» и «Письма Ивана Анненкова».', 3, 3);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('0f2f2401-1de0-4841-9832-6bc12904fc10', '2015-10-28 00:00:00.000000', 26, 30, 1, 'Gy7TCgAAQBAJ', 'Социология. Учебник', 'http://books.google.com/books/content?id=Gy7TCgAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'В учебнике «Социология» интегрированы классические представления о предмете, теоретических основах современной социологии, истории ее развития и инновационные подходы к анализу новейших тенденций в теории и методологии социологии, к изучению социальных процессов и социальных проблем, характерных для общества XXI в. Учебник создан в полном соответствии с федеральными государственными стандартами высшего профессионального образования по направлению подготовки «Социология» (квалификации «бакалавр» и «магистр»). Учебник предназначен для реализации учебных дисциплин «Основы социологии», «Общая социология», «Теоретическая социология», «История социологии», входящих в образовательные программы академического бакалавриата по направлению «Социология». Также учебник может быть использован на уровне магистратуры по направлению «Социология», особенно для студентов, обучающихся в магистратуре, имея степень бакалавра по другим направлениям. Учебник может быть частично использован в преподавании учебной дисциплины «Социология» в рамках образовательных программ уровня бакалавриата по другим социогуманитарным направлениям.', 250, 250);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('c6bca188-c904-43f4-be83-f1001efc876a', '2022-04-03 00:00:00.000000', 116, 4, 0, 'lz0sEAAAQBAJ', 'История России для технических вузов 4-е изд., пер. и доп. Учебник для прикладного бакалавриата', 'http://books.google.com/books/content?id=lz0sEAAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'В учебнике в сжатой форме излагаются основные события и проблемы истории России с древнейших времен до наших дней. Книга содержит необходимый объем систематизированных знаний по отечественной истории, расширяет базовые представления студентов, полученные ими в средней общеобразовательной школе, о характерных особенностях исторического пути, пройденного Российским государством и населяющими его народами. Издание содержит приложения: краткие сведения о выдающихся отечественных историках XVIII XX вв.; сведения о правителях и руководителях России с древности до наших дней; хронологическую таблицу, в которой приведены даты важнейших событий российской истории.', 1873, 1686);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('c810b7bf-06df-4b6b-b384-12b02f7f4852', '2022-05-12 00:00:00.000000', 726, 3, 0, 'E-4jEAAAQBAJ', 'Психология 2-е изд., испр. и доп. Учебное пособие для академического бакалавриата', 'http://books.google.com/books/content?id=E-4jEAAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'В настоящем издании изложены теоретические основы общей, возрастной и социальной психологии. Рассмотрены современное состояние методологии психологии, нейропсихологические основы поведения, развитие психики в фило- и онтогенезе, структура познавательной сферы, психология и психофизиология личности и деятельности. Представлены особенности общения людей в больших и малых группах, социально-психологические характеристики личности и семьи, механизмы отклоняющегося поведения, а также вопросы психологии здоровья.', 1098, 988);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('66ee2f42-619f-4d47-816b-08953ee256cf', '2020-07-31 00:00:00.000000', 873, 51, 0, 'UkQkEAAAQBAJ', 'Управление государственными и муниципальными закупками 3-е изд., пер. и доп. Учебник и практикум для СПО', 'http://books.google.com/books/content?id=UkQkEAAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Подготовленный учебник обобщает правовую и методическую информацию, необходимую для эффективного управления закупками, он максимально иллюстративен для того, чтобы улучшить восприятие и запоминание теоретических данных и систематизировать знания по дисциплине «Государственный заказ». Практические задания призваны подготовить слушателей к решению разнообразных практических задач по управлению государственными и муниципальными закупками. Соответствует актуальным требованиям Федерального государственного образовательного стандарта среднего профессионального образования и профессиональным требованиям. Для студентов образовательных учреждений среднего профессионального образования.', 1811, 1630);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('9b4f6975-203d-4f50-86ee-affa3d6e36c9', '2022-05-12 00:00:00.000000', 558, 38, 0, 'lhyQDwAAQBAJ', 'Социально-экономическая география зарубежной азии, австралии и океании 2-е изд., пер. и доп. Учебник и практикум для академического бакалавриата', 'http://books.google.com/books/content?id=lhyQDwAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'В учебнике представлено региональное и страноведческое описание Зарубежной Азии, Австралии и Океании и наиболее развитых в экономическом отношении стран. Дается оценка их политико- и экономико-географического положения, раскрываются этапы становления государств, показывается своеобразие природных условий, обеспеченность полезными ископаемыми, приводятся расовый, этнический и национальный состав населения, его демографические характеристики (рождаемость, смертность, половозрастной состав, продолжительность жизни), рассматриваются основные отрасли хозяйства, обеспечивающие экономическое развитие региона в целом и самых крупных стран, туристские макрорайоны, их основные природные достопримечательности и историко-культурные объекты. Соответствует актуальным требованиям Федерального государственного образовательного стандарта высшего образования. Для студентов и преподавателей высших ученых заведений образовательных направлений «География», «Страноведение», «Международные отношения», «Регионоведение», «Международный туризм», «Экономика» и др., для учителей географии, истории, обществознания, а также для всех интересующихся регио-новедческой и страноведческой проблематикой.', 1811, 1630);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('63fd39bd-e1b0-45cf-979a-e485dae2694b', '2022-05-13 00:00:00.000000', 354, 38, 0, 'yMWDDwAAQBAJ', 'Психологическая диагностика умственного развития детей 2-е изд., испр. и доп. Учебное пособие для СПО', 'http://books.google.com/books/content?id=yMWDDwAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Современная система образования характеризуется многообразием научно-методологических концепций, типов учебных заведений, программ и методов обучения. При этом ученик должен обладать определенным потенциалом, необходимым для эффективной учебной деятельности. Специфические черты умственного развития некоторых детей, вполне здоровых с медицинской точки зрения, предопределяют недостаточный уровень усвоения учебных предметов, трудности выполнения учебной деятельности. Помощь таким ученикам не может сводиться только к организации дополнительных занятий, но и должна быть также направлена и на изменение свойств мыслительно-речевой деятельности учащихся. Этой теме и посвящено настоящее учебное пособие, в котором освещены теоретические вопросы, касающиеся как определения умственного развития и его соотношения с умственными способностями, так и выявления его показателей, а также обуславливающих его факторов, и другим вопросам психологической диагностики умственного развития детей.', 973, 876);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('70450cfc-91ca-4b0c-915f-109e8ee4ffbf', null, 773, 36, 0, '2tH9AgAAQBAJ', 'Конструкция и прочность самолётов и вертолётов', 'http://books.google.com/books/content?id=2tH9AgAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'В учебнике рассматривается порядок проектирования и постройки летательных аппаратов.', 708, 708);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('a15f0a51-9589-4f31-bc50-33bc3968fca4', '2018-01-28 00:00:00.000000', 257, 24, 0, 'xUBJDwAAQBAJ', 'Д. Белл: «Грядущее постиндустриальное общество…»', 'http://books.google.com/books/content?id=xUBJDwAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Продолжая исследовать философское наследие Дениэла Белла, невозможно обойти вниманием его труд «Грядущее постиндустриальное общество: Опыт социального прогнозирования». В нем Белл уже предстает перед нами не в качестве яростного обвинителя идеологий, Идеологии как таковой, а как тщательно взвешивающий каждое свое слово автор, полностью отдающий себе отчет в том, кому он противопоставляет то, что он пишет. Наша же задача здесь была весьма скромной: просто донести до Вашего сведения обнаруженное Беллом – как сказанное, так и не-сказанное им.', 16, 16);
