INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('a8697c38-6937-4c7d-858d-f1e5b9d13c63', '2018-10-27 00:00:00.000000', 242, 9, 1, 'myh1DwAAQBAJ', 'Правосознание и правовая культура личности в России. Монография', 'http://books.google.com/books/content?id=myh1DwAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Данная монография посвящена вопросам теоретического осмысления правосознания и правовой культуры личности.Проблема правосознания и правовой культуры личности встает наиболее остро в периоды общественной трансформации, когда объективно происходит разрушение большинства институциональных и нормативных образцов, меняются цели, ценности, средства реализации социальных потребностей, в обществе устанавливаются новые правила правового поведения.', 612, 551);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('cdfd28f0-9dca-4a93-99b7-e2e97b78e40f', '2013-03-12 00:00:00.000000', 17, 4, 0, 'lVwDEAAAQBAJ', 'История книги от ее появления до наших дней', 'http://books.google.com/books/content?id=lVwDEAAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Вниманию читателей предлагается перевод книги французского эллиниста Огюста Эмиля Эггера (1813-1885 гг.) "История книги от ее появления до наших дней" (1882 г.). Работа представляет собой очерк, рассказывающий о появлении на свет книги, ее последующем совершенствовании и умножении, а также развитии книжной торговли. Автором рассматриваются исторические периоды до и после изобретения&nbsp;книгопечатания. Перевод с 3-го французского издания с примечаниями переводчика.', 127, 114);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('752ce611-7d5a-4f5a-b5c0-1f9304d50afd', '2020-01-30 00:00:00.000000', 485, 34, 0, 'UhrNDwAAQBAJ', 'Тайцзицюань стиль У', 'http://books.google.com/books/content?id=UhrNDwAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'В этой книге подробно описана техника и особенности прикладного применения Тайцзицюань стиля У а также представлена наиболее известная «Длинная форма» школы, известная также как: Дацзя - «Большой Каркас». Книга содержит большое количество иллюстраций с подробными пояснениями, что делает ее полезной для всех интересующихся боевыми искусствами. Содержание: Введение Философские аспекты прикладной техники Тайцзицюань Базовая техника Тайцзицюань "Длинная форма" - Дацзя Заключение', 108, 108);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('2af52710-b4e0-411e-976e-d0edf32324a0', '2011-10-21 00:00:00.000000', 485, 23, 0, 'ubbRAAAAQBAJ', 'О доблестном рыцаре Гае Гисборне', 'http://books.google.com/books/content?id=ubbRAAAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'В этом романе впервые показано на одном из самых известных примеров в истории, как было на самом деле и как должно быть в здоровом развивающемся обществе, устремленном к прогрессу. К счастью, в данном случае то и другое полностью совпадает.Хорошо бы каждого воспевающего пиратов, что грабили торговые и пассажирские суда и топили их вместе со всем экипажем, привлекать к суду. То же самое относится и к создателям бесчисленных фильмов и компьютерных игр о Робин Гуде, который грабил всех и каждого, проезжающего через Шервудский лес, и даже убил шерифа, как об этом с восторгом поют в балладах и взахлеб расписывают в нынешних сериалах.И вообще на каторжные работы всех, кто воспевает уголовников и братков, в каком бы времени те ни обретались.Мы идем в будущее! Зачем нам эта грязь на подошвах?Автор', 223, 201);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('29bae54a-b243-4d50-ac28-04dd302276b3', null, 775, 30, 0, 'uNL9AgAAQBAJ', 'Гидродинамика, или записки о силах и движениях жидкостей', 'http://books.google.com/books/content?id=uNL9AgAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'В 1738 вышла в свет знаменитая работа Даниила Бернулли "Гидродинамика, или Записки о силах и движениях жидкостей (Hydrodynamica, sive de viribus et motibus fluidorum commentarii)", в которой сформулированы основы механики жидкости. В этом сочинении Бернулли впервые ввел понятия работы и коэффициента полезного действия, представил уравнение стационарного движения идеальной жидкости (уравнение Бернулли), изложил идеи кинетической теории газов.', 2405, 2405);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('9fcb5cdc-92f1-4fdc-b479-bb7a30cb9320', '2017-04-06 00:00:00.000000', 709, 23, 0, 'Rk6fDgAAQBAJ', 'У-у-у, страшно!', 'http://books.google.com/books/content?id=Rk6fDgAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Что происходит в зверином мире?! Странные огни за деревьями, светящиеся фигуры и таинственные исчезновения... Неужели в Скверном лесу бесчинствуют потусторонние силы? Смелым сыщикам Фу-Фу и Кис-Кису придётся нелегко, ведь на этот раз они столкнутся с ОЧЕНЬ НЕОБЫЧНЫМИ преступниками!', 373, 336);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('70200af5-384c-4dfe-931b-c0eddc1ece94', '2022-05-15 00:00:00.000000', 558, 23, 0, 'PlTXDwAAQBAJ', 'У оружия нет имени', 'http://books.google.com/books/content?id=PlTXDwAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Репликанты – псы войны на цепи у алчных корпораций. Генетически совершенные солдаты, созданные для одной цели – побеждать. Но что делать, когда бои грохочут где-то далеко, командиров нет, а вокруг – мирная планета? Вражеская планета.', 205, 184);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('cc0213d1-467a-474c-a0f4-ebdb3da8c520', '2014-04-21 00:00:00.000000', 165, 0, 0, 'OBpmAwAAQBAJ', 'Основы информационного права. Учебник для бакалавров', 'http://books.google.com/books/content?id=OBpmAwAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Рассматриваются основные вопросы теории и практики информационного права, его понятия и объекты, принципы, особенности предмета и системы. Подробно описывается система законодательства в информационной сфере, уделяется внимание проблемам правового обеспечения информации ограниченного доступа и информационной безопасности, в том числе ответственности за информационные правонарушения. Студентам юридических вузов, обучающимся по специальностям 030501 «Юриспруденция», 030505 «Правоохранительная деятельность», 030500 «Юриспруденция (бакалавр)», аспирантам, преподавателям юридических вузов, а также всем, кто интересуется проблемами правового обеспечения развития информационного общества.', 250, 250);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('1f0fe604-dedf-4ba0-aedb-0f2b801766d2', '2022-04-23 00:00:00.000000', 418, 23, 0, 'Jv2hBQAAQBAJ', 'А что у вас?', 'http://books.google.com/books/content?id=Jv2hBQAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'В сборник вошли стихотворения Сергея Михалкова для детей с яркими иллюстрациями и делением слов в тексте на слоги.', 61, 55);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('41cc6908-2e32-4a52-863a-356d62cd1036', '2018-08-23 00:00:00.000000', 418, 5, 0, '079qDwAAQBAJ', 'А что у вас?', 'http://books.google.com/books/content?id=079qDwAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Самые лучшие стихи знаменитого писателя Сергея Михалкова впервые выходят по слогами с ударениями в одной большой и красивой книжке! Теперь те малыши, которые уже знают буквы, сами смогут прочитать весёлые стихотворения, песенки, загадки и считалочки. А большие иллюстрации В. Чижикова, С. Бордюга и Н. Трепенок, Н. Бугославской помогут сделать процесс чтения ещё увлекательнее и поближе познакомят с героями этой книжки.Для дошкольного возраста.', 348, 313);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('fdef8f78-a0c1-4db0-900b-8af29d377a95', '2017-09-05 00:00:00.000000', 502, 19, 0, 'DwxCAgAAQBAJ', 'Беседы у дерева', 'http://books.google.com/books/content?id=DwxCAgAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Заметки (беседы), собранные в книге, трудно отнести к какому-либо одному жанру. Их автор – специалист в области культурной антропологии, но назвать их просто антропологическими вряд ли правомерно, хотя профессия бесспорно накладывает на них отпечаток.В книге в диалогической форме толкуются традиции разных народов в их словесном (пословицы, поговорки, мифологические рассказы) и особенно действенном (поведение, ритуалы, праздники) проявлении. Узкий круг читателей, знакомых с «Беседами» в рукописи или по отдельным публикациям, видит в них философскую антропологию, но скорее это антропология поэтическая. В «Беседах» делается попытка вернуться к состоянию, когда поэзия, магия, наука и искусство еще не были расчленены, но с учетом сегодняшнего состояния знания и нашей необратимой расщепленности.Книга снабжена подробным указателем.', 200, 180);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('6874ca19-1387-489f-9c65-a68a007f53f6', null, 177, 31, 0, 'zEj6AgAAQBAJ', 'Сборник задач по элементарной математике повышенной трудности', 'http://books.google.com/books/content?id=zEj6AgAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Сборник содержит свыше тысячи задач по элементарной математике, главным образом повышенной трудности. Задачи, по возможности, систематизированы и снабжены решениями. В отдельных случаях в связи с решением задачи и там, где это уместно, приведены вопросы теории. Иногда они предпосланы решению группы задач, объединенных общей идеей. Даны разъяснения по вопросам теории равносильности уравнений, построения графиков, комплексных чисел, обратных тригонометрических функций, математической индукции и некоторым другим вопросам. Сборник рассчитан на лиц, окончивших среднюю школу и желающих продолжать совершенствоваться в методах решения задач или готовиться в вуз. Он может послужить дополнительным пособием учителю при работе в классе, для индивидуальных заданий учащимся, особо интересующимся математикой, студентам педагогических институтов.', 578, 578);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('ef157501-3eb4-43b8-9603-68e6976cd86c', '2017-09-05 00:00:00.000000', 59, 23, 0, 'sVqJXgEk-3oC', 'У окна', 'http://books.google.com/books/content?id=sVqJXgEk-3oC&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', '«Андрей Николаевич снял с подоконника горшок с засохшей геранью и стал смотреть на улицу. Всю ночь и утро сеял частый осенний дождь, и деревянные домики, насквозь промокшие, стояли серыми и печальными. Одинокие деревья гнулись от ветра, и их почерневшие листья то льнули друг к другу, шепча и жалуясь, то, разметавшись в разные стороны, тоскливо трепетали и бились на тонких ветвях. Наискосок, в потемневшем кривом домике, отвязалась ставня и с тупым упорством захлопывала половинку окна, таща за собой мокрую веревку, и снова со стуком ударялась о гнилые бревна...»', 25, 22);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('77ec9495-5511-4c2f-951f-888425ed2cb4', '2018-12-20 00:00:00.000000', 87, 37, 0, 'xK9hDwAAQBAJ', 'У Троицы на Самотеке... Подворье Свято-Троицкой Сергиевой Лавры в Москве', 'http://books.google.com/books/content?id=xK9hDwAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Издание рассказывает о подворье Свято-Троицкой Сергиевой Лавры, о его прошлом и нынешнем состоянии и о том, каким это примечательное место предстанет, если будет на то Божие благоволение, в недалеком будущем.', 186, 167);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('d88b8ef6-50f1-4e64-96b5-91dff3a482ab', '2020-01-25 00:00:00.000000', 485, 34, 1, 'BiLMDwAAQBAJ', 'Тайцзицюань стиль У', 'http://books.google.com/books/content?id=BiLMDwAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Книга известного мастера "Внутренних" стилей ушу посвящена подробному описанию быстрого комплекса формальных упражнений Тайцзицюань стиля У - Куайцюань. Эту форму практиковали и преподавали своим ученикам такие великие мастера тайцзицюань, как У Цзяньцюань и Ма Юэлян. В работе также подробно описана техника и особенности прикладного применения Тайцзицюань. Книга содержит большое количество иллюстраций с подробным изложением особенностей техники стиля и будет полезна всем изучающим боевые искусств. Содержание: Введение Философские аспекты прикладной техники Тайцзицюань Базовая техника Тайцзицюань Формы кисти Стойки Методы ударов кулаком Удары ногами Куай цюань – «Быстрый кулак» Заключение', 108, 108);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('d2a6e8bb-a0f5-4bc2-a742-4c1b44607ddf', '2020-01-29 00:00:00.000000', 485, 34, 0, 'b8TMDwAAQBAJ', 'Тайцзицюань стиль У', 'http://books.google.com/books/content?id=b8TMDwAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Эта книга представляет собой подробное описание упрощенного комплекса формальных упражнений Тайцзицюань стиля У. Эта форма содержит меньшее количество движений по сравнению с Длинной формой (Дацзя), но сохраняет все особенности и характеристики школы. Упрощенная форма была разработана мастерами Тайцзицюань семьи У и впервые была представлена широкой публике У Инхуа, дочкой знаменитого У Цзяньцюаня – основателя Тайцзицюань стиля У. Также в книге подробно описаны философские аспекты, базовая техника и боевое применение Тайцзицюань. Книга содержит большое количество иллюстраций с подробным изложением особенностей техники стиля и будет полезна всем изучающим боевые искусств. Содержание: Введение Философские аспекты прикладной техники Тайцзицюань Базовая техника Тайцзицюань Упрощённая форма Тайцзицюань стиля У Заключение', 108, 108);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('8e79d41a-e00e-489a-9b7f-c9ef093e36fd', '2018-01-30 00:00:00.000000', 175, 23, 0, 'bQnUDAAAQBAJ', 'Розовый дракон. У каждого человека свой дракон, пожирающий его изнутри...', 'http://books.google.com/books/content?id=bQnUDAAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'У каждого человека свой дракон, пожирающий его изнутри. Зависть, алчность, злоба – вот лишь некоторые из тех чувств, медленно разрушающих человека. Но может ли любовь стать таким разрушающим чувством? Из любви к своему умершему сыну мастер создаёт кукол-драконов, в каждой из которых заключена частичка души его умершего ребёнка, не задумываясь о последствиях. Из любви к крестьянину ведьма решит навсегда изменить свою жизнь, а спустя некоторое время, убьёт всех жителей деревни из той же любви.', 100, 90);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('7d30dfca-ef96-43cb-a353-7c0726728aa8', '2022-04-16 00:00:00.000000', 873, 23, 0, 'ewYIDAAAQBAJ', 'Все началось у алтаря', 'http://books.google.com/books/content?id=ewYIDAAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Искусная портниха Клер, изготовив на заказ свадебное платье для лучшей подруги, случайно забывает его в аэропорту и приходит в отчаяние. Все осложняется ссорой с братом подруги, Колином, с которым у нее и без того непростые отношения. Колин и Клер слишком разные, но совместная ночь, полная страсти, заставляет их пойти на сближение...', 111, 100);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('f34d8300-95fd-44fd-9390-534ec5b487fa', null, 494, 30, 0, 'w5j9AgAAQBAJ', 'Жизнь птиц у нас дома', 'http://books.google.com/books/content?id=w5j9AgAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', null, 720, 720);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('35bbaa50-701f-4d75-8793-e6976c6408ac', '2022-05-13 00:00:00.000000', 139, 38, 1, 'zpTBDwAAQBAJ', 'Теория и методика развития речи у детей. Дословесный период 2-е изд. Учебное пособие для вузов', 'http://books.google.com/books/content?id=zpTBDwAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Работа посвящена психологическим и психолингвистическим закономерностям развития дословесной коммуникации как предпосылке словесной речи у детей от двух месяцев до двух лет. Рассматривается система средств дословесной коммуникации – протоязык, его периодизация, развитие и психологическая природа протознаков на различных стадиях (жесты, мимика, взгляд, вокализации). Приводятся результаты экспериментальных исследований генезиса языковых функций, тема-рематических отношений и закономерностей развития диалога в дословесной коммуникации. Обсуждаются условия развития дословесной коммуникации у глухих и нормально слышащих детей. Соответствует актуальным требованиям Федерального государственного образовательного стандарта высшего образования. Для студентов и преподавателей психологических и филологических факультетов, факультетов дошкольного воспитания, дефектологов и воспитателей детей раннего возраста.', 761, 685);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('15f41dfc-d198-444f-b114-dbd7aa0d7f0e', '2019-02-22 00:00:00.000000', 873, 23, 0, 'vbSDAQAAQBAJ', 'У страха глаза велики', 'http://books.google.com/books/content?id=vbSDAQAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', '«Жили-были бабушка-старушка, внучка-хохотушка, курочка-клохтушка и мышка-норушка.Каждый день они ходили за водой. У бабушки были вёдра большие, у внучки – поменьше, у курочки – с огурчик, у мышки – с напёрсточек...»', 25, 22);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('c73ff9bd-8ed2-4e5d-99d9-a6af6ab38342', '2013-07-07 00:00:00.000000', 94, 23, 0, 'Lk7lNjZtH3YC', 'Мальчик у Христа на елке', 'http://books.google.com/books/content?id=Lk7lNjZtH3YC&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Короткий рассказ. Показана бедность, "дно" нищенской жизни. Маленький мальчик просыпается в подвале от дикого зимнего холода. Пытается погреться об маму, но она уже остыла (умерла прямо во сне от болезни и холода). Мальчик выходит на улицу, на лютый мороз. Дело было под Рождество.', 33, 33);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('ee4d95ba-f2dc-425a-94fa-783891785c63', '2017-09-05 00:00:00.000000', 485, 23, 0, 'nUxF60prWi4C', 'Империя Ч', 'http://books.google.com/books/content?id=nUxF60prWi4C&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Русские Ромео и Джульетта на Востоке.В Иокогаме. В Сибири. В Шанхае.Моряк Василий и девочка Лесико. Странники. Скитальцы.Знаменитая шанхайская певица Лесико Фудзивара, русская эмигрантка, помнит все.Она глядит через моря разлуки. Через горы ужаса и торжества.Она знает: когда-нибудь седой Василий встретит ее – и не увидит ее морщин.Они обнимутся, понимая: нет времени.Они узнают вкус вечной любви. Ожог любимой руки. Свет единственных глаз.', 25, 22);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('d6364bcc-6791-4105-914a-ca682fdb9a7a', '2017-09-05 00:00:00.000000', 51, 22, 1, 'CazdCwAAQBAJ', 'Методы исследования нутритивного статуса у детей и подростков', 'http://books.google.com/books/content?id=CazdCwAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'В монографии подробно описываются методы оценки физического развития и его нарушений, обсуждаются основные причины развития трофологических нарушений, приводятся как рутинные, так и современные методы исследования. Пособие снабжено большим иллюстративным материалом и нормативными данными и предназначено для участковых врачей-педиатров, нутрициологов, гастроэнтерологов, эндокринологов, врачей общей практики, слушателей факультетов повышения квалификации.', 187, 168);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('9f493826-622e-4041-9d96-4e5b8c795ae1', '2021-01-15 00:00:00.000000', 775, 3, 0, '7KgUEAAAQBAJ', 'Формирование звукопроизношения у дошкольников с тяжелыми нарушениями речи (ТНР). Этап работы со звуками среднего онтогенеза', 'http://books.google.com/books/content?id=7KgUEAAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'В пособие включен речевой и наглядный материал по коррекции нарушений формирования звукопроизношения у детей дошкольного возраста с тяжелыми нарушениями речи (ТНР) на этапе среднего онтогенеза. В пособии рассматривается произношение твердых и мягких переднеязычных смычно взрывных и смычно проходных согласных звуков, гласного звука [Ы], дефекты произношения свистящих звуков и звуков [ЛЬ], [Й].Речевой материал может быть использован для автоматизации звуков у детей, имеющих выраженные нарушения слоговой структуры слова и симптоматику дизартрического расстройства.Иллюстративный материал используется для многократного проговаривания слов ребенком совместно со взрослым и дальнейшего самостоятельного называния картинок.Пособие адресовано логопедам, воспитателям дошкольных образовательных организаций и родителям для занятий по формированию звукопроизношения у дошкольников с ТНР на раннем этапе развития.', 157, 141);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('ce484551-2484-4f32-878e-c025fd6b6117', '2017-02-07 00:00:00.000000', 873, 23, 0, 'wMATDgAAQBAJ', 'У ангела', 'http://books.google.com/books/content?id=wMATDgAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Три повести (которые сама Дина Рубина именует маленькими повестями) были написаны в первое и самое трудное десятилетие эмиграции. Абсолютно разные, они имеют некий общий знак, как именную печать художника, – образ ангела; странный, парадоксальный, порой чуть ли не издевательский. «Образ ангела, литературного ангела, ангела-хранителя, просто прохожего и едва ли не бомжа – один из моих тотемов в прозе, очень значимый для меня». Так, в повести «Камера наезжает!» ангел-хранитель предстает в образе лагерного охранника, того, что при попытке героини к бегству «из зоны, именуемой «жизнью», хватает ее и тащит по жизненному этапу. А в повести «Во вратах Твоих» героине без ангела не выжить: опасна и трудна жизнь человека, врастающего в новую родину. Вот только там ангел является в образе карнавальном, утешая и веселя. Внезапно оказавшаяся на границе жизни и смерти героиня «Высокой воды венецианцев» поселяется в отеле «Аль Анжело», ставшем для нее судьбоносным. Есть, есть Тот, кто посылает нам спасение. Повести 90-х поражают разнообразием тональностей и ритмов: лирические отступления чередуются с экспрессивными монологами, стремительная фабула – с философскими размышлениями, синкопы диалектов – с плавностью правильной речи.', 323, 291);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('6d5ce69f-4d0b-4f6f-a6d5-29828540b7c4', '2021-06-17 00:00:00.000000', 178, 26, 0, 'O-zTDAAAQBAJ', '«У ворот английского могущества». А. Е. Снесарев в Туркестане, 1899–1904.', 'http://books.google.com/books/content?id=O-zTDAAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Настоящее издание – научная биография выдающегося представителя русской школы военного востоковедения А. Е. Снесарева (1865–1937). В книге рассматривается туркестанский период (1899–1904) жизни А. Е. Снесарева, который имел важное значение для его формирования как военачальника и исследователя стран Востока. На основе архивных документов раскрываются многие эпизоды службы А. Е. Снесарева в Туркестане: поездка в Индию, рекогносцировки по Туркестанскому краю, командование Памирским отрядом, поездка в Великобританию, работа с британскими военными атташе. Значительное внимание уделено эволюции военных и военно-востоковедных взглядов А. Е. Снесарева, а также малоизвестным эпизодам его личной жизни. Многие архивные материалы и фотодокументы из российских и британских государственных и личных собраний вводятся в научный оборот впервые. Книга выходит в свет к 150-летию со дня рождения А. Е. Снесарева.', 500, 450);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('3daa035f-5315-413d-8314-116773dde543', '2017-06-06 00:00:00.000000', 178, 26, 0, '9zUrDwAAQBAJ', 'У ворот английского могущества', 'http://books.google.com/books/content?id=9zUrDwAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Настоящее издание – научная биография выдающегося представителя русской школы военного востоковедения А.Е. Снесарева (1865–1937). В книге рассматривается туркестанский период (1899–1904) жизни Снесарева, который имел важное значение для его формирования как военачальника и исследователя стран Востока. На основе архивных документов раскрываются многие эпизоды службы А.Е. Снесарева в Туркестане: поездка в Индию, рекогносцировки по Туркестанскому краю, командование Памирским отрядом, поездка в Великобританию, работа с британскими военными атташе. Значительное внимание уделено эволюции военных и военно-востоковедных взглядов А.Е. Снесарева, а также малоизвестным эпизодам его личной жизни. Многие архивные материалы и фотодокументы из российских и британских государственных и личных собраний вводятся в научный оборот впервые. Книга выходит в свет к 150-летию со дня рождения А.Е. Снесарева.', 445, 400);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('920bd9bb-f4c6-433d-8b0d-385f044509ed', '2018-01-26 00:00:00.000000', 959, 3, 0, 'RxNJDwAAQBAJ', 'Культура межличностных отношений у детей дошкольного возраста на занятиях по изобразительному искусству', 'http://books.google.com/books/content?id=RxNJDwAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'В пособии рассматривается методика актуализации нравственного потенциала личности средствами изобразительного искусства в период дошкольного детства, а также психолого-педагогические условия общего социокультурного и нравственного развития детей раннего и дошкольного возраста. Представлены конспекты занятий по изобразительному искусству, направленные на формирование культуры межличностных отношений у детей дошкольного возраста.Пособие обеспечивает теоретическим и практическим материалом ряд элективных курсов, направленных на практическую реализацию ФГОС дошкольного образования.Представленная методика дополняет содержание учебных дисциплин «Дошкольная педагогика» и «Теория и методика изобразительной деятельности детей дошкольного возраста».Пособие адресовано учителям, реализующим ФГОС дошкольного образования, а также студентам бакалавриата по направлению подготовки 050100.62 «Педагогическое образование (дошкольное образование)».', 373, 336);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('faf76d6d-3318-4010-9e2f-1f155d190fe1', null, 634, 31, 1, 'S3D9AgAAQBAJ', 'Теория графов', 'http://books.google.com/books/content?id=S3D9AgAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Монография крупного канадского математика, содержащая перспективные методы и конструкции современной теории графов (связность, факторизация, раскраска, планарность и др.). Многие результаты принадлежат автору, активно работающему в области комбинаторной теории. Книга вышла в известной серии "Энциклопедия математики и её приложения", ряд томов которой издан на русском языке в издательствах ""Мир" и "Наука".Для математиков различных специальностей, инженеров-исследователей, аспирантов и студентов, специализирующихся в области дискретной математики.', 1368, 1368);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('13114fbc-8cab-437a-8133-8256b21bf4ad', '2018-01-31 00:00:00.000000', 238, 24, 1, 'RbxEDwAAQBAJ', 'Проблемы коммуникации у Чехова', 'http://books.google.com/books/content?id=RbxEDwAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Книга А. Д. Степанова «Проблемы коммуникации у Чехова», творчески развивая теорию Бахтина, впитав лучшие достижения методологии структурализма в его тартуско-московском изводе, достижения современной лингвистики, теории коммуникации, информатики, является в известном смысле этапной в изучении поэтики Чехова. Отныне мимо нее не пройдет ни один серьезный исследователь. (А. П. Чудаков)', 125, 112);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('7afe65f9-6828-48fe-aa0e-8cb101a49be4', '2014-02-04 00:00:00.000000', 93, 18, 0, 'VYfKAgAAQBAJ', 'У стен града невидимого', 'http://books.google.com/books/content?id=VYfKAgAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Михаил Михайлович Пришвин — замечательный отечественный писатель середины прошлого века, «певец русской природы», как назвал его К.С. Паустовский. Заядлый путешественник, страстный охотник, фотограф и краевед Пришвин всю силу своего литературного таланта посвятил описанию неброской, но такой милой нашему сердцу красоты средней полосы России.', 33, 33);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('a1fa4a60-02aa-44bc-818f-efbd13542ad9', '2022-03-31 00:00:00.000000', 490, 46, 0, 'wZ2-DwAAQBAJ', 'Игры, задания, конспекты занятий по развитию письменной речи у младших школьников. Практическое пособие для учащихся, учителей, логопедов и родителей', 'http://books.google.com/books/content?id=wZ2-DwAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Дети есть дети, игровая деятельность для них – необходимая стихия. Ребенок, играя, не только повышает уровень умственного развития, но и подсознательно участвует в коррекционном процессе.Предложенные в книге игры: «Улитка», «Змейка», «Путешествие по предложениям», «Загадки времен года», «Паутинка» и др. – можно использовать как в работе со школьниками, у которых имеются нарушения письма, так и со школьниками, допускающими «ошибки роста» (т. е. ошибки, которые связаны с неполным усвоением правил русского языка), а также со всеми учениками для улучшения владения устной и письменной речью.Применять игры могут логопеды школьных логопунктов, учителя начальной школы, родители, тифлопедагоги для работы с учащимися коррекционных (зрительных) классов. Большинство игр рассчитано на читающих детей в возрасте 7-10 лет.2-е издание, исправленное', 406, 365);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('0ef86c7e-d1d5-4596-a596-387bae573d12', '2017-09-05 00:00:00.000000', 558, 22, 0, 'GfGdCgAAQBAJ', 'Амбулаторная эхокардиография у детей', 'http://books.google.com/books/content?id=GfGdCgAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'В руководстве представлена методика эхокардиографического обследования детей и подростков. Отражен стандартный ход ультразвукового исследования сердца, описаны современные эхокардиографические технологии. Книга может служить пособием для самостоятельного обучения, а врач, уже владеющий эхокардиографией, найдет в ней полезный материал по частным вопросам ультразвукового исследования сердца при различной патологии.Руководство предназначено для детских кардиологов, врачей функциональной и ультразвуковой диагностики, а также для педиатров.', 750, 675);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('338d8c1a-d26b-4e31-9b22-ead924a60e32', null, 70, 30, 0, 'sJ79AgAAQBAJ', 'Поляризованный свет', 'http://books.google.com/books/content?id=sJ79AgAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'в ней обсуждаются сущность явления поляризации и способы ее математического описания, рассматриваются методы экспериментального получения и исследования поляризованного света, а также его многочисленные применения. Она содержит ценный материал, до сих пор отсутствовавший в книгах по оптике.', 456, 456);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('38df7177-17e6-4499-b4ee-c07f9939aec8', '2010-11-10 00:00:00.000000', 222, 22, 0, 'Wg7CXdHAwWQC', 'Справочник красоты', 'http://books.google.com/books/content?id=Wg7CXdHAwWQC&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'В своей новой книге академик У ВэйСинь открывает нам секреты молодости, красоты и долголетия. В справочнике подробно описаны методы лечения косметологических проблем с помощью природных материалов, с глубокой древности применяемых традиционной китайской медициной: растений, эфирных растительных масел, а также лечение пищей. В книге представлены более 500 уникальных косметических рецептов, хранимых в Китае на протяжении тысячелетий, бережно собранных автором и изложенных на страницах данного издания с практическими рекомендациями по их применению. Схемы лечения и рецепты составлены авторами и не имеют аналогов в мировой литературе, в том числе китайской, и в медицинской практике. Это объемное и содержательное издание предназначено не только для врачей и специалистов, желающих познать китайскую медицину и овладеть ее эффективными методами, но и для широкого круга читателей, интересующихся этим феноменом мировой науки и культуры.', 81, 73);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('3b5c5619-65ce-4ac1-8f07-8c120c5a059e', '2017-09-05 00:00:00.000000', 221, 23, 1, 'ARHBAAAAQBAJ', 'У жадности в плену', 'http://books.google.com/books/content?id=ARHBAAAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Случай сыграл с Андреем и Алеком скверную шутку: прихватив по ошибке в кафе чужой кейс, они обнаружили в нем сто тысяч долларов. С этого дня их жизнь превратилась в сплошной кошмар. Владелец «дорогого» кейса, естественно, не может смириться с такой потерей и посылает своих отморозков найти ребят и вернуть деньги. Но Андрей с Алеком снова оказались во власти случая: доверившись приятелю, они потеряли баксы, зато получили реальную возможность расстаться с жизнью, так как бандиты их уже вычислили. Бодигард Евгения Охотникова, нанятая Андреем, пытается отыскать злополучные деньги, но лишь усугубляет ситуацию. Бандиты, получив свои доллары, свидетелей не оставят...', 124, 112);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('6ca0545d-8d8e-47cc-80cc-b9109a150a32', '2021-07-10 00:00:00.000000', 378, 45, 0, '4pcUEAAAQBAJ', 'Я у себя одна', 'http://books.google.com/books/content?id=4pcUEAAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Вырвите с корнем мысль "это невозможно!" – зачем держать в уме такие сорняки? Пока женщина чувствует себя женщиной, ей никогда не поздно начать все сначала! Для Вас 16 бесценных жизненных уроков. Итак, УРОК No1 Наслаждайся мной. – Твое настоящее!', 248, 223);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('ef49169d-d426-4343-b360-9b60f6806f98', '2017-09-05 00:00:00.000000', 833, 30, 1, 'LAQqDwAAQBAJ', 'Забота о потомстве у грызунов: физиологические, этологические и эволюционные аспекты', 'http://books.google.com/books/content?id=LAQqDwAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'В монографии обобщены результаты многолетних полевых и лабораторных исследований автора, проведенных в 1976-2012 гг. и дополненных литературными данными о поведении более 40 видов грызунов. Рассмотрены физиологические и этологические механизмы формирования и регуляции родительского поведения, а также его связь с пространственно-этологической структурой популяций грызунов и формированием феномена социальности в эволюционном аспекте.Для широкого круга зоологов, экологов и этологов, а также студентов биологических специальностей.', 205, 184);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('29429d86-0e02-4ac2-8d80-1975c03ef1d8', '2018-02-06 00:00:00.000000', 461, 23, 1, 'bxdLDwAAQBAJ', 'На даче у бабушки', 'http://books.google.com/books/content?id=bxdLDwAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', '«На даче у бабушки» — произведение русского писателя Бориса Степановича Житкова (1882–1938).*** «На даче у бабушки» — рассказ Бориса Житкова для самых маленьких из цикла «Что я видел». Маленький Алеша в восторге от жизни у бабушки на даче: кошки, подарки, пристань, пароход — все так интересно! Борис Степанович Житков — автор популярных рассказов для детей, приключенческих рассказов и повестей на морскую тематику и романа о событиях революции 1905 года. Перу Бориса Житкова принадлежат такие произведения: «Зоосад», «Коржик Дмитрий», «Метель», «История корабля», «Мираж», «Храбрость», «Черные паруса», «Ураган», «Элчан-Кайя», «Виктор Вавич», другие. Борис Житков, мастерски описывая любые жизненные ситуации, четко определяет полюса добра и зла, верит в торжество справедливости. Писатель даже в обычной повседневности видит неповторимую красоту жизни, побуждая читателя быть внимательным к окружающему миру, пытаться разгадать загадки природы, заботиться о «братьях наших меньших». По мотивам произведений Бориса Житкова сняты мультипликационные фильмы для самых маленьких, художественные фильмы. И сегодня творчество Б. Житкова не утратило своей познавательной и эстетической ценности, учит любить жизнь во всех ее проявлениях.', 3, 3);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('a35ec0a0-e895-4f0a-a1b0-4b95f7d38737', '2018-04-14 00:00:00.000000', 294, 4, 1, '6KZVDwAAQBAJ', 'Варшавское восстание 1944 г. Движение Сопротивления в Польше 1939-1945 гг. Направления. Программы. Практика. Результаты. Часть 1', 'http://books.google.com/books/content?id=6KZVDwAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'В книге показаны все направления польского антигитлеровского Сопротивления в годы Второй мировой войны, при особом внимании на Армию Крайову и Гвардию (Армию) Людову, проблемы 1944 г. Работа написана на основе подпольной печати, документов отечественных и зарубежных архивов и историографии проблемы, рассчитана на отечественных знатоков и любителей истории.', 250, 225);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('964f0766-b6f9-4ba4-b983-b397cb4624e3', '2022-10-24 00:00:00.000000', 265, 11, 0, 'FT-XEAAAQBAJ', 'Мы все немного у жизни в гостях', 'http://books.google.com/books/content?id=FT-XEAAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', '«Ахматова – жасминный куст, обугленный туманом серым», – так говорили о поэтессе современники. Её считали совершенством. Её стихами зачитывались. Её сравнивали с античными богинями.Она родилась на рубеже двух веков – «железного» XIX и жестокого XX, – соединив их незримой нитью своей трагической судьбы.Лирика Анны Ахматовой зародилась в лоне «Серебряного века», когда на поэтическом небосклоне уже сияли звёзды Есенина, Цветаевой, Маяковского и многих других великих мастеров слова, в чьём творчестве нашла вдохновение Ахматова.Анна Андреевна – великий поэт с трагическим мироощущением, проницательный художник, человек стальной воли и непреклонного мужества, с непоколебимым чувством собственного достоинства и бескомпромиссной совестью. Она застала великую эпоху «смены времен» и масштабных революционных потрясений, события которой окрасили ее лирику в тёмные тона.В сборник «Мы все немного у жизни в гостях», посвящённый творчеству поэтессы, вошли произведения, авторы которых затрагивают значимые философские темы: любви, дружбы, войны, патриотизма и необратимости судьбы.', 186, 167);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('35d4c679-6d17-40e8-abb2-ca9ecfc6da17', '2022-03-27 00:00:00.000000', 472, 3, 0, '2k1mEAAAQBAJ', 'Психосоматика у детей. 9 шагов к здоровью', 'http://books.google.com/books/content?id=2k1mEAAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Ольга Шубенкова – практикующий психолог, помогает людям обрести внутреннюю гармонию и решить проблемы со здоровьем через работу с подсознанием и негативными установками мышления. Она – мамы двоих детей, которая прошла путь от двух сложных беременностей и угрозы эпилепсии старшей дочери до полного выздоровления, и счастливой жизни без лекарств и с минимальными медицинскими вмешательствами.В книге автор расскажет о том, как влияют состояния мамы и окружающее пространство на здоровье ребёнка, и как можно исцелять через работу с подсознанием.Ольга делится инструментами для восстановления детского здоровья из области психологии, в том числе психосоматики, нейробиологии, тетахилинга и дизайна человека. Также дает практики на работу с телесными блоками. Все эти методы помогли автору и более сотни ее клиентов решить проблемы со здоровьем детей. Избавиться от аллергии, частых простуд, проблем с речью и других болезней.В формате PDF A4 сохранен издательский макет.', 398, 358);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('1f3af59b-e7df-484e-9bee-a20b10cea1dc', '2015-07-24 00:00:00.000000', 238, 23, 0, 'XOE-CgAAQBAJ', 'Отель «У призрака»', 'http://books.google.com/books/content?id=XOE-CgAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Вот и настал День тринадцатого снега! Самый главный праздник в году! Да здравствуют карнавалы, ёлки-палки, сломанные ноги и всеобщее веселье! Вот только полиции городка Мокрые Псы, как всегда, не до отдыха. Террористы из «Хвала Амбар», странные трупы в канализации, интриги местных поэтов... да мало ли? А в далёкой Полякии вообще возобновляет работу сакральный клуб самоубийц-любителей. Подтянутый и бескомпромиссный сержант Ирджи Брадзинский вновь встаёт на пути преступников. Чёрт-полицейский – это всегда больше чем просто чёрт. И даже таинственные смерти в отеле «У призрака» его не остановят!', 220, 198);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('6e30f397-cf88-4009-ba0e-8a643b8c9220', '2022-04-16 00:00:00.000000', 873, 23, 0, 'dzFDDwAAQBAJ', 'У подножия Монмартра', 'http://books.google.com/books/content?id=dzFDDwAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Журналистка Хелена Фоласаду, конечно, должна была сказать «нет», когда незнакомец в кафе спросил, не месье ли Белливье она ждет. Но творческий застой и природная авантюрная жилка заставили ее сказать «да». Так она получила высокооплачиваемую и весьма странную работу, вызывающую ощущение причастности к тайне. Выходец из Туниса Мансебо, владелец маленького магазинчика в окрестностях Монмартра, ведет размеренную жизнь. Но однажды к нему обращается соседка из дома напротив и предлагает проследить за ее неверным мужем. К своему удивлению, Мансебо соглашается. Хелена и Мансебо прежде никогда не встречались, но их миссии пересекутся самым удивительным образом. И как только это произойдет, станет ясно, что кафе и улочки Парижа хранят куда больше секретов, чем можно было бы предположить...', 361, 325);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('fc9887ca-8ef9-4ba3-8bf0-e506f8496b01', '2021-09-02 00:00:00.000000', 734, 23, 0, 'isZAEAAAQBAJ', 'Время «Ч»', 'http://books.google.com/books/content?id=isZAEAAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Книги Брэдбери будут находиться всегда как бы «между прошлым и будущим», но тем не менее оставаться «безвременными». Творческое наследие Брэдбери по-прежнему одновременно вдохновляет и пугает людей, как любая хорошая литература. «Время «Ч» - это пугающе реалистичный и нестандартный рассказ о том, как инопланетные захватчики нашли лазейку «на задний двор» землян.', 15, 15);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('dc3c16ce-7876-40b2-93e7-946653e8daa0', '2022-05-13 00:00:00.000000', 900, 30, 0, 'GCgkEAAAQBAJ', 'Ботаника. Экология растений в 2 ч. Часть 1 2-е изд., испр. и доп. Учебник для бакалавриата и магистратуры', 'http://books.google.com/books/content?id=GCgkEAAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Учебник состоит из двух частей. В первой части представлены вопросы взаимодействия растений, их популяций и сообществ со средой, а также факторы, оказывающие влияние на эти процессы. В учебнике по каждой из рассматриваемых глав предлагаются вопросы для самопроверки и контроля, а также практические задания. Всем, кто хочет более основательно изучить проблемы экологии растений, будет полезен дополнительный список рекомендуемой литературы, приведенный на сайте электронной библиотечной системы «Юрайт» (biblio-online.ru).', 1548, 1393);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('571dc5dd-faab-4c5b-b0a0-28ee6a223623', '2022-05-13 00:00:00.000000', 33, 38, 0, 'qikkEAAAQBAJ', 'Физика в 2 ч. Часть 1 2-е изд., испр. и доп. Учебник и практикум для СПО', 'http://books.google.com/books/content?id=qikkEAAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'В настоящем учебнике сформулирован необходимый теоретический минимум и даны методические рекомендации по решению задач по физике. Все основные определения и законы вводятся на примере решения задач, а затем на других примерах демонстрируются принципы их использования. Особенностью учебника является включение в него исторических сюжетов о физиках и физике, которые позволяют понять, как рождалась современная физика. Учебник написан ведущими педагогами-физиками Национального исследовательского ядерного университета «МИФИ», являющегося лидером инженерно-технического и ядерного образования в нашей стране.', 1173, 1056);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('067444c4-e5e4-4bb7-98ca-98812c55d698', '2022-05-13 00:00:00.000000', 502, 38, 1, 'MSybDwAAQBAJ', 'Клиническая фармакология в 2 ч. Часть 2. Учебник и практикум для СПО', 'http://books.google.com/books/content?id=MSybDwAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Во второй части учебника «Клиническая фармакология» представлены разделы, посвященные вопросам клинической фармакологии лекарственных средств, используемых при аллергических заболеваниях, вирусных и грибковых инфекциях, заболеваниях желудочно-кишечного тракта. Представлены принципы фармакотерапии инфекционно-воспалительных заболеваний женской половой сферы и мочевыводящих путей, ревматических заболеваний, сахарного диабета и эпилепсии. Описаны клинико-фармакологические подходы к выбору лекарственных средств при неотложных состояниях. Отдельная глава посвящена клинической фармакологии витаминов. Частные вопросы клинической фармакологии изложены по отдельным синдромам и заболеваниям. При этом описанию отдельных препаратов предшествуют данные об этиологии, патогенезе и клинических особенностях наиболее распространенных болезней. Соответствует актуальным требованиям Федерального государственного образовательного стандарта среднего профессионального образования и профессиональным требованиям. Для студентов фармацевтических и медицинских специальностей. Представленные сведения важны практикующим врачам, слушателям курсов повышения квалификации, специалистам при решении вопросов рационального использования лекарственных препаратов в лечебном учреждении.', 1511, 1360);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('82916117-0309-4013-a7ef-f85878f7154f', '2022-05-13 00:00:00.000000', 337, 31, 0, 'TDYkEAAAQBAJ', 'Теория и методика обучения математике: частная методика в 2 ч. Часть 1 2-е изд., испр. и доп. Учебное пособие для вузов', 'http://books.google.com/books/content?id=TDYkEAAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Учебное пособие содержит методики преподавания отдельных разделов математики. Представлены различные возможные подходы к изложению основных разделов школьного курса математики, дан их сравнительный анализ. Помимо методических рекомендаций приведены математическое содержание основных дидактических единиц, примеры применения методических схем, приемов, методов решения задач. К каждой лекции даны вопросы и задания для самостоятельной работы, а также список рекомендуемой литературы для более глубокого и всестороннего изучения рассматриваемых вопросов. Учебное пособие состоит из двух частей. В первой части рассмотрены основные вопросы частной методики обучения математике в 5—9 классах общеобразовательной школы. Вторая часть представляет собой курс лекций по частной методике обучения математике в старших классах.', 973, 876);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('26286dd0-b175-4d4e-98c3-ff301f096fee', '2022-05-13 00:00:00.000000', 418, 31, 0, 'kikkEAAAQBAJ', 'Теоретическая и математическая физика. Сборник задач в 2 ч. Часть 2 3-е изд., пер. и доп. Учебное пособие для академического бакалавриата', 'http://books.google.com/books/content?id=kikkEAAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Представленный сборник задач по курсу теоретической и математической физике имеет в своей основе цель научить студентов самостоятельно решать некоторый класс задач из довольно широкого круга физических и математических дисциплин. Учебное пособие включает в себя более ста задач по теоретической и математической физике. Большое внимание уделено физической постановке задач и приводится их подробное математическое решение с полным анализом. Пособие состоит из двух частей. Вторая часть включает в себя задачи по квантовой механике и статической физике.', 948, 853);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('ad011d04-a3e2-4aab-8580-6b3d6757b74b', '2022-05-12 00:00:00.000000', 873, 51, 1, 'UfJeDwAAQBAJ', 'Реклама и опросы общественного мнения в США в 2 ч. Часть 1. Научная реклама 2-е изд., пер. и доп. Монография', 'http://books.google.com/books/content?id=UfJeDwAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Центральными темами данной книги являются история опросов общественного мнения и возникновение рекламы в США. Большое внимание автором уделяется изучению жизни и творчества выдающихся аналитиков общественного мнения XX века. В первой части освещается становление американской научной рекламы, основное внимание уделяется изучению творчества копирайтеров и ученых, которые первыми задались вопросом о природе рекламы.', 1048, 943);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('ef4984c8-6dae-49e2-bf73-5c77bbdf9e73', '2022-05-13 00:00:00.000000', 317, 0, 0, 'qFAkEAAAQBAJ', 'Судебные речи известных русских юристов. Сборник в 2 ч. Часть 1 2-е изд., испр. и доп', 'http://books.google.com/books/content?id=qFAkEAAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'В двухтомник включены речи семи выдающихся ораторов русской присяжной адвокатуры. Каждый из этой «великолепной семерки» удостоился высоких оценок еще при жизни, на примерах их защитительных речей построены методические рекомендации многих научных работ по ораторскому искусству. Они различались профессиональными пристрастиями, тяготением к разным категориям дел, природными способностями к импровизации или интуитивному предвидению всех опасных поворотов процесса, особенностями ораторских приемов. Объединяли корифеев любовь к профессии, верность адвокатскому долгу и неустанный труд, развивавший их природный художественный дар, либо сделавший их выдающимися ораторами благодаря непрерывной работе над словом. В истории каждой страны есть люди, которые поистине являются ее национальным достоянием. Элита присяжной адвокатуры гордость России.', 1323, 1191);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('8aa014e1-b981-4e65-9130-97297d11c799', '2022-05-13 00:00:00.000000', 687, 4, 1, 'rCQkEAAAQBAJ', 'Всемирная история в 2 ч. Часть 2. История нового и новейшего времени. Учебник для академического бакалавриата', 'http://books.google.com/books/content?id=rCQkEAAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'В этом замечательном учебнике с учетом современных методологических подходов и оценок рассмотрены основные события и тенденции социально-экономического, политического и духовного развития важнейших цивилизационных центров мира. Обозначены наиболее важные проблемы истории Запада и Востока на различных ее этапах. Предложен богатый практикум – контрольные вопросы и задания, темы докладов, рекомендована соответствующая проблематике учебная и научная литература. Книга предназначена для студентов, обучающихся по гуманитарным направлениям и специальностям, а также для всех читателей, интересующихся всемирной историей.', 1336, 1202);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('3bbab8d1-41c1-4f62-99f7-0ea3d2966f27', null, 98, 30, 0, 'kH0GAwAAQBAJ', 'Ч. Дарвин. Происхождение видов', 'http://books.google.com/books/content?id=kH0GAwAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Настоящее издание «Происхождение видов» осуществлено по русскому тексту, изданному в 1907 году («Иллюстрированное собрание сочинений», том 1,Москва, издание Ю. Лепковского). Помещенный в издании 1907 г. перевод является переизданием хорошо известного, лучшего русского перевода под редакцией К. А. Тимирязева (К. А. Тимирязев перевел исторический очерк, введение, гл. I—VII и XV; М. А. Мензбир—гл. VIII, XII, XIII, XIV и часть VII; А. П. Павлов—гл. X и XI; И. А. Петровский—гл. IX). Этот перевод был тщательно сверен для настоящего издания с английским подлинником А. Д. Некрасовым и С. Л. Соболем. О характере этой сверки и о многочисленных изменениях и исправлениях, внесенных в перевод, исчерпывающе изложено в третьем томе «Сочинений» издания Биомедгиза.', 456, 456);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('c386978b-a850-4ff8-b64b-ffcf25d412ad', '2022-05-12 00:00:00.000000', 238, 51, 0, 'gVRGEAAAQBAJ', 'Производственный менеджмент. Теория и практика в 2 ч. Часть 1 2-е изд. Учебник для вузов', 'http://books.google.com/books/content?id=gVRGEAAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Учебный курс содержит материалы, дающие комплексное представление о производственном менеджменте предприятия. Освещены теоретические и практические аспекты организации и управления производством, регламентации протекания производственных процессов. Рассмотрены особенности производственного планирования, управления материальными потоками и инновациями на предприятии, обеспечения его конкурентоспособности. Даны основы управления трудовыми ресурсами, организации труда и заработной платы. Представлена методика оценки результатов производственно-хозяйственной деятельности предприятия. Соответствует требованиям федерального государственного образовательного стандарта высшего образования. Для студентов бакалавриата, обучающихся по направлению «Менеджмент». Курс может быть полезен при подготовке магистров указанного направления, а также в системе дополнительного профессионального образования.', 1636, 1472);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('39d4f367-8012-43c6-b39f-d31a0b352df4', '2022-05-13 00:00:00.000000', 585, 38, 0, 'pEYkEAAAQBAJ', 'Французский язык в 2 ч. Часть 1 (A1—А2) 4-е изд., пер. и доп. Учебник и практикум для СПО', 'http://books.google.com/books/content?id=pEYkEAAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Курс формирует компетенции учащихся в объеме, предусмотренном требованиями стандарта среднего общего образования по французскому языку. Курс представлен в двух самостоятельных частях, имеющих разную структуру и цели. В первой части в доступной и удобной форме представлен базовый материал по фонетике, грамматике и лексике, необходимый для выработки навыков коммуникации на французском языке в ситуациях повседневного общения. Практикум серьезно дополняет основной курс, дает возможность варьировать объем и сложность изучаемого материала в зависимости от конкретных условий, обеспечить индивидуальный подход. Соответствует актуальным требованиям Федерального государственного образовательного стандарта среднего профессионального образования и профессиональным требованиям. Для студентов образовательных учреждений среднего профессионального образования, а также широкого круга лиц, приступающих к изучению французского языка с целью достижения уровня A2—В1.', 1661, 1495);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('34119869-98f8-4293-9227-04ec4b1d0fc3', '2022-05-12 00:00:00.000000', 65, 38, 0, 'sEYkEAAAQBAJ', 'Нейрофизиология: физиология ЦНС. В 2 ч. Часть 2 2-е изд., испр. и доп. Учебник для вузов', 'http://books.google.com/books/content?id=sEYkEAAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'В первой части рассматриваются такие базовые вопросы, как функциональная роль ЦНС и принципы управления, функциональная система Анохина, методы исследования ЦНС, морфология и физиология нейронов и нейроглии, общие закономерности функционирования ЦНС, морфология спинного и головного мозга, физиология большого мозга, мозговые оболочки и мозговое кровообращение. Вторая часть полностью посвящена 11 основным трансмиттерным системам мозга. Учебник предназначен для студентов бакалавриата, магистратуры и специалитета психологических, медицинских и биологических специальностей, а также для аспирантов и студентов факультетов физической культуры, спорта, адаптивной физической культуры.', 2423, 2423);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('d7d388cc-7031-4eed-a8f7-efeddb90425d', '2022-05-13 00:00:00.000000', 303, 49, 1, 'gEokEAAAQBAJ', 'Современный русский литературный язык в 2 ч. Часть 1. Учебник для вузов', 'http://books.google.com/books/content?id=gEokEAAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'В учебнике излагается систематический курс современного русского литературного языка. Авторы – известные ученые – представляют разные лингвистические школы – московскую и петербургскую. Теоретические положения сопровождаются сведениями, связанными с их реализацией на практике, и подкрепляются иллюстративным материалом. Издание включает ряд тем, ранее в учебниках вообще не затрагивавшихся (например, «Функциональная грамматика», «Активные процессы в современном русском языке»). Для студентов высших учебных заведений, обучающихся по гуманитарным, в том числе филологическим, специальностям.', 1411, 1270);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('268261a5-ad8b-4113-81a5-32a61ed1b134', '2022-05-13 00:00:00.000000', 567, 0, 1, 'gFIkEAAAQBAJ', 'Международное право в 2 ч. Часть 1 4-е изд., пер. и доп. Учебник для вузов', 'http://books.google.com/books/content?id=gFIkEAAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Курс состоит из двух частей. Содержание первой части охватывает базовый понятийно-терминологический аппарат международного права, стержневые проблемы его современного толкования и применения. Во второй части рассмотрены отрасли международного права. Целостное изложение общих и специальных вопросов международного права, в том числе сложных, сочетается с ясностью и доступностью текста. Соответствует актуальным требованиям Федерального государственного образовательного стандарта высшего образования. Для студентов юридических факультетов, обучающихся по направлениям и специальностям по программам академического бакалавриата, а также для аспирантов и преподавателей юридических вузов, практических работников МИД России, других федеральных органов власти, для всех читателей, кого интересуют глубокие знания международного права.', 1461, 1315);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('633f2758-1d8f-4db2-bcc5-05aa9d3dcf94', '2022-05-12 00:00:00.000000', 870, 51, 0, 'tEptDwAAQBAJ', 'Банковское дело в 2 ч. Часть 1 2-е изд., испр. и доп. Учебник для академического бакалавриата', 'http://books.google.com/books/content?id=tEptDwAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'В учебнике представлена универсальная система банковских операций, основы их организации, безопасности и правового обеспечения. Авторы показывают, что все многообразие пассивных и активных операций банка, включая собственные и привлеченные средства, кредиты, операции с ценными бумагами, сопровождается рисками. Изложение глав заканчивается вопросами для самопроверки и тестовыми заданиями. Учебник предназначен для студентов, преподавателей экономических дисциплин учебных заведений экономического профиля, а также будет интересен широкому кругу читателей, самостоятельно изучающих банковскую деятельность или пользующихся услугами банков.', 1036, 932);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('9cb958f6-7634-4263-8ea1-5ff9361ea422', '2022-05-12 00:00:00.000000', 643, 30, 0, 'sjMkEAAAQBAJ', 'Общая и неорганическая химия. В 2 ч. Часть 1. Теоретические основы 2-е изд., пер. и доп. Учебник и практикум для СПО', 'http://books.google.com/books/content?id=sjMkEAAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Учебник поможет студентам сформировать адекватную современному уровню знаний научную картину мира на основе знания законов химии, состава и строения вещества, химических и физико-химических свойств элементов и соединений. В первой части рассмотрены основы химической термодинамики, кинетики, свойства растворов, теория строения атома и химической связи, окислительно-восстановительные реакции и электрохимические процессы, свойства комплексных соединений. Вторая часть учебника посвящена химии s-, р-, d-элементов. Каждая глава содержит характеристику подгруппы, физические и химические свойства простых веществ и их соединений, сведения об их получении, применении, токсичности и экологической опасности. Учебник содержит образцы решения задач, контрольные вопросы и тесты. В Приложениях приведены все необходимые вспомогательные сведения. Для лучшего усвоения материала в учебник включен обширный лабораторный практикум. Лабораторные работы содержат индивидуальные задания с элементами научного исследования, проведением количественных расчетов и оценкой погрешности определений.', 811, 730);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('79464dcd-e810-4de0-b8f0-baed0ae06554', '2022-05-13 00:00:00.000000', 687, 4, 0, 'sCMkEAAAQBAJ', 'Всемирная история в 2 ч. Часть 1. История древнего мира и средних веков. Учебник для академического бакалавриата', 'http://books.google.com/books/content?id=sCMkEAAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'В этом замечательном учебнике с учетом современных методологических подходов и оценок рассмотрены основные события и тенденции социально-экономического, политического и духовного развития важнейших цивилизационных центров мира. Обозначены наиболее важные проблемы истории Запада и Востока на различных ее этапах. Предложен богатый практикум – контрольные вопросы и задания, темы докладов, рекомендована соответствующая проблематике учебная и научная литература. Книга предназначена для студентов, обучающихся по гуманитарным направлениям и специальностям, а также для всех читателей, интересующихся всемирной историей.', 598, 538);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('cdaa0118-dc3c-4a60-ad45-246b958dd361', '2022-05-13 00:00:00.000000', 687, 4, 0, 'nN2gDwAAQBAJ', 'Всеобщая история в 2 ч. Часть 1. История Древнего мира и Средних веков. Учебник для СПО', 'http://books.google.com/books/content?id=nN2gDwAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'В учебнике с учетом современных методологических подходов и оценок рассмотрены основные события и тенденции социально-экономического, политического и духовного развития важнейших цивилизационных центров мира. Обозначены наиболее важные проблемы истории Запада и Востока на различных ее этапах. Предложен богатый практикум – контрольные вопросы и задания, темы докладов, рекомендована соответствующая проблематике учебная и научная литература. Издание состоит из двух частей. В первую часть включены начало всемирной истории (становление первых цивилизаций Древнего мира) и мир в период Средних веков и раннего Нового времени (развитие Запада и Востока в V—XVI веках), во вторую – Запад и Восток в период раннего нового времени (конец XVI – XVII век), развитие всемирной истории в XVIII—XIX веках и мировое сообщество в Новейшее время. Соответствует актуальным требованиям Федерального государственного образовательного стандарта среднего профессионального образования и профессиональным требованиям. Для студентов, обучающихся по гуманитарным специальностям, а также для всех читателей, интересующихся всемирной историей.', 598, 538);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('91d0e74e-4d00-410c-92bb-3af488cc1fd4', '2022-05-13 00:00:00.000000', 116, 38, 1, 'LCIkEAAAQBAJ', 'Культура речи и деловое общение в 2 ч. Часть 1. Учебник и практикум для академического бакалавриата', 'http://books.google.com/books/content?id=LCIkEAAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Настоящий учебник всесторонне охватывает такие вопросы, как современная деловая риторика и этика делового взаимодействия; культура устной и письменной форм общения; роль делового общения в профессиональной деятельности, коммуникативная, перцептивная и интерактивная функции делового взаимодействия; вербальные (говорение и слушание) и невербальные средства делового общения, сенсорные каналы и типы деловых партнеров. Технологии общения и культуры речи рассмотрены через формирование у читателя коммуникативной, интерактивной, перцептивной и презентационной компетентностей, ораторского мастерства. Книга выгодно отличается тем, что в ней уделяется большое внимание кросс-культурным характеристикам общения, письменной и устной речи деловых людей, участию в конгресс-менеджменте, презентациям, переговорам, групповым и массовым акциям взаимодействия. Подача материала позволяет читателю поддерживать интерес и легко осваивать содержание. В книге даются практические рекомендации для достижения карьерного успеха в профессиональной деятельности, предложен коммуникативный практикум интерактивных технологий для диалогового и командного взаимодействия, вопросы и литература для контроля знаний.', 1086, 977);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('ed2a53b4-3f79-4234-971c-a0ee7709c60e', '2022-05-12 00:00:00.000000', 483, 31, 0, '8kkkEAAAQBAJ', 'Сборник задач по высшей математике в 4 ч. Часть 2. Учебное пособие для вузов', 'http://books.google.com/books/content?id=8kkkEAAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'В сборнике содержатся задачи по основам математического анализа, векторной алгебре и аналитической геометрии, линейной алгебре, дифференциальному и интегральному исчислениям функций одной и нескольких переменных, кратным интегралами дифференциальным уравнениям. Приведенные краткие теоретические сведения, иллюстрируемые большим количеством разобранных примеров, позволяют использовать сборник для всех видов обучения, а также для самостоятельной работы студентов. Сборник состоит из четырех частей. Во второй части задачника представлены линейная алгебра и математический анализ, в которых рассматриваются кратные интегралы и интегральное исчисление функции одной переменной, дифференциальное исчисление функций нескольких переменных и дифференциальные уравнения. Для студентов младших курсов высших технических учебных заведений.', 948, 853);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('a75bef94-7a0e-4c74-a7a9-b6172c37c7c6', '2022-05-13 00:00:00.000000', 17, 25, 0, 'SDIkEAAAQBAJ', 'Организация баз данных в 2 ч. Часть 2 2-е изд., испр. и доп. Учебник для вузов', 'http://books.google.com/books/content?id=SDIkEAAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'В учебнике систематически изложены идеи и методы, используемые при разработке и эксплуатации современных систем управления базами данных. Общетеоретический материал изложен применительно к одной из популярных СУБД Oracle. Приведено большое количество работающих примеров в Oracle, позволяющих студентам на практике овладевать приемами программирования в данной СУБД. Учебник состоит из двух частей. В первой части изложены вопросы создания объектов баз данных, во второй рассматриваются вопросы защиты и эффективности использования баз данных.', 1736, 1562);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('474dd607-b2b2-4922-8c3f-a333723c9a5e', '2022-05-13 00:00:00.000000', 873, 25, 0, 'njIkEAAAQBAJ', 'Компьютерные сети и телекоммуникации. Маршрутизация в ip-сетях в 2 ч. Часть 2. Учебник и практикум для СПО', 'http://books.google.com/books/content?id=njIkEAAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'В учебнике рассматриваются различные модели архитектуры сетей передачи данных, различные протоколы маршрутизации, а также алгоритмы, используемые в этих протоколах. Подробно разбирается работа как дистанционно-векторных, так и протоколов маршрутизации по состоянию канала. Описывается реализация, использование и оптимизация работы протоколов RIP, EIGRP, OSPF, маршрутизация в одной или нескольких зонах, совместная работа нескольких протоколов маршрутизации, создание и использование виртуальных каналов, управление трафиком маршрутных обновлений, маршрутные карты и маршрутизация по политикам. Учебник включает большое количество лабораторных работ, покрывающих все содержание курса. Материалы к лабораторным работам расположены в Электронной библиотечной системе «Юрайт» (biblio-online.ru).', 1548, 1393);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('37050c63-865b-4a7e-824c-f513fd4e7262', '2022-05-13 00:00:00.000000', 775, 19, 0, 'NjEkEAAAQBAJ', 'Теория культуры в 2 ч. Часть 2 2-е изд., испр. и доп. Учебное пособие для академического бакалавриата', 'http://books.google.com/books/content?id=NjEkEAAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'В учебном пособии с разных сторон рассмотрены теоретические аспекты дисциплины: представления о культуре, ее сущность, образование и развитие. Особое внимание уделено современному состоянию культуры и связанным с ним вопросам. Пособие написано на высоком научном уровне, но без излишнего наукообразия, понятным языком. Основной материал дополнен словарем терминов и обширным списком литературы. Учебное пособие состоит из двух частей. В первой части рассмотрены историческое развитие теоретических представлений о культуре, методологии и методы ее исследования, проблемы ее понимания, ценности, культурогенез, типология и разные типы культур. Во второй части изложены проблемы взаимодействия, семиотическая концепция, культура повседневности, пространство и время культуры, современная культура, глобализация.', 1173, 1056);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('671d376a-31f4-4390-911a-f5fa79c60bab', '2022-05-12 00:00:00.000000', 76, 44, 1, 'bFEkEAAAQBAJ', 'Архитектура ЭВМ и систем в 2 ч. Часть 1. Учебное пособие для вузов', 'http://books.google.com/books/content?id=bFEkEAAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'В пособии представлена информация о принципах построения и функционирования различных устройств современных компьютеров; о взаимодействии аппаратных и программных средств; о современных компьютерных технологиях; о конструктивном исполнении компьютерных устройств и комплектующих изделий; об основных тенденциях и направлениях развития современных компьютерных средств. Эта книга поможет хорошо разбираться в структурно-функциональной организации микропроцессоров, компьютеров, их систем, компьютерной памяти. Пособие дает целостное представление об основных концепциях и общих тенденциях развития компьютерной техники.', 1011, 910);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('f638317b-c499-4b5d-b4a7-31bad1a2fe00', '2022-05-13 00:00:00.000000', 263, 44, 1, 'YC1tDwAAQBAJ', 'Информационные системы и технологии в экономике и управлении в 2 ч. Часть 1 5-е изд., пер. и доп. Учебник для академического бакалавриата', 'http://books.google.com/books/content?id=YC1tDwAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Учебник представляет собой обобщенный труд в области современных информационных систем и технологий, применяемых в экономике, и включает в себя не только обязательные разделы программы, но и дополнительный материал, который поясняет современное состояние дел в области создания и эксплуатации современных информационных систем и технологий, а также перспектив их развития. В конце каждого раздела приводятся контрольные вопросы и задания, которые помогут студенту проверить свои знания.', 1636, 1472);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('4fb24af5-6290-4ec9-95de-23d57280a762', '2021-01-18 00:00:00.000000', 29, 23, 0, 'LcMVEAAAQBAJ', 'Я подросток', 'http://books.google.com/books/content?id=LcMVEAAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Само название сборника рассказов «Я подросток» даёт возможность сразу определить возраст читателей, которых заинтересует эта книга. Да, эта книга для подростков и молодёжи. Автор, находясь и сама в столь юном возрасте, смогла в формате коротких рассказов и очерков передать те проблемы, которые особенно волнуют подрастающее поколение. Как выбраться из сложной жизненной ситуации, как остаться верным дружбе, как не предать любовь? Давайте читать и искать ответы на эти вопросы вместе.', 25, 22);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('ab8f25ff-6fd6-4372-a2bd-95e4f296a225', '2022-05-15 00:00:00.000000', 106, 51, 0, '9ixtDwAAQBAJ', 'Моделирование бизнес-процессов. В 2 ч. Часть 1. Учебник и практикум для бакалавриата и магистратуры', 'http://books.google.com/books/content?id=9ixtDwAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'В основе этой книги – многолетний опыт работы в области управления процессами. Авторы постарались систематически изложить понятия и представить методы по управлению бизнес-процессами, которые до сих пор не являются интуитивно очевидными для большинства менеджеров и только-только входят в набор общепринятых представлений. Очень важно постичь концепцию процессов, суть которой состоит в том, чтобы управлять компанией вокруг ее процессов. Эта книга о том, как реально управлять процессами, как стать процессно-управляемой компанией. Управление процессами – очень увлекательная задача. Ее не нужно бояться, так как уже существует достаточно большой опыт и наработаны методики, подходы, инструменты, технологии, которыми можно воспользоваться и пройти этот путь менее болезненно и достаточно эффективно.', 1398, 1258);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('ae218ef1-f0c9-442b-91a7-87bf653c2210', '2022-05-13 00:00:00.000000', 288, 4, 0, 'ADIkEAAAQBAJ', 'История исторической науки. Историография истории России в 2 ч. Часть 2. Учебник для академического бакалавриата', 'http://books.google.com/books/content?id=ADIkEAAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Вторая часть учебника посвящена истории исторической науки XX в. и в настоящее время. Учебник утверждает необходимость не просто знания истории России, но и знания об этом знании. Важно понимать, как зарождались исторические концепции, как они сталкивались, как формировались государственно-охранительные подходы к истории России, что мешает сегодня формированию единой остойчивой государственно ориентированной концепции отечественной истории. Эти и другие вопросы – в центре внимания автора учебника, доктора исторических наук, профессора Г. Р. Наумовой.', 1036, 932);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('f0be3031-4f0b-4a0e-a5d1-b6082ee9ce78', '2022-05-13 00:00:00.000000', 417, 44, 1, 'tiwkEAAAQBAJ', 'Резание материалов. Режущий инструмент в 2 ч. Часть 1. Учебник для СПО', 'http://books.google.com/books/content?id=tiwkEAAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'В учебнике изложены общие вопросы, связанные с физическими основами резания материалов. Рассмотрены современные инструментальные материалы и смазочно-охлаждающие технологические средства, а также новые методы определения профиля образующей исходной инструментальной поверхности. Приведены конструкции наиболее распространенных инструментов общего назначения. Освещены теоретические основы формирования информационно-поисковой системы режущего инструмента. В приложении приведены примеры решения задач. Учебник может быть полезен студентам и магистрантам, обучающимся по направлению подготовки «Конструкторско-технологическое обеспечение машиностроительных производств», аспирантам, обучающимся по специальности «Технология и оборудование механической и физико-технической обработки», а также инженерно-техническим работникам машиностроительных производств.', 1211, 1090);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('890c0e53-2bd1-4dbb-91ca-de06f7e7958a', '2022-05-13 00:00:00.000000', 531, 44, 1, 'Q2B2DwAAQBAJ', 'Электротехника и электроника: бытовая техника. В 2 ч. Часть 2 2-е изд., пер. и доп. Учебник для СПО', 'http://books.google.com/books/content?id=Q2B2DwAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Одной из наиболее динамично развивающихся отраслей современной российской экономики является индустрия сервиса. Предприятия сервиса многочисленны и разнообразны, при этом постоянно возникают новые виды услуг, призванные удовлетворить изменяющиеся потребности человека. В современных условиях сфера сервиса превращается в важный рычаг экономического и социального прогресса, ее развитие становится условием социально-экономического роста страны и повышения качества жизни людей. Настоящий учебник предназначен для профилей «Сервис машин, оборудования и инженерных машин», «Сервис и диагностика электронных систем управления», «Сервис транспортных средств» и может быть полезен при изучении других профилей направления «Сервис». Он является первой попыткой системного изложения дисциплины «Основы функционирования систем сервиса» для указанной специальности.', 1761, 1585);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('e796509a-6743-474c-a10e-5bc336ab66f1', null, 244, 5, 0, 'OnA87v-XF-cC', 'Мир живой природы. Кто живет в горах', 'http://books.google.com/books/content?id=OnA87v-XF-cC&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Это отличное средство без всякой подготовки и совсем без усилий активно заняться с ребенком, одновременно развивая его сообразительность, эрудицию, фантазию. Книга будет полезна родителям, педагогам', 59, 53);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('69bac615-1683-45aa-9fc1-c204decf3e19', '2022-05-13 00:00:00.000000', 337, 31, 1, 'GjYkEAAAQBAJ', 'Теория и методика обучения математике: частная методика в 2 ч. Часть 1 2-е изд., испр. и доп. Учебное пособие для СПО', 'http://books.google.com/books/content?id=GjYkEAAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Учебное пособие содержит методики преподавания отдельных разделов математики. Представлены различные возможные подходы к изложению основных разделов школьного курса математики, дан их сравнительный анализ. Помимо методических рекомендаций приведены математическое содержание основных дидактических единиц, примеры применения методических схем, приемов, методов решения задач. К каждой лекции даны вопросы и задания для самостоятельной работы, а также список рекомендуемой литературы для более глубокого и всестороннего изучения рассматриваемых вопросов. Учебное пособие состоит из двух частей. В первой части рассмотрены основные вопросы частной методики обучения математике в 5 9 классах общеобразовательной школы. Вторая часть представляет собой курс лекций по частной методике обучения математике в старших классах.', 973, 876);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('956f5c32-c336-4da7-95ed-14b0fe92951e', '2022-05-12 00:00:00.000000', 89, 4, 0, '6kokEAAAQBAJ', 'Новейшая история России в 2 ч. Часть 2. 1941—2015 8-е изд., пер. и доп. Учебник для вузов', 'http://books.google.com/books/content?id=6kokEAAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Издание подготовлено коллективом петербургских историков, специализирующихся в изучении проблем истории России XX века. Каждый из членов авторского коллектива имеет многолетний опыт педагогической работы, что позволило осветить в книге именно те вопросы, которые необходимы для усвоения в полном объеме вузовского курса новейшей истории России. Учебник, построенный по проблемно-хронологическому принципу, характеризует все ключевые события одного из важнейших этапов в развитии государства и общества. Затронуты также дискуссионные исторические проблемы, авторы предлагают читателю собственное их видение, основанное на доскональном изучении источников и литературы. Одной из сильных сторон данной книги является органическое сочетание на страницах политических, социально-экономических и историко-культурных составляющих. Учебник дополнен соответствующей литературой, рекомендуемой для углубленного изучения интересующих студентов проблем.', 1086, 977);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('8cf951ec-3578-44fe-a8f5-edbe907ffac4', '2022-05-12 00:00:00.000000', 775, 44, 0, 'vI1VDwAAQBAJ', 'Технологическое оборудование для переработки продукции животноводства в 2 ч. Часть 1 2-е изд., пер. и доп. Учебник и практикум для академического бакалавриата', 'http://books.google.com/books/content?id=vI1VDwAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'В учебнике подробно рассматривается современное технологическое оборудование для подготовки и переработки продукции животноводства, изложены основы рабочих процессов, принцип действия, устройство и технические данные машин и аппаратов для переработки сырья животного происхождения. Описание основных видов оборудования сопровождается технологическими расчетами. Издание состоит из двух частей. Первая часть посвящена оборудованию, обеспечивающему изготовление молочных продуктов.', 1161, 1045);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('b75fcc42-3c20-49ea-a323-e44b8c4afc76', '2022-05-13 00:00:00.000000', 873, 24, 0, 'EfZmDwAAQBAJ', 'Исторические очерки в 2 ч. Ч. 2', 'http://books.google.com/books/content?id=EfZmDwAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Дмитрий Иванович Писарев – русский мыслитель, публицист, литературный критик. Содержание его работ отличает исключительная чуткость к событиям общественной жизни и оригинальность мировоззрения. В трудах Д. И. Писарева рассмотрены узловые философско-исторические проблемы: связь природы и общества, взаимодействие в истории материального и идеального, динамика общественного сознания и ряд других. Книга представляет собой собрание статей, написанных в разные годы и объединенных исторической тематикой.', 736, 662);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('e7e0b74e-3205-47c5-843f-7d3833a25bee', '2022-05-12 00:00:00.000000', 165, 3, 1, 'o_sjEAAAQBAJ', 'Экономическая дипломатия в 2 ч. Часть 2. Учебник и практикум для бакалавриата и магистратуры', 'http://books.google.com/books/content?id=o_sjEAAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Экономическая дипломатия – это новая дисциплина, формирующаяся на стыке международных политических, экономических и правовых наук и пока слабо представленная в учебных планах отечественных вузов и научной литературе. Между тем на современном этапе развития мировой экономики, в условиях глобализации мирохозяйственных связей экономическая дипломатия наращивает свое участие в формировании нового типа международных экономических отношений, способствует решению важных политических задач государства. В учебнике обобщены и систематизированы взгляды классиков на теорию и практику дипломатии как науки, рассмотрены современные тенденции ее развития и насущные задачи, решаемые в рамках экономической дипломатии. На его страницах нашли отражение вопросы экономической дипломатии ведущих государств мира. Особое внимание уделено противоречивой и зачастую импульсивной экономической политике России. Авторы ставили перед собой задачу донести до каждого, кто раскроет эту книгу, мысль о том, что знание предмета, понимание сути применяемых инструментов, искусное владение ими при решении практических задач, в том числе и в бизнесе, сегодня все более актуальны и востребованы. Настоящий учебник призван помочь в этом научным работникам, преподавателям, студентам и аспирантам, изучающим мировую экономику, международные экономические отношения, международное право. Написанный доступным языком, снабженный примерами из истории и современной жизни, он будет интересен и широкому кругу читателей.', 1211, 1090);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('1e8c8ae7-4787-4c70-964c-57c7d0aec014', '2022-05-12 00:00:00.000000', 815, 30, 0, 'eEokEAAAQBAJ', 'Квантовая механика и квантовая химия. В 2 ч. Часть 2. Квантовая химия. Учебник и практикум для вузов', 'http://books.google.com/books/content?id=eEokEAAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'В издание включены такие разделы, как квантовая механика, квантовая химия и практикум по прикладной квантовой химии. Изложены основные понятия и методы квантовой механики, современные расчетные методы квантовой химии. Приведены теоретические положения и необходимые справочные данные для выполнения практических заданий. В конце глав приведены контрольные вопросы и задачи, с помощью которых обучающиеся смогут проверить качество усвоения материала. Книга включает обширный список литературы, основных обозначений и сокращений, а также предметный указатель', 1398, 1258);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('3876ae23-9bf2-4ce9-ac6e-990f932810d6', '2019-10-30 00:00:00.000000', 485, 23, 1, 'TjpAEAAAQBAJ', 'Я то, что надо, или Моя репутация не так безупречна', 'http://books.google.com/books/content?id=TjpAEAAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'В сборнике «Я то, что надо, или Моя репутация не так безупречна» представлены романы Юлии Шиловой, королевы криминальной мелодрамы: «Я то, что надо, или Тебе не избежать моего плена» и «Тайны звёзд, или Моя репутация не так безупречна», вышедшие в ИД «Бауэр Медиа» и ставшие хитами продаж.Это истории женщин с необычной судьбой и необычными способностями, которые, даже загнанные в угол жизненными обстоятельствами, не теряют рассудок, а пытаются исправить ситуацию и вернуть благосклонность судьбы.', 186, 167);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('7c7680cc-3d13-4011-8730-293651d0627e', '2021-02-03 00:00:00.000000', 105, 11, 0, 'UPUYEAAAQBAJ', 'Я', 'http://books.google.com/books/content?id=UPUYEAAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Мои стихи обо мне и о людях, о жизни и смерти, о вере и безысходности, и конечно же о любви. Чтобы стихи были более живые я разбавила их небольшими рассказами о моей жизни.', 186, 167);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('f9fcf6a9-7747-4608-a942-c168231b2eb7', '2015-03-20 00:00:00.000000', 381, 23, 1, 'nwR8BwAAQBAJ', 'Кто я такой, чтоб не пить', 'http://books.google.com/books/content?id=nwR8BwAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'В эту, пятую, книгу вошли трактатики, написанные после 2000 года.Автор', 298, 268);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('c9fc22dd-76fe-40d5-855c-be405819ba3b', '2021-01-15 00:00:00.000000', 494, 23, 1, '4q8UEAAAQBAJ', '+Я', 'http://books.google.com/books/content?id=4q8UEAAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Сборник фантастических рассказов и повестей. Маленькие головокружительные путешествия в большие миры, те самые, что есть рядом с нами, по соседству. Живущие своей жизнью, охраняющие от нас свои границы. Но, как водится, всегда найдутся те люди, что их обязательно нарушат, и тогда теряется привычное равновесие и... начинается очередная история!', 186, 167);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('d5934a50-60fb-4eb4-9d18-fffb5408ee76', '2019-10-08 00:00:00.000000', 485, 23, 0, 'bPO0DwAAQBAJ', 'Я то, что надо, или Тебе не избежать моего плена', 'http://books.google.com/books/content?id=bPO0DwAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Что делать, когда судьба-злодейка загнала вас в угол – покорно принимать её удары или пытаться противостоять? А если противостоять – любой ценой или с оглядкой на мораль? И где грань, за которой мораль заканчивается и начинается жесткое манипулирование людьми?Лера, огненноволосая красавица, готова для достижения своей цели на все. У нее нет выбора: либо она добьется справедливости, либо её оставят без квартиры. А чтобы ускорить процесс, Лера соглашается сделать приворот на олигарха – пусть решает ее финансовые проблемы, пока она решает, как строить свою жизнь дальше.', 186, 167);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('95bd3b35-a8be-4431-8355-17fc0f0175eb', '2022-07-22 00:00:00.000000', 387, 23, 0, '6jB9EAAAQBAJ', 'Кто я?', 'http://books.google.com/books/content?id=6jB9EAAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', '«Когда в стабильность врывается хаос, трагедии не миновать».В Городе все жители получают сценарий собственной жизни, которому они должны непреклонно следовать.Бунтовать и нарушить сценарий?Нет, невозможно...А что будет, если пойти наперекор Городу и Министерству?Адам Грин – обычный парень, в день совершеннолетия он получает сценарий своего будущего. Он надеется на интересную работу, но ему выпадает роль осведомителя, и его обязанность ныне – докладывать Министерству о секретах людей. Адам решается помогать несчастным, попавшим под его наблюдение, не зная, чем это может обернуться.В то же время в мире, где нет преступности, где судьба каждого прописана законом, а все сценарии идеальны, внезапно начинают пропадать люди...Новый роман Лоры Кейли захватывающий и головокружительно стремительный. Как и в романе «В петле времени», автор вплетает актуальные темы в острый сюжет.«Никто не свободен и никто не в безопасности. Счастливая жизнь с развлечениями и удовольствиями не входит в планы государства. Балансируя между тревожным триллером и классической антиутопией, автору удалось создать захватывающее произведение, которое на сегодняшний день актуально, как никогда раньше. Кто знает, может именно так будет выглядеть тоталитарное общество ближайшего будущего?» – Гарри @ultraviolence_g, книжный блогер.«Кем ты мечтал стать в детстве? А, хотя забудь! В этой истории выбора у тебя нет.Ведь отныне у тебя есть только сценарий. Именно он расскажет, как тебе жить. Не нравится? Ну, выхода у тебя нет. Вместе с героями тебе предстоит попасть в самые грязные уголки Города, из которых ты вряд ли выберешься, но все же будь аккуратен». – Александр @raccoon_demigod, книжный блогер.', 298, 268);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('bd3b86a7-10a2-47b9-a4f0-70f21945d3b9', '2017-12-28 00:00:00.000000', 873, 23, 0, 'oc5EDwAAQBAJ', 'Вот я', 'http://books.google.com/books/content?id=oc5EDwAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Новый роман Фоера ждали более десяти лет. «Вот я» – масштабное эпическое повествование, книга, явно претендующая на звание большого американского романа. Российский читатель обязательно вспомнит всем известную цитату из «Анны Карениной» – «каждая семья несчастлива по-своему». Для героев романа «Вот я», Джейкоба и Джулии, полжизни проживших в браке и родивших трех сыновей, разлад воспринимается не просто как несчастье – как конец света. Частная трагедия усугубляется трагедией глобальной – сильное землетрясение на Ближнем Востоке ведет к нарастанию военного конфликта. Рвется связь времен и связь между людьми – одиночество ощущается с доселе невиданной остротой, каждый оказывается наедине со своими страхами.Отныне героям придется посмотреть на свою жизнь по-новому и увидеть зазор – между жизнью желаемой и жизнью проживаемой.', 411, 370);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('feb62130-ea09-401a-adcb-bdae87e33887', '2017-02-17 00:00:00.000000', 545, 51, 0, 'vDImDgAAQBAJ', 'Управление качеством. Учебник', 'http://books.google.com/books/content?id=vDImDgAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'В учебнике согласно требованиям государственных образовательных стандартов рассматривается существо всеобщего управления качеством; его связь со стандартами серий ИСО 9000 и ИСО 14000 и философией и концепцией классиков квалилогии. Рассматриваются вопросы статистического контроля и управления качеством продукции, анализируются модели отказов на основе предложенного автором статистико-физического подхода. Материал, составивший содержание учебного пособия, соответствует программам курсов «Менеджмент качества», «Статистические методы контроля и управления», «Всеобщее управление качеством», читаемых студентам и магистрантам. Учебник предназначен для студентов и преподавателей технических вузов, менеджеров, экономистов и инженеров.', 210, 210);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('c939975e-acc2-43ec-96cf-2970ee64e851', '2015-05-27 00:00:00.000000', 545, 51, 0, 'SCGuCQAAQBAJ', 'Менеджмент качества. Учебное пособие', 'http://books.google.com/books/content?id=SCGuCQAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'В учебном пособии, составленном на основе курса лекций, читаемых автором на протяжении ряда лет в Московском государственном техническом университете радиотехники, электроники и автоматики, рассматривается существо Всеобщего управления качеством; его связь со стандартами серий ИСО 9000 и ИСО 14000 и философией и концепцией классиков квалитологии. Рассматриваются вопросы статистического контроля и управления качеством продукции, анализируются модели отказов на основе предложенного автором статистико-физического подхода. Материал, составивший содержание учебного пособия, соответствует программам курсов «Менеджмент качества», «Статистические методы контроля и управления качеством», «Всеобщее управление качеством», читаемых студентам и магистрантам по специальностям 200503 «Стандартизация и сертификация (по отраслям)» и 200501 «Управление качеством», а также магистрантам по специальности «Менеджмент качества». Оно может быть полезно при повышении квалификации и переподготовки специалистов.', 140, 140);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('066a7bc4-5340-4985-8ffc-9e68318df5d4', '2021-01-19 00:00:00.000000', 544, 3, 1, 'gDsVEAAAQBAJ', 'Анатомия "Я"', 'http://books.google.com/books/content?id=gDsVEAAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Когда мы говорим "Я", мы редко задумываемся над тем, какое из наших "Я" в конкретный момент говорит, чувствует, думает, реагирует. Мы также не всегда можем видеть какое из "Я" стоит за словами, поступками, реакциями других. Из-за этого наша жизнь часто наполнена разного рода недоразуменями и неприятностями. Нельзя быть врачом без знания анатомии тела, также нельзя быть самодостаточным, реализовавшимся в ключевых сферах жизни взрослым человеком без знания, что представляет собой наше "Я", из чего оно состоит и каковы связи внутри целого мира "Я". Эта книга – небольшой экскурс в наше "Я".', 186, 167);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('62bb9602-4e23-4ea4-a2cb-720d2810d065', '2022-04-19 00:00:00.000000', 873, 23, 0, 'POroCwAAQBAJ', 'Что я без тебя...', 'http://books.google.com/books/content?id=POroCwAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Легкомысленный граф Стивен Уэстморленд и независимая американка Шеридан Бромлей даже и не подозревали о существовании друг друга. Он был удачно помолвлен, она – сопровождала в Англию приятельницу. Но ситуация вышла из-под контроля – приятельница сбежала с возлюбленным, а Стивен, лихо правя коляской, задавил, как выяснилось, ее нелюбимого жениха! Возникает прелестная путаница, ибо Стивен полагает, что невеста погибшего – Шеридан, а рыжеволосая красавица, потерявшая память, не знает, кто она...', 248, 223);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('9c8d2efb-3a27-4fa0-be78-bac99dea1e6e', null, 427, 9, 0, 'nVC69HXMILIC', 'Технология программирования на С++. Win32 API приложения', 'http://books.google.com/books/content?id=nVC69HXMILIC&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Изложен начальный курс низкоуровневого программирования на C++ для Windows с использованием библиотеки Win32 API. Рассмотрены графический интерфейс Windows-приложения, стандартные диалоговые окна, элементы управления, растровая графика, DLL-библиотеки, процессы и потоки. Материал иллюстрирован многочисленными примерами, выполненными в Visual Studio 2010 под управлением Windows 7.', 215, 193);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('b52ae3e1-0263-43af-b317-acc3e9e92f17', '2021-11-12 00:00:00.000000', 132, 23, 1, 'qtkXEAAAQBAJ', 'Я Землянин', 'http://books.google.com/books/content?id=qtkXEAAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', '«Я землянин»-это научная-фантастика, с приближенным к реальности описанием, возможных будущих событий, которые могут привести к катастрофе и уничтожению нашей планеты. В этом произведении описываются всемирная ядерная война, тяжелые последствия этой катастрофы для земной цивилизации и планеты в целом, судьбы людей, переживших войну. В этом произведении широко раскрывается тема о человеческом разуме, об огромных когнитивных способностях человеческого мозга, затрагиваются темы об опасностях, исходящих от черных дыр, о жизни других разумных и неразумных живых существ в дальних от Земли планетах Вселенной. В целом это произведение о проблемах человечества в будущем, о путях предотвращения и преодоления этих опасностей. Интересное и увлекательное путешествие в очень загадочно-таинственный, неисчислимо многогранный, в то же время, для каждого человека очень индивидуальный, неповторимый и удивительный мир разума людей.', 173, 156);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('bc4a6644-0d18-4c07-b281-818877504dae', '2017-09-05 00:00:00.000000', 873, 26, 1, '_GHQDQAAQBAJ', 'Я лечила Высоцкого', 'http://books.google.com/books/content?id=_GHQDQAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Автор этой книги – врач-психиатр. Несколько десятилетий своей жизни Зинаида Агеева отдала работе в психиатрической больнице, куда волею судьбы однажды привезли Владимира Высоцкого. Впоследствии певец и актер еще не раз оказывался на грани безумия. Зинаида Агеева вместе с персоналом больницы была рядом с Высоцким в самые мрачные периоды его жизни. Отношения врача и пациента вскоре переросли в доверительно-дружеские, что позволило автору глубже узнать душевный мир всенародного кумира. Вашему вниманию предлагается история жизни Владимира Высоцкого, написанная его лечащим врачом.', 286, 257);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('1e305e3f-bcb4-45de-bd57-8be0e380c55c', '2010-11-25 00:00:00.000000', 738, 23, 0, 'nSGhAAAAQBAJ', 'Мое второе я', 'http://books.google.com/books/content?id=nSGhAAAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Я лихорадочно пыталась вспомнить статью Уголовного кодекса... Сколько же лет мне светит? Да уж, прятать трупы нелегкое дело. Вроде бы ничего сложного: свернул в лесок и оставил мертвого дядечку под ближайшими кустами. А если в кустах сидит гаишник? Именно это и случилось с моей подругой. Раиса гаишника оглушила, связала, заклеила рот скотчем и кинула к трупу в багажник. А потом подруга отколола очередной номер – приехала со всем этим добром ко мне. И что же теперь делать двум бедным девушкам? Правильно, обратиться за помощью к настоящему мужчине... Правда, год назад мне пришлось внезапно исчезнуть из жизни Арсения...', 223, 201);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('8cfbcce3-5ef3-48dd-a4c8-7fafa68b8ffe', '2017-09-05 00:00:00.000000', 418, 26, 0, 'WRsxDwAAQBAJ', 'Те, с которыми я... Вячеслав Тихонов', 'http://books.google.com/books/content?id=WRsxDwAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Имя Вячеслава Васильевича Тихонова – великого советского и российского актера – навсегда вписано в историю отечественного кино. Вдвойне счастливы те, кому посчастливилось общаться с легендарным актером лично. Книга известного кинорежиссера Сергея Александровича Соловьева, созданная по мотивам его фильма о Вячеславе Тихонове из цикла «Те, с которыми я...» для телеканала «Культура», пронизана трепетным отношением к выдающимся современникам, с которыми автора сводила судьба на съемочной площадке и за ее пределами. Его словесные портреты выдающихся мастеров экрана лишены банальных черт, общеизвестных фактов, они согреты неповторимой личностной интонацией автора, который рассказывает о своих коллегах по искусству свободно, раскованно, иронично, но и нежно, с массой ярких деталей и подробностей, известных только ему. Книга дополнена также фильмографией артиста, списком наград и званий.', 248, 223);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('1677cf63-d45f-447b-85dc-e90b94ce360d', '2015-09-04 00:00:00.000000', 775, 23, 1, '_iSBCgAAQBAJ', 'Я хочу домой', 'http://books.google.com/books/content?id=_iSBCgAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Знаешь, кого ты мне напоминаешь? На древней улице Стамбула есть необычная лестница по имени Камондо, построенная в форме двух восьмерок. Ее волшебство в том, что на ней можно упасть, но скатиться до конца – нет. А еще, как бы ни плутал в ступенях лестницы, Камондо выведет к ровной светлой улице.Тебе не раз будет казаться, что ты запутался. Что если упадешь, то окажешься в темной яме. Не бойся продолжай идти', 348, 313);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('91e13cbf-7ace-453f-9d4b-8e1497379487', '2019-06-19 00:00:00.000000', 879, 23, 0, 'tCKeDwAAQBAJ', 'Я выжил на тонущем «Титанике»', 'http://books.google.com/books/content?id=tCKeDwAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Основано на реальных событиях! Самое знаменитое кораблекрушение в истории глазами десятилетнего мальчика. Как он сумел спастись и помочь своим близким? И почему стольким пассажирам «Титаника» это не удалось? Джордж Колдер – самый везучий мальчик на свете. Ведь он отправляется в путешествие на величайшем корабле из всех, что когда-либо были построены, – на «Титанике». Это будет лучшее приключение в его жизни! Но случается невозможное. Корабль, названный «непотопляемым», начинает... Тонуть! И Джордж остаётся в одиночестве, потерянный и напуганный, на судне, которое неотвратимо уходит под воду. А ведь он – всего лишь ребёнок. Как Джордж сумеет пережить эту катастрофу? Бестселлер New York Times. Общий тираж цикла более 30 миллионов экземпляров.', 311, 280);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('20c7e0ae-4ef8-4139-834a-dca6f0d721b0', '2021-08-14 00:00:00.000000', 62, 19, 1, 'M4sVEAAAQBAJ', 'Энциклопедия трудящегося и эксплуатируемого народа. Том 2 (Выпуск 21). О – Я', 'http://books.google.com/books/content?id=M4sVEAAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Эта энциклопедия является периодическим научным изданием, не имеющим аналогов в мировой науке и энциклопедической культуре. Она рассматривает общество с идеологических позиций трудящегося и эксплуатируемого народа, внося тем самым вклад в формирование энциклопедической культуры эксплуатируемых классов.Для участников демократического рабочего движения, учёных-обществоведов, студентов, магистрантов и аспирантов, а также для всех, не прекращающих самообразование.', 311, 280);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('1173325b-b46f-409e-b1e0-504567d9aeb8', '2020-05-26 00:00:00.000000', 485, 37, 0, 'DS_nDwAAQBAJ', 'Во что я верю', 'http://books.google.com/books/content?id=DS_nDwAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Эта книга глубоко личная, автобиографическая. Ганс Кюнг адресует ее тем, кто живет в вере, но хотел бы не просто верить, но быть уверенным и поэтому ищет веру, обоснованную с точки зрения философии, богословия, экзегетики, истории, которая могла бы быть практическим руководством к жизни. Автор признается, что он не спускается на богословском вертолете с небес, а начинает в долине повседневности с того, что подготавливает человека к жизни: с необходимого для каждого из нас доверия к жизни, глубинного доверия.', 311, 280);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('04367eef-ef8c-4654-a6fa-7e08ac1fac16', '2021-11-04 00:00:00.000000', 744, 23, 0, 'PzpMEAAAQBAJ', 'Как я развалил СССР и другие шалости', 'http://books.google.com/books/content?id=PzpMEAAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Родился в Одессе в 1960 году. В 1983 переехал в Москву, где периодически менял профиль своих занятий. Образование техническое. Никогда и не думал начинать что – нибудь писать. В 1998 переехал жить в США. Позже нашёл своего старого друга, профессионального писателя, Хафиза Сайфуллаева из Таджикистана. Рассказал ему вкратце историю своей жизни в Москве и США. Хафиз предложил изложить мои приключения на бумаге. Так получилась миниатюрная повесть «Как я разваливал СССР». Затем появились другие рассказы о людях, с которыми сводила судьба; о забавных, иногда грустных, порой смешных ситуациях, в которые попадал. Спасибо моим друзьям за помощь, поддержку и критику. В первую очередь моему другу Хафизу за толчок к написанию этой книги. Моему другу и учителю жизни Галине Клейман из Одессы и моему другу, живущему в Израиле, Aлёне Боковой. И конечно, моей супруге за поддержку, критику и веру в мои силы. Огромное спасибо странице «Одесса творческая» за издание в интернете, а также издательству «БуксНонСтоп», без которого эта книга не пришла бы к своему читателю.', 198, 178);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('cf67bd50-94ec-4ec4-ac5e-fd65356f8be6', '2010-01-01 00:00:00.000000', 357, 51, 1, 'wkqCvmcZkhwC', 'Я слышу вас насквозь. Эффективная техника переговоров!', 'http://books.google.com/books/content?id=wkqCvmcZkhwC&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Книга будет полезна всем, кто хочет быть услышанным и сам готов услышать и понять другого', 449, 404);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('e1bd9ac4-ab0f-48e5-8dca-bf2332f0c41a', '2019-05-16 00:00:00.000000', 559, 4, 0, '_JLBDwAAQBAJ', 'Я думаю', 'http://books.google.com/books/content?id=_JLBDwAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'В книге собраны мысли автора о различных явлениях общества и человеческого сознания, изложены собственные взгляды на историю. Некоторые фрагменты оригинальны и радикальны, практически все произведения невеселы. Ведь многие знания несут много печалей, умножающий мудрость умножает скорбь.© Виктор Улин 2021 г. – фотография.© Виктор Улин 2021 г. – дизайн обложки.', 186, 167);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('ca0e5555-be36-43e4-a8dd-7d64fc0d0935', '2020-09-16 00:00:00.000000', 300, 3, 1, 't1YNEAAAQBAJ', 'Я — человек! (курс «Человековедение»: старшеклассникам о самопознании и самовоспитании)', 'http://books.google.com/books/content?id=t1YNEAAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Материал данной книги адресован детям, но может быть использован учителями, ведущими факультативный курс «Человековедение», классными руководителями и воспитателями старшеклассников, а также родителями, помогающими своим детям в нелегком процессе взросления.', 514, 462);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('076b0a18-88ee-40ee-81ac-002dd0610720', '2016-03-29 00:00:00.000000', 275, 15, 1, 'D_LXCwAAQBAJ', 'Кто я? Инструкция к реальности', 'http://books.google.com/books/content?id=D_LXCwAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Автор проработал такие значимые для развития человечества системы, как Дзен, Дао, Йога Кундалини, Тантра, – все они, очищенные от философских и религиозных догм, представлены в разных работах lee. В том числе – в виде простых инструментов, которые помогают осознать закономерные связи между мыслями человека и внешними событиями и научиться использовать это знание для привлечения в жизнь только желаемых явлений.Вы хотите полностью управлять своей жизнью? Это книга-тренинг. Книга практикуется, а не просто читается. Вы можете пройти эту практику самостоятельно, постигнув основы абсолютного здоровья, осознав главные пути достижения своих целей. Простой язык книги, иллюстрации, приближенные к тем, что даются в живых тренингах, – все адаптировано для глубокого восприятия. Эта книга – опыт, который изменит вашу жизнь. «Кто вы, какова цель вашей жизни?» – вопрос, на который вам поможет дать ответ первая книга.Дополненное издание.', 473, 426);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('0ee14f41-dbae-437b-8bf7-c04ebb25925d', '2021-11-26 00:00:00.000000', 873, 23, 0, 'uynnDwAAQBAJ', 'Я – игрушка', 'http://books.google.com/books/content?id=uynnDwAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'У меня нет выбора. Мне пришлось стать его рабыней. Позволить ему всё. Всё и немного больше. А потом ещё больше...Волнительно. Откровенно. Возбуждающе. Жёстко.Именно эти мысли будоражили меня каждый день, каждый час, который я пробыла в его власти. В его полном подчинении, пока он со мной играл.Содержит нецензурную брань.', 161, 145);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('530374fd-de70-40ff-9fe2-44f3052ffc48', '2021-09-09 00:00:00.000000', 347, 23, 1, 'gRZCEAAAQBAJ', 'Испытание Мефистофеля. Я – часть той силы, что вечно хочет зла и вечно совершает благо. Гёте. «Фауст»', 'http://books.google.com/books/content?id=gRZCEAAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Это история о девочке Нике, о ее страхах, комплексах. О том, что недоверие к себе и к своим способностям может разрушить жизнь.Но главное зерно, которое я посеяла в этой книге, в этом мифе о Мефистофеле, – это утверждение о том, что необходимо внимательнее относится к своим мыслям, идеям и желаниям. Ведь мозг человека, – это приемник, который осознает не только мыслеформы вашего духа, но и тех кто стремиться на ваши поступки повлиять.', 250, 225);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('b9baecdb-aa16-4dc0-932d-8837af4e8e7e', '2021-12-30 00:00:00.000000', 228, 3, 0, 'KB1XEAAAQBAJ', 'Я – архитектор. Книга, которая ведёт вас домой. Книга об архитектуре вашей жизни', 'http://books.google.com/books/content?id=KB1XEAAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Представьте: Вы создаете вокруг себя пространство, которое раскрывает на 100% все Ваши способности! В нём Вы можете быстрее достигать целей и гарантированно улучшаете уровень своей жизни.Книга даёт необходимые знания и актуальные упражнения, которые помогают развить навыки осознанного создания среды, от выбора обоев и мебели до создания пространств для бизнеса и целого города.Описанная тут теория базируется на психологических и научных методах, и на моём профессиональном практическом опыте.', 610, 549);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('8eeeb89d-874a-43b0-ad58-3f90131d7803', '2019-05-04 00:00:00.000000', 747, 25, 0, 'NmCWDwAAQBAJ', 'Программирование для детей на С, С++. 1-я часть', 'http://books.google.com/books/content?id=NmCWDwAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Авторский курс для детей, 5 – 7 классы, по программированию на С, С++. Интересные задачи, лекции в следующих частях. Книга для педагога.', 500, 450);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('b205cc80-f5e3-40e1-acee-347219c423ed', '2022-05-14 00:00:00.000000', 485, 34, 0, '_u45EAAAQBAJ', 'Вера – твой пропуск к пьедесталу. 33 спортивные истории от Я до А', 'http://books.google.com/books/content?id=_u45EAAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Эта книга для будущих чемпионок, для тех, кто уверенно идет к своей мечте и когда-нибудь обязательно окажется на пьедестале Олимпийских Игр. А я поделюсь своим опытом, расскажу о трудностях и превратностях спортивного пути художественной гимнастки и дам советы, как пройти свой путь и не сломаться, вновь взлететь после падения и как не потерять веру в себя и свою уникальность.В формате PDF A4 сохранен издательский макет.', 473, 426);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('b3eafe32-f41e-40ba-bf4a-efbcad3fa428', null, 171, 44, 0, 'ZcP5AgAAQBAJ', 'Микроскопы', 'http://books.google.com/books/content?id=ZcP5AgAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'В книге описаны геометрическая теория микроскопа и волновая теория образования в нем изображения. Приведены принципиальные оптические схемы различных типов микроскопов, рекомендации по выбору объектива и окуляра для визуального'' наблюдения и микрофотографирования; рассмотрены вопросы освещенности изображения и потери света в микроскопах, основные типы источников и приемников излучения, применяемых в микроскопии.', 756, 756);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('4e37bef6-d988-4f16-a2b0-41bbc46938eb', '2015-10-27 00:00:00.000000', 545, 26, 0, 'TJvRCgAAQBAJ', 'Воспоминания об Александре Солженицыне и Варламе Шаламове', 'http://books.google.com/books/content?id=TJvRCgAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'В новой книге Сергея Гродзенского «Воспоминания об Александре Солженицыне и Варламе Шаламове» автор делится личными впечатлениями о выдающихся писателях, с которыми был знаком задолго до того, как их имена стали известны всему миру. А. Солженицын — его школьный учитель, В. Шаламов — близкий друг его отца. В книге приводятся ранее неизвестные факты из биографий известных писателей, например об их отношении к шахматам. Книга адресована широкому кругу читателей, в первую очередь тем, кто интересуется отечественной историей.', 180, 180);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('7e17c10c-de8a-44b3-8e58-8a15b4aae569', '2021-11-10 00:00:00.000000', 216, 23, 0, 'Q-1NEAAAQBAJ', 'Однажды я выберу тебя', 'http://books.google.com/books/content?id=Q-1NEAAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'МАКСЯ достиг самого дна, заполняя пустоту всем подряд.Мой организм был истощен ночными сменами в «Скорой».Став медбратом миллионера Эдварда Марша, я надеялся обрести билет в лучшую жизнь.Там я встретил его сына, и мое сердце наполнилось надеждой.Вот только его душа была соткана из боли. И я никак не мог ему помочь.Я был бессилен.САЙЛАСЯ сын миллионера, и в будущем хотел возглавить семейный бизнес.Мечтая сделать из меня марионетку, отец отправил меня на терапию в лагерь, в котором я чуть не умер.Теперь я бездушная машина, и никому не под силу повлиять на меня.Макс стал единственным, с кем я вновь почувствовал себя живым.Мы сблизились, и это могло изменить все.Но было слишком поздно: я стоял на краю пропасти.И никто не мог меня удержать.', 0, 0);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('feeca7c7-b2d5-4d4d-a758-d8807957fe7c', '2017-09-05 00:00:00.000000', 418, 26, 0, 'z_cwDwAAQBAJ', 'Те, с которыми я... Татьяна Друбич', 'http://books.google.com/books/content?id=z_cwDwAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Известная актриса театра и кино и при этом успешный врач-эндокринолог Татьяна Люсьеновна Друбич – явление, без сомнения, выдающееся в нашем кинематографе. Ее яркие и неординарные образы, подлинная природная красота до сих пор не оставляют равнодушными зрителей разных поколений. Будучи женой известного кинорежиссера Сергея Александровича Соловьева, она была и остается его музой на десятилетия. Его книга, созданная по мотивам фильма о Татьяне Друбич из цикла «Те, с которыми я...» для телеканала «Культура», пронизана трепетным отношением к выдающимся современникам, с которыми автора сводила судьба на съемочной площадке и за ее пределами. Его словесные портреты выдающихся мастеров экрана лишены банальных черт, общеизвестных фактов, они согреты неповторимой личностной интонацией автора, который рассказывает о своих коллегах по искусству свободно, раскованно, иронично, но и нежно, с массой ярких деталей и подробностей, которые известны только ему.', 248, 223);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('5ae789e9-dacd-4261-abc5-c3260a817e1f', '2016-07-08 00:00:00.000000', 418, 23, 1, '9qd9DQAAQBAJ', 'Как я чёрта искушал - Юмористическая фантастика, фэнтези-роман', 'http://books.google.com/books/content?id=9qd9DQAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Роман о дружбе и любви – двух бесценных сокровищах, обрести которые желает каждый. История о чёрте, который решил стать добрым, прекрасно подойдёт для читательских кругов как подросткового, так и взрослого возрастов. Издательство Animedia Company желает вам приятного чтения! *** *** *** Дорогие друзья! Обращаем ваше внимание на то, что при скачивании книг, размещенных бесплатно в рамках рекламной акции, Google Play списывает со счета 30 рублей для проверки актуальности ваших данных в системе. Но через короткий промежуток времени эта сумма возвращается на ваш счет.', 189, 189);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('79c0f4a6-443a-4a57-b0c6-30a28ef407fc', '2021-05-11 00:00:00.000000', 156, 23, 1, '_YgtEAAAQBAJ', 'Эскортница, или Я не такая', 'http://books.google.com/books/content?id=_YgtEAAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Сказка о современной Золушке: вместо кареты – длинный лимузин, вход на бал по контракту, а принц далек от голубых кровей. Достанется ли героине хрустальная туфелька? Каким окажется финал? Новая история уже начиналась, не пропусти.', 124, 112);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('23d88c57-e9ac-428e-800b-8aacae952305', '2017-09-05 00:00:00.000000', 873, 23, 0, 'UWgsDwAAQBAJ', 'Р.В.С.', 'http://books.google.com/books/content?id=UWgsDwAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', '«Раньше сюда иногда забегали ребятишки затем, чтобы побегать и полазить между осевшими и полуразрушенными сараями. Здесь было хорошо.Когда-то немцы, захватившие Украину, свозили сюда сено и солому. Но немцев прогнали красные, после красных пришли гайдамаки, гайдамаков прогнали петлюровцы, петлюровцев – еще кто-то. И осталось лежать сено почерневшими, полусгнившими грудами.А с тех пор, когда атаман Криволоб, тот самый, у которого желто-голубая лента пересекала папаху, расстрелял здесь четырех москалей и одного украинца, пропала у ребятишек всякая охота лазить и прятаться по заманчивым лабиринтам. И остались стоять черные сараи, молчаливые, заброшенные...»', 25, 22);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('2438514f-de57-4537-bb6e-a54a602d027c', '2022-04-23 00:00:00.000000', 873, 23, 0, 'MXsEtMmsQI0C', 'Р.В.С.', 'http://books.google.com/books/content?id=MXsEtMmsQI0C&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', '«Раньше сюда иногда забегали ребятишки затем, чтобы побегать и полазить между осевшими и полуразрушенными сараями. Здесь было хорошо.Когда-то немцы, захватившие Украину, свозили сюда сено и солому. Но немцев прогнали красные, после красных пришли гайдамаки, гайдамаков прогнали петлюровцы, петлюровцев – еще кто-то. И осталось лежать сено почерневшими, полусгнившими грудами.А с тех пор, когда атаман Криволоб, тот самый, у которого желто-голубая лента пересекала папаху, расстрелял здесь четырех москалей и одного украинца, пропала у ребятишек всякая охота лазить и прятаться по заманчивым лабиринтам. И остались стоять черные сараи, молчаливые, заброшенные...»', 48, 43);
