INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('b0b1ab31-bb63-43b5-9326-bee56d62f31f', null, 187, 44, 0, 'PLj-AgAAQBAJ', 'Пластическая деформация металлов', 'http://books.google.com/books/content?id=PLj-AgAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Для инженерно-технических и научных работников заводов и научно-исследовательских институтов, преподавателей ВУЗов, аспирантов и студентов старших курсов.', 418, 418);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('f6f90ac8-5399-44b9-b70e-bce08f21d6de', '2022-01-27 00:00:00.000000', 253, 48, 0, '5CLODQAAQBAJ', 'Проблемы формирования гостиничного продукта в России', 'http://books.google.com/books/content?id=5CLODQAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Важная проблема отечественного рынка средств размещения на современном этапе – несоответствие существующего гостиничного продукта мировым стандартам качества. В данной статье проведен анализ гостиничного рынка России, определены факторы возникновения проблемы несоответствия отечественных гостиничных услуг мировому уровню, а также предложены рекомендации по решению выявленных проблем. Анализируемые данные были сопоставлены с гостиничным рынком США, обладающим передовым опытом в рассматриваемой сфере.', 210, 189);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('abafc069-a41f-4dbd-aa42-0cb9128d7996', '2020-03-02 00:00:00.000000', 247, 46, 0, '9EnUDwAAQBAJ', 'Логопедический альбом по формированию правильного произношения звуков Р–Рь', 'http://books.google.com/books/content?id=9EnUDwAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'С этим Логопедическим альбомом ваш ребенок в домашних условиях овладеет правильным произношением звуков Р и Рь! В нем содержится 80 упражнений и заданий. Альбомы серии «Логопедическая практика» не требуют дополнительных пособий:• все задания подкреплены иллюстрациями;• на последней странице размещена раскраска по номерам – ее детали ребенок закрашивает по мере выполнения заданий;• на внутренней стороне обложки есть иллюстрированная речевая гимнастика, которую рекомендовано выполнять перед каждым занятием;• в конце альбома – вырезной диплом, подтверждающий, что ребенок научился произносить звуки Р и Рь.', 348, 313);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('073331a8-743f-46fe-80cf-4dbdc530c8f3', '2017-12-26 00:00:00.000000', 449, 30, 1, 'zY1EDwAAQBAJ', 'Геология и ландшафтоведение', 'http://books.google.com/books/content?id=zY1EDwAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Описаны главные минералы и горные породы, эндогенные и экзогенные процессы, основные этапы геологической истории Земли. Изложены вопросы строения естественных и природно-антропогенных ландшафтов, их динамики, систематики и устойчивости. Описаны факторы и законы ландшафтной дифференциации, генезис и функционирование. Приведены сведения о геохимии и биогеохимии ландшафтов.', 248, 223);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('60f18b81-bcf4-467a-935f-b92a56bc4c4f', '2017-09-05 00:00:00.000000', 414, 27, 0, 'lmmPCgAAQBAJ', 'Лингвистическая полифония. Сборник статей в честь юбилея профессора Р. К. Потаповой', 'http://books.google.com/books/content?id=lmmPCgAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Содержание сборника, посвященного юбилею лингвиста – исследователя – педагога Р. К. Потаповой, отражает результаты изысканий в различных областях фундаментальной и прикладной науки о языке и речи и полностью соответствует названию «Лингвистическая полифония». Тематически и композиционно материал сборника включает следующие разделы: теория языка, проблемы языковой диахронии, фонетика / фонология, семантика / лексикология /лексикография, прикладная и экспериментальная лингвистика, художественная коммуникация. Широкий диапазон авторских концепций позволит читателю составить полное представление о современных научных школах и направлениях в лингвистике как в России, так и за рубежом.', 425, 382);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('e1fa977d-139b-484c-81ca-82f3c36ae237', '2020-05-29 00:00:00.000000', 58, 31, 0, 'OXboDwAAQBAJ', 'Алгебра и геометрия. Том 3. Проективные геометрии и геометрии Кэли – Клейна', 'http://books.google.com/books/content?id=OXboDwAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'В книге рассматривается проективная геометрия (над действительными числами, комплексными числами и кватернионами) и тесно с ней связанные так называемые геометрии Кэли – Клейна, соответствующие всевозможным классическим группам. Этот материал, на который, как правило, не хватает времени в стандартных университетских курсах, лежит в основе многих разделов современной математики: владение им очень помогает при изучении дифференциальной и алгебраической геометрий, а также современной физики.Для студентов, аспирантов и научных работников.', 357, 321);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('cc32546c-6859-4bbe-8da0-a0863aa58054', null, 83, 44, 0, 'E3n-AgAAQBAJ', 'Справочник по теплоснабжению и вентиляции в гражданском строительстве', 'http://books.google.com/books/content?id=E3n-AgAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'This book, "Spravochnik po teplosnabzheniyu i ventilyatsii v grazhdanskom stroitel''stve," by R. V. Schekin, is a replication of a book originally published before 1960. It has been restored by human beings, page by page, so that you may enjoy it in a form as close to the original as possible.', 720, 720);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('1ccbb00f-f60f-4ac0-96ed-7c7731c86378', '2022-05-15 00:00:00.000000', 567, 11, 0, 'k73nDwAAQBAJ', 'for Ю', 'http://books.google.com/books/content?id=k73nDwAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Стихи Алексея Баширова прежде всего о любви – яркой, многогранной и вечной, как сама жизнь. И о жизни, в которой есть место искренности, чувственности и мечтам. Соскучились по романтике? Тогда читайте, мечтайте, любите!', 250, 225);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('dfb503ba-b4c8-42e4-9fbb-febd0fcd792d', '2012-11-09 00:00:00.000000', 790, 23, 0, 'GeGmAAAAQBAJ', 'Н.', 'http://books.google.com/books/content?id=GeGmAAAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', '«Дорогой Чарли!Называть тебя вот так, по имени, одновременно и странно, и правильно. Когда мы виделись в последний раз, мне было почти вполовину меньше лет... Жутко влюбленной в тебя тогда девчонке было шестнадцать. Знал ли ты о моих чувствах? Ну конечно, знал. Сейчас я замужем, у меня растет малыш, мальчик; я счастлива. Не пропускаю ни одной твоей передачи о здоровье на Си-эн-эн. Ты все такой же красавчик (ну, почти), что и в дни былой юности, когда мы втроем ходили на рыбалку или в кино, во фрипортский „Рейлроуд“...»', 73, 66);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('63260d7e-e847-41fc-9e82-aeda7f7d9801', '2020-05-05 00:00:00.000000', 14, 23, 0, 'RYLhDwAAQBAJ', 'С.О.Н.', 'http://books.google.com/books/content?id=RYLhDwAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Алиса Cветикова в свои восемнадцать лет переживает не лучшие времена: отец отгородился стеной безразличия, мать трагически погибла, а деятельная мачеха пытается полностью подстроить под себя героиню, пренебрегая ее желаниями. Вдобавок ко всему, каждую ночь девушку мучает один и тот же кошмар: незнакомый, но почему-то очень близкий и родной человек умирает на ее глазах. Что это? Наваждение? Воспоминание из прошлой жизни? Психологическая травма? Самое захватывающее начнется, когда Алиса узнает, что Ангел из ее сна существует в реальном мире...', 199, 199);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('e686d506-208e-4c72-a8e4-0befe3708861', null, 735, 30, 1, 'Sff9AgAAQBAJ', 'Как устроен и работает электровоз', 'http://books.google.com/books/content?id=Sff9AgAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'В книге в доступной для читателя форме описаны устройство и работа электровозов. Даны основные понятия о схемах электрических цепей, принципах их чтения. Приведены краткие сведения об организации эксплуатации электровозов, об устройстве тяговых подстанций и контактной сети.', 456, 456);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('9fa3fa49-7ddb-4e63-bb54-f0da773a193d', '2017-02-13 00:00:00.000000', 684, 18, 0, 'xr0kDAAAQBAJ', 'Русская книга для чтения в 4-х выпусках', 'http://books.google.com/books/content?id=xr0kDAAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Предлагаем вниманию читателей - взрослых и детей - четыре сборника детских произведений великого русского писателя Льва Николаевича Толстого, объединенных автором под единым названием "Русская книга для чтения". В книги вошли избранные рассказы, басни и сказки классика русской литературы, адресованные детям младшего школьного возраста. Великолепно написанные, поучительные и трогательные, эти басни, сказки и притчи доставят истинное удовольствие и читающему и слушающему.', 29, 29);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('73f3f1c9-785d-4ab1-bc1a-d5b77bd61c4c', null, 156, 27, 1, 'O-03EAAAQBAJ', 'Историческая грамматика русского языка', 'http://books.google.com/books/content?id=O-03EAAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Учебник представляет собой курс лекций по указанной дисциплине, читавшийся в течение ряда лет на филологическом факультете МГТГУ в соответствии с действующей программой и реальным количеством часов. При этом основные вопросы фонетики, морфологии и синтаксиса древнерусского языка даются в сравнении не только со старославянским и современным русским языком, но и с русской диалектологией. Главное достоинство учебника — доступный уровень изложения материала. Каждый раздел завершается контрольными вопросами для закрепления полученных знаний. Учебник адресован студентам учреждений высшего профессионального образования.', 192, 172);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('a8f645ac-ef05-4b9d-951f-40e2798921b2', '2018-02-14 00:00:00.000000', 315, 0, 0, 'zy9MDwAAQBAJ', 'Права детей. Учебно-практическое пособие', 'http://books.google.com/books/content?id=zy9MDwAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Издание посвящено правовым аспектам статуса ребенка по российскому законодательству. Основное внимание уделено проблеме ребенка как самостоятельного, хотя и особого, субъекта права, его возможностям в сфере семьи, образования, медицинского обеспечения и др. Рассматриваются также вопросы, связанные с разрешением споров о воспитании детей и ответственности родителей за ненадлежащее воспитание. Законодательство приводится на 1 сентября 2017 г. Книга ориентирована на широкий круг читателей: специалистов в области охраны и защиты прав ребенка, ученых-семейноведов, родителей и других категорий российских граждан, интересующихся данной проблематикой.', 130, 130);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('092293e3-5a52-4480-9307-9f2d68fd1657', '2013-09-18 00:00:00.000000', 367, 51, 0, 'rSDiAAAAQBAJ', 'Конкурентоспособность стран и регионов. Учебное пособие', 'http://books.google.com/books/content?id=rSDiAAAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Рассмотрены основные понятия, связанные с конкурентоспособностью стран и регионов, а также основные факторы и механизмы ее повышения. Проанализированы наиболее актуальные международные и российские рейтинги конкурентоспособности. Исследованы вопросы управления конкурентоспособностью стран и регионов. Особое внимание уделено основным направлениям повышения конкурентоспособности Российской Федерации. В сопровождающем учебное пособие практикуме в целях формирования у студентов исследовательских навыков предложены учебные задания для самостоятельной работы и кейсы. Для студентов высших учебных заведений, обучающихся по специальности «Мировая экономика».', 170, 170);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('daf03f5e-1189-4c43-9c3b-a9a97a5a94a0', '2013-10-09 00:00:00.000000', 485, 51, 0, 'na5FAQAAQBAJ', 'Муниципальное управление. Учебник', 'http://books.google.com/books/content?id=na5FAQAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Последовательно представлены и раскрыты основные понятия в отношении объекта, субъекта и методов муниципального управления. С учетом того, что муниципальное управление является одним из видов публичного управления, подлежащего специальному законодательному регулированию, а также использующего в качестве одного из методов управления правовое регулирование, структура учебника включает отдельную главу, посвященную этим вопросам. Сохраняя преемственность в объективном изложении базового материала по названным специальности и дисциплине, книга отличается от другой учебной литературы особым вниманием к законодательным и иным новациям последнего времени в области муниципального управления, что делает ее весьма актуальной. В подготовке учебника широко использована современная отечественная и зарубежная практика муниципального управления. Для студентов вузов, государственных и муниципальных служащих, специалистов, заинтересованных в обучении по специальности «Государственное и муниципальное управление», дисциплине «Система государственного и муниципального управления».', 139, 139);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('82bd86c2-40ba-4908-9b24-d361e952eb5b', '2020-03-12 00:00:00.000000', 20, 51, 0, 'ytviDwAAQBAJ', 'Финансовый менеджмент', 'http://books.google.com/books/content?id=ytviDwAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Учебное пособие «Финансовый менеджмент» структурно представлено 4 разделами. Первый раздел посвящен рассмотрению теоретических основ финансового менеджмента, описанию финансовой системы, и системы информационного обеспечения финансового управления. В следующих разделах последовательно рассматриваются подходы к финансовому управлению организацией: вопросы управления финансовыми результатами и рентабельностью, решения по оптимизации структуры капитала, принятие инвестиционных решений, управление долгосрочными и оборотными активами. Пособие разработано для обеспечения одноименной дисциплины и предназначено для студентов всех форм обучения по направлению «Экономика», а также может оказаться полезным руководителям и специалистам различного уровня. Текст печатается в авторской редакции.', 147, 132);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('b9d0083f-0721-4f43-ac50-f136c0c482b5', '2019-08-27 00:00:00.000000', 773, 30, 0, 'VfvRDAAAQBAJ', 'Гидротехнические сельскохозяйственные мелиорации. 2-е издание. Учебное пособие', 'http://books.google.com/books/content?id=VfvRDAAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Даны основные положения оросительных и осушительных мелиораций, режима орошения сельскохозяйственных культур, способов регулирования водного режима осушаемых земель. Рассмотрены вопросы регулирования местного стока, в том числе лиманное орошение, предупреждения засоления орошаемых земель, борьбы с водной эрозией почв, планировки полей. Даны характеристики современных дождевальных машин, а также других машин и установок, применяемых в мелиоративных работах. Представлены примеры курсовых проектов систем двустороннего регулирования водного режима и упражнений по осушению и орошению земель в различных областях России. Даны примеры расчетов проектирования водоема, глубоководных и мелководных лиманов, поверхностных способов полива, а также расчеты полива дождеванием. Для студентов сельскохозяйственных вузов агрономических специальностей, может быть использовано аспирантами и специалистами, работающими в области мелиорации и водного хозяйства.', 370, 370);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('eb49fc39-533a-49c8-89f9-a9df8d1d1a0b', '2018-10-10 00:00:00.000000', 773, 0, 0, 'yj9yDwAAQBAJ', 'Проблемы квалификации преступлений. Монография', 'http://books.google.com/books/content?id=yj9yDwAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Монография посвящена проблемам квалификации преступлений. В ней рассмотрены понятие, виды, значение, юридическая основа квалификации преступлений, ее принципы и правила, особенности квалификации преступлений с переменными признаками состава, проблемы квалификации преступлений при конкуренции уголовно-правовых норм, разграничения сложных единичных преступлений и множественности преступных деяний, квалификации неоконченных преступлений и преступлений, совершенных несколькими лицами, проанализированы основания и процессуальный порядок изменения квалификации преступления. Законодательство приведено по состоянию на сентябрь 2017 г. Монография рассчитана на научных сотрудников, преподавателей, практикующих юристов, студентов и аспирантов юридических вузов и факультетов.', 290, 290);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('f9346674-b44f-49a0-8b20-9c82c8ba747d', null, 123, 9, 0, 'MojmH1KA9nwC', 'Система экологического воспитания дошкольников', 'http://books.google.com/books/content?id=MojmH1KA9nwC&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Адресовано студентам, магистрантам, аспирантам, учителям школ, студентам педучилищ, преподавателям', 101, 90);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('38d013a4-5adb-426a-b5b3-72184de7187c', '2020-08-20 00:00:00.000000', 340, 22, 1, 'GpIEEAAAQBAJ', 'Введение в медицинский перевод', 'http://books.google.com/books/content?id=GpIEEAAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'В предлагаемом учебном пособии системно излагаются основные наиболее типичные особенности современного медицинского текста, дается характеристика медицинской, фармацевтической терминологии и сокращений, рассматриваются основные трудности перевода медицинского текста. Цель пособия — развитие переводческой компетентности будущих переводчиков в области письменного перевода медицинских текстов. Пособие предназначено для студентов высших учебных заведений, обучающихся по специальности «Перевод и переводоведение», преподавателей, переводчиков, а также всех, кто занимается или интересуется переводом медицинских текстов.', 115, 103);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('56dca1a7-c03a-4e17-8b39-970f533d79c1', '2017-12-21 00:00:00.000000', 766, 30, 1, '0CREDwAAQBAJ', 'Дискретная математика. Булевы функции. Сборник контрольных работ', 'http://books.google.com/books/content?id=0CREDwAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Данное пособие представляет собой сборник однотипных контрольных работ по дискретной математике, а именно по такому разделу, как булевы функции. Эти контрольные предлагались студентам факультета информационных систем и безопасности Института информационных наук и технологий безопасности РГГУ на протяжении нескольких последних лет. Ко всем задачам имеются ответы. Последний вариант каждой контрольной полностью разобран. Пособие рассчитано на студентов первого и второго курсов. Книга также может быть полезна преподавателям, которые начинают читать курс дискретной математики.', 140, 140);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('03c609d9-135b-4c27-9ca3-aa793732f0cc', '2019-09-05 00:00:00.000000', 873, 13, 0, '_DbDDwAAQBAJ', 'Архитектурное проектирование зданий дошкольных образовательных учреждений', 'http://books.google.com/books/content?id=_DbDDwAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Учебное пособие предназначено для студентов направления подготовки «Архитектура» (уровень бакалавриат) по дисциплине «Архитектурное проектирование». Учебное пособие содержит методические и практические рекомендации по проектированию дошкольных образовательных учреждений. Изложенный материал сопровождается иллюстрациями и контрольными вопросами для самопроверки. Текст приводится в авторской редакции.', 104, 93);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('37743d07-ea95-45b2-8075-9d1c803f7ce2', null, 465, 4, 0, 'LYL6AgAAQBAJ', 'Буряты', 'http://books.google.com/books/content?id=LYL6AgAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', null, 1802, 1802);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('2b1ab92a-24ed-49da-85db-a794cb3f1104', '2016-02-24 00:00:00.000000', 139, 51, 0, 'I-igCwAAQBAJ', 'Микроэкономика: промежуточный уровень. Учебник', 'http://books.google.com/books/content?id=I-igCwAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Учебник охватывает стандартную проблематику данного курса микроэкономики промежуточного уровня, а также некоторые темы микроэкономики продвинутого уровня. В качестве сопровождения к учебнику авторами разработано учебно-методическое пособие «Микроэкономика: промежуточный уровень», которое содержит комплекс тренировочных и развивающих заданий (упражнений, тестов и задач) по всем разделам и главам учебника. Для студентов и аспирантов экономических факультетов университетов и экономических вузов.', 299, 299);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('6fcbf922-a9d2-40c4-9676-a3756d0c1769', '2018-11-19 00:00:00.000000', 49, 0, 1, 'XPB6DwAAQBAJ', 'Участие прокурора в рассмотрении судами уголовных дел. Учебное пособие', 'http://books.google.com/books/content?id=XPB6DwAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Курс лекций подготовлен коллективом кафедры организации судебной и прокурорско-следственной деятельности Университета имени О. Е. Кутафина (МГЮА) и предназначен для использования в учебном процессе студентами, обучающимися по направлению «Юриспруденция» по программе бакалавриата. В данном курсе рассматриваются теоретические, правовые и организационно-практические вопросы подготовки и участия прокурора в рассмотрении уголовных дел судами первой, апелляционной, кассационной, надзорной инстанций. Особое внимание уделено освещению вопросов подготовки и тактики представления прокурором доказательств в судебном следствии, их исследованию наряду с доказательствами защиты, участию в судебных прениях. Содержание курса ориентировано на прочное усвоение студентами их обязанности в будущей практической деятельности всемерно содействовать суду в вынесении законного, обоснованного и справедливого решения по уголовному делу, объективности в оценке доказательств, недопустимости проявления обвинительного уклона при выполнении возложенной на них функции. Усвоению содержания курса поможет использование контрольных вопросов для самопроверки знаний по каждой теме, изучение и усвоение дополнительно рекомендуемой литературы. Законодательство приведено по состоянию на июнь 2018 г. Рекомендуется для студентов, аспирантов, научных и педагогических работников.', 210, 210);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('73f60555-4a20-491a-96f4-07e965026f49', null, 387, 27, 1, '-hD_AgAAQBAJ', 'Стенография', 'http://books.google.com/books/content?id=-hD_AgAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Уже давно люди думали о письме, которое давало бы возможность записывать речь с такой же быстротой, с какой она произносится. Они обратили внимание на большую разницу в скорости произнесения слова и его записи. Различными способами люди сокращали слова и буквы. В результате появилась стенография... Настоящий учебник стенографии является первым пособием, написанным специально для средней школы. В отличие от всех ранее изданных учебных пособий по стенографии, данный учебник составлен в соответствии со школьной программой...', 708, 708);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('918b72a1-584e-48b4-a9c6-1d643c2e2649', null, 775, 4, 0, 'EGz6AgAAQBAJ', 'Обозрение историко-географических материалов XVII и начала XVIII вв', 'http://books.google.com/books/content?id=EGz6AgAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', null, 629, 629);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('602c92c3-0bac-4f09-8911-4fc585262bff', null, 433, 27, 0, 'M_n8AgAAQBAJ', 'Старославянский язык', 'http://books.google.com/books/content?id=M_n8AgAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Учебник содержит сведения по истории старославянского языка, его фонетике, лексике, словообразованию, морфологии и синтаксису.Работая над вторым изданием «Старославянского языка», автор стремился привести его в соответствие с требованиями ныне действующей программы по старославянскому языку для педагогических институтов. Это потребовало — по сравнению с текстом первого издания, вышедшего в свет в 1974 г., — исключения или значительного сокращения разделов, посвященных общеславянскому словообразованию знаменательных частей речи, служебным словам и старославянскому синтаксису, поскольку программой подробное освещение этих разделов не предусмотрено.', 708, 708);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('a604b016-a761-46d0-a6e1-076adb657e56', null, 789, 31, 0, 'Pp39AgAAQBAJ', 'Теория игр', 'http://books.google.com/books/content?id=Pp39AgAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Предлагаемое издание представляет собой краткое и сравнительно элементарное учебное пособие, пригодное как для первоначального, так и для углубленного изучения теории игр. Для его чтения достаточно знания элементов математического анализа и теории вероятностей.Работа делится на две части, первая из которых посвящена играм двух лиц, а вторая - играм п лиц. Она охватывает большинство направлений теории игр. В частности, рассмотрены антагонистические игры, игры двух лиц с ненулевой суммой и основы классической кооперативной теории. Каждая глава снабжена задачами разной степени сложности.Книга вполне доступна студентам и аспирантам университетов, технических и экономических высших учебных заведений. Она представляет интерес не только для математиков, но и для специалистов в области исследования операций, военного дела, теории управления и математической экономики.', 568, 568);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('2271095d-f134-4b28-ab4c-05782a85b34a', null, 51, 44, 0, 'wNX5AgAAQBAJ', 'Справочник молодого токаря', 'http://books.google.com/books/content?id=wNX5AgAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Справочник содержит сведения по основам токарной обработки металлов и некоторых пластмасс, технические характеристики и краткие сведения об устройстве токарных станков и приспособлений к ним. Приведены данные о резцах, приемах обработки и режимах резания, изложены рекомендации по повышению точности обработки.Справочник предназначен для учащихся профессионально-технических учебных заведений и молодых рабочих, обучающихся на производстве', 456, 456);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('adf27506-d8b7-40ea-b8ff-0042ac3bf71a', null, 622, 27, 1, 'ugH9AgAAQBAJ', 'Принципы истории языка', 'http://books.google.com/books/content?id=ugH9AgAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', '"Принципы истории языка" Германа Пауля являются классическим трудом, отражающим концепцию "младограмматизма" в языкознании.В своем труде автор касается всех основных вопросов теоретического языкознания. В книге, охватывающей разнообразные стороны лингвистики, четко выражены основные черты младограмматической концепции: подчеркнутый историзм, индивидуальный психологизм, эмпиризм и индуктивизм, отказ от рассмотрения слишком широких и общих вопросов. Рассматривая лингвистику как историческую науку, Пауль в то же время по ряду вопросов (например, по классификации частей речи) выходил за рамки чисто исторического подхода. Разделы, посвященные историческим изменениям в синтаксисе и семантике, не утратили значения до настоящего времени. Книга Пауля оказала значительное влияние на лингвистов многих стран, в том числе на Н.В.Крушевского, Ф.Ф.Фортунатова и др.Является единственным трудом общетеоретического характера в школе младограмматиков. "Катехизиз младограмматизма"', 456, 456);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('bd43f5fb-449b-4b8c-a2f8-ef716fa7e882', '2015-11-16 00:00:00.000000', 559, 51, 0, 'NJ3tCgAAQBAJ', 'Девелопмент недвижимости. Учебное пособие', 'http://books.google.com/books/content?id=NJ3tCgAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'На начальном этапе становления рынка недвижимости и девелопмента (инвестиционно-строительной деятельности) в переходной экономике России технико-экономические параметры будущих проектов определялись эмпирически, на основе субъективного экспертного мнения архитекторов, маркетологов и менеджеров рынка недвижимости (девелоперов, брокеров, риелторов и др.). За 25 лет с 1991 по 2015 г. такая практика еще не полностью себя изжила. Обобщая 25-летний опыт развития рынка, настоящее учебное пособие кратко излагает научно обоснованный подход к оптимизации технико-экономических параметров девелоперских проектов по критерию рыночной реализуемости на основе инвестиционного анализа и прогнозирования рынка недвижимости, который разрабатывался и внедрялся в том числе и авторами настоящего пособия. Учебное пособие по дисциплине «Девелопмент недвижимости» рассчитано как на студентов бакалавриата и магистратуры по направлениям «Экономика» и «Менеджмент» в соответствии с Федеральными стандартами образования, так и на широкий круг профильных специалистов и руководителей частного и государственного сектора управления инвестиционно-строительной деятельностью в Российской Федерации.', 200, 200);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('b3025d61-5d83-43f5-94b5-1a40612f518e', '2015-03-18 00:00:00.000000', 279, 0, 1, '2u11BwAAQBAJ', 'Международное частное право. Учебник для бакалавров', 'http://books.google.com/books/content?id=2u11BwAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'В учебнике, подготовленном в соответствии с Федеральным образовательным стандартом высшего профессионального образования по направлению подготовки «Юриспруденция» (квалификация (степень) — «бакалавр»), на основе современных достижений науки международного частного права, изложены все основные положения, характеризующие природу и специфику международного частного права, а также рассмотрены: правовое положение иностранных граждан и юридических лиц в России и за рубежом; основные институты и подотрасли международного частного права (правовой режим собственности, трансграничные торговые и другие сделки, перевозки, денежные и внедоговорные обязательства, трансграничные семейно-брачные, наследственные отношения); вопросы интеллектуальной собственности, международного гражданского процесса и международного коммерческого арбитража. Учебник подготовлен на основе анализа законодательства и практики российской Федерации, законов ряда иностранных государств и международных документов. Нормативные акты используются по состоянию на ноябрь 2013 г. Для студентов-бакалавров юридических вузов и факультетов экономических и других учебных учреждений, специализирующихся на международной', 320, 320);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('86d50011-5e7e-4844-bb2e-21e859eede03', '2022-01-21 00:00:00.000000', 345, 23, 0, 'ZGtNEAAAQBAJ', 'Фантазер из 8 "Г"', 'http://books.google.com/books/content?id=ZGtNEAAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Что должен уметь делать настоящий школьник? Мастерски прятать шпаргалки от учителей, незаметно списывать контрольные у соседа по парте, подшучивать над друзьями, влюбляться. Но, пожалуй, самое сложное занятие – спокойно усидеть за партой, когда в классе появляется покемон. Попробуй удержаться, чтобы не сорвать урок. Никакого терпения не хватит. И, конечно же, каждый ученик должен уметь хранить тайны. Одна из таких тайн есть у Филиппа, Сереги, Милки и Руслана. Однажды в руках Филиппа оказался конверт. Стало ясно, тайна ребят раскрыта. Что же последует дальше?..', 311, 280);
INSERT INTO book_shop.book (id, pub_date, author_id, genre_id, is_bestseller, slug, title, image, description, price_old, price) VALUES ('4ced4da8-ad2d-4568-be3a-eca2b0ad07f6', null, 163, 30, 1, 'd7P-AgAAQBAJ', 'Общая энтомология', 'http://books.google.com/books/content?id=d7P-AgAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Учебник состоит из пяти разделов.Наружная морфология рассматривает внешнее строение, анатомия и физиология - внутреннее строение и процессы, протекающие в организме. В этих разделах показаны сложность и высокое совершенство ряда морфофизиологических свойств насекомых.Размножение и жизненный цикл изложены в разделе "Биология".Особенности классификации, подразделения на отряды и другие таксоны, их краткая характеристика даны в разделе "Систематика и классификация".Последний раздел - "Экология" - рассматривает взаимоотношения насекомых со средой, факторы среды и их влияние на динамику численности и жизненные процессы.Приведен обширный список литературы, а также указатель русских и латинских названий, имен и терминов. Учебник хорошо иллюстрирован.Учебник предназначен для студентов университетов и сельскохозяйственных вузов, обучающихся по специальности «Защита растений», может быть использован специалистами сельского хозяйства.', 419, 419);
